            "category": "Basics",
            "code": "<div class=\"container\">\n    <div class=\"switch\">\n        <div class=\"ball\"></div>\n    </div>\n</div>\n\n<script type=\"module\">\n    import { animate } from \"https://cdn.jsdelivr.net/npm/motion@12.26.1/+esm\"\n\n    // From https://easings.net/#easeOutBounce\n    function bounceEase(x) {\n        const n1 = 7.5625\n        const d1 = 2.75\n\n        if (x < 1 / d1) {\n            return n1 * x * x\n        } else if (x < 2 / d1) {\n            return n1 * (x -= 1.5 / d1) * x + 0.75\n        } else if (x < 2.5 / d1) {\n            return n1 * (x -= 2.25 / d1) * x + 0.9375\n        } else {\n            return n1 * (x -= 2.625 / d1) * x + 0.984375\n        }\n    }\n\n    const bounce = {\n        duration: 1.2,\n        ease: bounceEase,\n    }\n\n    const spring = {\n        type: \"spring\",\n        stiffness: 700,\n        damping: 30,\n    }\n\n    const ball = document.querySelector(\".ball\")\n    const switchEl = document.querySelector(\".switch\")\n    let isOn = true\n\n    switchEl.addEventListener(\"click\", () => {\n        isOn = !isOn\n\n        animate(ball, { y: isOn ? 0 : 120 }, isOn ? spring : bounce)\n    })\n</script>\n\n<style>\n    .container {\n        display: flex;\n        align-items: center;\n        justify-content: center;\n        flex-direction: column;\n        min-height: 100vh;\n    }\n\n    .switch {\n        width: 80px;\n        height: 200px;\n        background-color: #fff3;\n        display: flex;\n        align-items: flex-start;\n        border-radius: 50px;\n        padding: 10px;\n        cursor: pointer;\n    }\n\n    .ball {\n        width: 80px;\n        height: 80px;\n        background-color: #f5f5f5;\n        border-radius: 40px;\n        will-change: transform;\n    }\n</style>\n"
