            "code": "<div class=\"container\" id=\"container\">\n    <div class=\"image-container\">\n        <img src=\"/photos/japan-day/7.jpg\" alt=\"Image\" class=\"image\" />\n        <img\n            src=\"/photos/japan-day/7.jpg\"\n            alt=\"Image in black and white\"\n            class=\"image overlay\"\n            id=\"overlay\"\n        />\n    </div>\n\n    <div class=\"slider\" id=\"slider\" tabindex=\"0\">\n        <div class=\"handle\">\n            <svg\n                xmlns=\"http://www.w3.org/2000/svg\"\n                width=\"24\"\n                height=\"24\"\n                viewBox=\"0 0 24 24\"\n                fill=\"none\"\n                stroke=\"currentColor\"\n                stroke-width=\"2\"\n                stroke-linecap=\"round\"\n                stroke-linejoin=\"round\"\n            >\n                <path d=\"m18 8 4 4-4 4\" />\n                <path d=\"m6 8-4 4 4 4\" />\n                <path d=\"M8 12h.01\" />\n                <path d=\"M12 12h.01\" />\n                <path d=\"M16 12h.01\" />\n            </svg>\n        </div>\n    </div>\n</div>\n\n<script type=\"module\">\n    import {\n        animate,\n        motionValue,\n        mapValue,\n        styleEffect,\n        clamp,\n        frame,\n    } from \"https://cdn.jsdelivr.net/npm/motion@12.26.1/+esm\"\n\n    const step = 50\n    const elastic = 0.1\n\n    const container = document.getElementById(\"container\")\n    const slider = document.getElementById(\"slider\")\n\n    // State\n    let boundary = container.clientWidth / 2\n    const x = motionValue(0)\n\n    styleEffect(slider, {\n        x,\n        backgroundColor: mapValue(\n            x,\n            [-boundary + 20, -boundary + 60, boundary - 60, boundary - 20],\n            [\n                \"rgba(255, 255, 255, 0)\",\n                \"rgba(255, 255, 255, 1)\",\n                \"rgba(255, 255, 255, 1)\",\n                \"rgba(255, 255, 255, 0)\",\n            ]\n        ),\n    })\n\n    styleEffect(\"#overlay\", {\n        clipPath: mapValue(\n            x,\n            [-boundary, boundary],\n            [\"inset(0% 0% 0% 0%)\", \"inset(0% 0% 0% 100%)\"]\n        ),\n    })\n\n    // Keyboard navigation\n    function handleKeyDown(event) {\n        let moveBy = 0\n\n        if (event.key === \"ArrowLeft\") {\n            moveBy = -step\n        } else if (event.key === \"ArrowRight\") {\n            moveBy = step\n        } else {\n            return\n        }\n\n        animate(x, clamp(-boundary, boundary, x.get() + moveBy), {\n            type: \"spring\",\n            stiffness: 900,\n            damping: 40,\n            // Manually setting velocity is what allows the slider\n            // to give a little tug at the boundary.\n            velocity: moveBy * 10,\n        })\n    }\n\n    function setupDrag() {\n        let startX = 0\n\n        slider.addEventListener(\"pointerdown\", (e) => {\n            startX = x.get() - e.clientX\n            document.body.style.cursor = \"grabbing\"\n            slider.style.cursor = \"grabbing\"\n            slider.classList.add(\"active\")\n            slider.setPointerCapture(e.pointerId)\n        })\n\n        slider.addEventListener(\"pointermove\", (e) => {\n            if (!slider.hasPointerCapture(e.pointerId)) return\n\n            let newX = startX + e.clientX\n\n            // Apply elastic resistance when dragging beyond boundaries\n            if (newX < -boundary) {\n                newX = -boundary + (newX + boundary) * elastic\n            } else if (newX > boundary) {\n                newX = boundary + (newX - boundary) * elastic\n            }\n\n            x.set(newX)\n        })\n\n        slider.addEventListener(\"pointerup\", (e) => {\n            if (!slider.hasPointerCapture(e.pointerId)) return\n\n            slider.releasePointerCapture(e.pointerId)\n            document.body.style.cursor = \"default\"\n            slider.style.cursor = \"grab\"\n            slider.classList.remove(\"active\")\n\n            // If x is outside boundary, animate to the nearest boundary\n            if (x.get() < -boundary || x.get() > boundary) {\n                const targetX = x.get() < -boundary ? -boundary : boundary\n                animate(x, targetX, {\n                    type: \"spring\",\n                    stiffness: 900,\n                    damping: 40,\n                })\n            }\n        })\n    }\n\n    // Setup event listeners\n    slider.addEventListener(\"focus\", () => {\n        document.addEventListener(\"keydown\", handleKeyDown)\n    })\n\n    slider.addEventListener(\"blur\", () => {\n        document.removeEventListener(\"keydown\", handleKeyDown)\n    })\n\n    window.addEventListener(\"resize\", () => {\n        boundary = container.clientWidth / 2\n        x.set(clamp(-boundary, boundary, x))\n    })\n\n    setupDrag()\n</script>\n\n<style>\n    body {\n        overflow: hidden;\n    }\n\n    .container {\n        position: relative;\n    }\n\n    .image-container {\n        position: relative;\n        background-color: #0b1011;\n        border: 1px solid #1d2628;\n        border-radius: 12px;\n        overflow: hidden;\n        aspect-ratio: 4/3;\n        height: 300px;\n    }\n\n    @media (max-width: 500px) {\n        .image-container {\n            height: 200px;\n        }\n    }\n\n    .image {\n        position: absolute;\n        width: 100%;\n        height: 100%;\n        object-fit: cover;\n        will-change: clip-path;\n    }\n\n    .overlay {\n        top: 0;\n        left: 0;\n        filter: grayscale(100%);\n    }\n\n    .slider {\n        position: absolute;\n        top: 0;\n        bottom: 0;\n        width: 2px;\n        background-color: white;\n        cursor: grab;\n        left: 50%;\n        transform: translateX(-50%);\n        filter: drop-shadow(2px 0 3px rgba(0, 0, 0, 0.4));\n        will-change: transform, background-color;\n    }\n\n    .handle {\n        position: absolute;\n        top: 50%;\n        left: 50%;\n        transform: translate(-50%, -50%);\n        width: 40px;\n        height: 40px;\n        background-color: white;\n        border-radius: 50%;\n        display: flex;\n        align-items: center;\n        justify-content: center;\n        color: #0f1115;\n        cursor: grab;\n        touch-action: none;\n    }\n\n    .slider:focus-visible {\n        outline: none;\n    }\n\n    .slider:focus-visible .handle {\n        outline: 4px solid #ff0088;\n        outline-offset: 2px;\n    }\n\n    .handle:active {\n        cursor: grabbing;\n    }\n</style>\n"
