            "category": "Interactions",
            "code": "<div id=\"notifications\">\n    <ul id=\"notification-list\"></ul>\n    <button class=\"add\" id=\"add-button\">Add notification +</button>\n</div>\n\n<script type=\"module\">\n    import { animate, press } from \"https://cdn.jsdelivr.net/npm/motion@12.26.1/+esm\"\n\n    // Notification state\n    let notifications = []\n    let newIndex = 0\n\n    const notificationList = document.getElementById(\"notification-list\")\n    const addButton = document.getElementById(\"add-button\")\n\n    function addNewNotification() {\n        newIndex++\n        notifications.push(newIndex)\n        createNotification(newIndex)\n    }\n\n    // Initialise with a notification\n    addNewNotification()\n\n    // Add a new notification when the button is pressed\n    press(addButton, () => {\n        animate(addButton, { scale: 0.95 })\n\n        return () => {\n            animate(addButton, { scale: 1 })\n            addNewNotification()\n        }\n    })\n\n    function createNotification(id) {\n        // Create notification elements\n        const li = document.createElement(\"li\")\n        li.id = `notification-${id}`\n        li.setAttribute(\"data-id\", id)\n\n        li.innerHTML = `\n            <div class=\"placeholder-title\"></div>\n            <div class=\"placeholder-message\"></div>\n            <button class=\"close\">\n                <svg width=\"18\" height=\"18\" viewBox=\"0 0 23 23\">\n                    <path fill=\"transparent\" stroke-width=\"3\" stroke=\"var(--feint-text)\" stroke-linecap=\"round\" d=\"M 3 16.5 L 17 2.5\" />\n                    <path fill=\"transparent\" stroke-width=\"3\" stroke=\"var(--feint-text)\" stroke-linecap=\"round\" d=\"M 3 2.5 L 17 16.346\" />\n                </svg>    \n            </button>\n        `\n\n        // Close notification when close button is pressed\n        press(li.querySelector(\".close\"), () => {\n            removeNotification(id)\n        })\n\n        // Add to list\n        animateListLayout(() => {\n            notificationList.appendChild(li)\n        })\n\n        // Animate the notification entry\n        animate(\n            li,\n            {\n                opacity: [0, 1],\n                y: [20, 0],\n                scale: [0.3, 1],\n            },\n            {\n                duration: 0.3,\n                easing: \"ease-out\",\n            }\n        )\n    }\n\n    function removeNotification(id) {\n        const notification = document.getElementById(`notification-${id}`)\n        if (!notification) return\n\n        // Remove from our array\n        notifications = notifications.filter((item) => item !== id)\n\n        // Animate exit\n        animate(\n            notification,\n            { opacity: 0, scale: 0.5 },\n            { duration: 0.2, easing: \"ease-in\" }\n        ).then(() => {\n            animateListLayout(() => {\n                notification.remove()\n            })\n        })\n    }\n\n    function animateListLayout(updateDom) {\n        // Get all notification elements and record their positions\n        const notificationElements = document.querySelectorAll(\n            \"#notification-list li\"\n        )\n        const initialPositions = new Map()\n\n        notificationElements.forEach((element) => {\n            const rect = element.getBoundingClientRect()\n            initialPositions.set(element.id, rect.top)\n        })\n\n        // Execute the DOM update (add or remove notifications)\n        updateDom()\n\n        /**\n         * Measure new positions and calculate deltas. We do this read operation in\n         * an initial pass, and **then** apply the initial delta transform/start\n         * animations (write operations) in a second pass to avoid layout thrashing.\n         */\n        const layoutDelta = []\n        notificationElements.forEach((element) => {\n            // Skip elements that were removed\n            if (!document.body.contains(element)) return\n\n            const newRect = element.getBoundingClientRect()\n            const oldTop = initialPositions.get(element.id)\n\n            if (oldTop !== undefined) {\n                const delta = oldTop - newRect.top\n                layoutDelta.push({ element, delta })\n            }\n        })\n\n        /**\n         * Apply initial delta transform and start animations (DOM write)\n         */\n        layoutDelta.forEach(({ element, delta }) => {\n            element.style.transform = `translateY(${delta}px)`\n            animate(\n                element,\n                { y: [delta, 0] },\n                {\n                    duration: 0.3,\n                    easing: \"ease-out\",\n                }\n            )\n        })\n    }\n</script>\n\n<style>\n    #notifications {\n        display: flex;\n        width: 100vw;\n        height: 100vh;\n        flex-direction: column;\n        z-index: 10000000;\n        pointer-events: none;\n    }\n\n    #notifications ul,\n    #notifications li {\n        padding: 0;\n        margin: 0;\n    }\n\n    #notifications ul {\n        position: fixed;\n        left: calc(50% - 150px);\n        bottom: 0;\n        display: flex;\n        flex-direction: column;\n        list-style: none;\n        justify-content: flex-end;\n        width: 300px;\n        gap: 10px;\n        padding: 10px;\n    }\n\n    #notifications li {\n        background: #0b1011;\n        border: 1px solid #1d2628;\n        flex: 0 0 100px;\n        position: relative;\n        border-radius: 10px;\n        pointer-events: all;\n        padding: 20px 80px 20px 20px;\n        display: flex;\n        flex-direction: column;\n        gap: 10px;\n        will-change: transform, opacity;\n    }\n\n    #notifications .placeholder-title {\n        height: 24px;\n        width: 200px;\n        border-radius: 5px;\n        background: var(--feint-text);\n    }\n\n    #notifications .placeholder-message {\n        height: 14px;\n        width: 150px;\n        border-radius: 5px;\n        background: var(--feint-text);\n        opacity: 0.5;\n    }\n\n    #notifications button {\n        outline: none;\n        -webkit-appearance: none;\n        cursor: pointer;\n        pointer-events: all;\n    }\n\n    #notifications button.add {\n        position: fixed;\n        bottom: 10px;\n        left: 10px;\n        border-radius: 10px;\n        font-size: 18px;\n        background: var(--accent);\n        color: #0f1115;\n        border: none;\n        display: flex;\n        justify-content: center;\n        align-items: center;\n        padding: 10px 12px;\n    }\n\n    #notifications button.close {\n        position: absolute;\n        top: 15px;\n        right: 10px;\n        background: transparent;\n        border: none;\n        padding: 0;\n    }\n</style>\n"
