            "code": "<div class=\"stack\">\n    <div class=\"header\">\n        <h2 class=\"header-title\">Notifications</h2>\n        <button class=\"header-close\">Collapse</button>\n    </div>\n    <div class=\"notification\" style=\"z-index: 3\"></div>\n    <div class=\"notification\" style=\"z-index: 2\"></div>\n    <div class=\"notification\" style=\"z-index: 1\"></div>\n</div>\n\n<script>\n    import { animate, press } from \"https://cdn.jsdelivr.net/npm/motion@12.26.1/+esm\"\n\n    const N_NOTIFICATIONS = 3\n    const NOTIFICATION_HEIGHT = 60\n    const NOTIFICATION_WIDTH = 280\n    const NOTIFICATION_GAP = 8\n\n    // DOM elements\n    const stack = document.querySelector(\".stack\")\n    const header = document.querySelector(\".header\")\n    const headerClose = document.querySelector(\".header-close\")\n    const notifications = document.querySelectorAll(\".notification\")\n\n    // State\n    let isOpen = true\n\n    // Stack animations\n    const stackVariants = {\n        open: {\n            y: 20,\n            scale: 0.9,\n            cursor: \"pointer\",\n        },\n        closed: {\n            y: 0,\n            scale: 1,\n            cursor: \"default\",\n        },\n    }\n\n    // Header animations\n    const headerVariants = {\n        open: {\n            y: 0,\n            scale: 1,\n            opacity: 1,\n        },\n        closed: {\n            y: 60,\n            scale: 0.8,\n            opacity: 0,\n        },\n    }\n\n    // Animation functions\n    function animateStack(state) {\n        animate(stack, stackVariants[state], {\n            type: \"spring\",\n            mass: 0.7,\n        })\n    }\n\n    function animateHeader(state) {\n        animate(header, headerVariants[state], {\n            type: \"spring\",\n            stiffness: 600,\n            damping: 50,\n            delay: state === \"open\" ? 0.2 : 0,\n        })\n    }\n\n    function animateNotification(element, index, state) {\n        const variants = {\n            open: {\n                y: 0,\n                scale: 1,\n                opacity: 1,\n                pointerEvents: \"auto\",\n                cursor: \"pointer\",\n            },\n            closed: {\n                y:\n                    -index * (NOTIFICATION_HEIGHT + NOTIFICATION_GAP) -\n                    NOTIFICATION_GAP * index,\n                scale: 1 - index * 0.1,\n                opacity: 1 - index * 0.4,\n                pointerEvents: index === 0 ? \"auto\" : \"none\",\n                cursor: index === 0 ? \"pointer\" : \"default\",\n            },\n        }\n\n        animate(element, variants[state], {\n            type: \"spring\",\n            stiffness: 600,\n            damping: 50,\n            delay: index * 0.04,\n        })\n    }\n\n    // Event handlers\n    function toggleStack() {\n        isOpen = !isOpen\n        const state = isOpen ? \"open\" : \"closed\"\n\n        animateStack(state)\n        animateHeader(state)\n        notifications.forEach((notification, index) => {\n            animateNotification(notification, index, state)\n        })\n    }\n\n    press([...notifications, headerClose], () => {\n        toggleStack()\n    })\n</script>\n\n<style>\n    .stack {\n        position: relative;\n        display: flex;\n        flex-direction: column;\n        gap: 8px;\n        will-change: transform;\n        transform: translateY(20px) scale(0.9);\n    }\n\n    .header {\n        position: absolute;\n        top: -40px;\n        left: 0;\n        height: 28px;\n        width: 100%;\n        display: flex;\n        align-items: center;\n        justify-content: space-between;\n        transform-origin: bottom center;\n        pointer-events: none;\n        will-change: transform, opacity;\n    }\n\n    .header-title {\n        font-size: 18px;\n        line-height: 1;\n        margin-left: 8px;\n    }\n\n    .header-close {\n        font-size: 14px;\n        line-height: 1;\n        margin-right: 8px;\n        background-color: #1f1f1f;\n        padding: 4px 12px;\n        height: 20px;\n        display: flex;\n        align-items: center;\n        justify-content: center;\n        border-radius: 16px;\n        cursor: pointer;\n        pointer-events: auto;\n        user-select: none;\n    }\n\n    .header-close:hover {\n        background-color: #f5f5f5;\n        color: #0f1115;\n    }\n\n    .header-close:focus-within {\n        outline: 2px solid #f5f5f5;\n        outline-offset: 2px;\n    }\n\n    .notification {\n        height: 60px;\n        width: 280px;\n        background-color: #f5f5f5;\n        border-radius: 16px;\n        display: flex;\n        justify-content: center;\n        align-items: center;\n        user-select: none;\n        will-change: transform, opacity;\n    }\n</style>\n"
