            "category": "Text",
            "code": "<div class=\"container\">\n    <h1 class=\"h1\">Move your pointer over the text to scatter.</h1>\n</div>\n\n<script type=\"module\">\n    import { animate, hover } from \"https://cdn.jsdelivr.net/npm/motion@12.26.1/+esm\"\n    import { splitText } from \"https://cdn.jsdelivr.net/npm/motion-plus@0.1.1/+esm\"\n\n    const pointerVelocity = { x: 0, y: 0 }\n    let prevEvent = 0\n\n    function animateText() {\n        const { chars } = splitText(\".h1\")\n\n        document.addEventListener(\"pointermove\", (event) => {\n            const now = performance.now()\n            const timeSinceLastEvent = (now - prevEvent) / 1000 // seconds\n            prevEvent = now\n            pointerVelocity.x = event.movementX / timeSinceLastEvent\n            pointerVelocity.y = event.movementY / timeSinceLastEvent\n        })\n\n        hover(chars, (element) => {\n            /**\n             * Calculate the speed of the pointer movement\n             * and use that to calculate the distance the character should move\n             */\n            const speed = Math.sqrt(\n                pointerVelocity.x * pointerVelocity.x +\n                    pointerVelocity.y * pointerVelocity.y\n            )\n            const angle = Math.atan2(pointerVelocity.y, pointerVelocity.x)\n            const distance = speed * 0.1\n\n            animate(\n                element,\n                {\n                    x: Math.cos(angle) * distance,\n                    y: Math.sin(angle) * distance,\n                },\n                { type: \"spring\", stiffness: 100, damping: 50 }\n            )\n        })\n    }\n\n    document.fonts.ready.then(animateText)\n</script>\n\n<style>\n    body {\n        overflow: hidden;\n    }\n\n    .container {\n        display: flex;\n        justify-content: center;\n        align-items: center;\n        width: 100%;\n        max-width: 420px;\n        text-align: left;\n        color: #0f1115;\n    }\n\n    .split-char {\n        will-change: transform, opacity;\n    }\n</style>\n"
