            "code": "<div class=\"card\">\n    <img src=\"/photos/amsterdam-cityscape/1.jpg\" alt=\"Amsterdam cityscape\" />\n    <div class=\"gradient-overlay\"></div>\n    <div class=\"caption\">A high-rise in Amsterdam Zuid</div>\n</div>\n\n<script type=\"module\">\n    import { animate, frame } from \"https://cdn.jsdelivr.net/npm/motion@12.26.1/+esm\"\n\n    const card = document.querySelector(\".card\")\n    const maxTilt = 15\n\n    const tilt = {\n        z: 0,\n        rotateX: 0,\n        rotateY: 0,\n    }\n\n    // Calculate tilt based on pointer position\n    function calculateTilt(event) {\n        const rect = card.getBoundingClientRect()\n        tilt.z = -10\n        const x = event.clientX - rect.left\n        const y = event.clientY - rect.top\n\n        // Convert coordinates to percentages\n        const xPercent = x / rect.width\n        const yPercent = y / rect.height\n\n        tilt.rotateX = maxTilt * (0.5 - yPercent)\n        tilt.rotateY = maxTilt * (xPercent - 0.5)\n    }\n\n    function animateToTilt() {\n        animate(card, {\n            transformPerspective: 500,\n            rotateX: tilt.rotateX,\n            rotateY: tilt.rotateY,\n            z: tilt.z,\n        })\n    }\n\n    function updateTilt(e) {\n        calculateTilt(e)\n        frame.postRender(animateToTilt)\n    }\n\n    card.addEventListener(\"pointerenter\", updateTilt)\n    card.addEventListener(\"pointermove\", updateTilt)\n    card.addEventListener(\"pointerleave\", () => {\n        tilt.z = 0\n        tilt.rotateX = 0\n        tilt.rotateY = 0\n        frame.postRender(animateToTilt)\n    })\n</script>\n\n<style>\n    body {\n        overflow: hidden;\n    }\n    .card {\n        position: relative;\n        width: 300px;\n        height: 400px;\n        border-radius: 20px;\n        overflow: hidden;\n        cursor: pointer;\n        background: #0b1011;\n        border: 1px solid #1d2628;\n        will-change: transform;\n        transform-style: preserve-3d;\n        perspective: 500px;\n    }\n\n    .card img {\n        width: 100%;\n        height: 100%;\n        object-fit: cover;\n        -webkit-touch-callout: none;\n        -webkit-user-select: none;\n        user-select: none;\n        touch-action: none;\n    }\n\n    .gradient-overlay {\n        position: absolute;\n        bottom: 0;\n        left: 0;\n        right: 0;\n        height: 120px;\n        z-index: 1;\n        background: linear-gradient(to top, rgba(0, 0, 0, 1), rgba(0, 0, 0, 0));\n    }\n\n    .caption {\n        position: absolute;\n        z-index: 2;\n        bottom: 20px;\n        left: 20px;\n        right: 20px;\n        color: #f5f5f5;\n        font-size: 16px;\n        line-height: 1.5;\n        text-shadow: 0 1px 2px rgba(0, 0, 0, 0.5);\n    }\n</style>\n"
