            "code": "<script setup lang=\"tsx\">\n/** @jsxImportSource vue */\nimport { motion } from \"motion-v\"\nimport { useCarousel, Carousel } from \"motion-plus-vue\"\nimport { computed, defineComponent } from \"vue\"\n\n// Chevron SVG icon components\nconst ChevronUpIcon = defineComponent({\n  name: \"ChevronUpIcon\",\n  setup() {\n    return () => (\n      <svg\n        width=\"20\"\n        height=\"20\"\n        viewBox=\"0 0 24 24\"\n        fill=\"none\"\n        stroke=\"currentColor\"\n        stroke-width=\"2\"\n        stroke-linecap=\"round\"\n        stroke-linejoin=\"round\"\n      >\n        <path d=\"M18 15l-6-6-6 6\" />\n      </svg>\n    )\n  }\n})\n\nconst ChevronDownIcon = defineComponent({\n  name: \"ChevronDownIcon\",\n  setup() {\n    return () => (\n      <svg\n        width=\"20\"\n        height=\"20\"\n        viewBox=\"0 0 24 24\"\n        fill=\"none\"\n        stroke=\"currentColor\"\n        stroke-width=\"2\"\n        stroke-linecap=\"round\"\n        stroke-linejoin=\"round\"\n      >\n        <path d=\"M6 9l6 6 6-6\" />\n      </svg>\n    )\n  }\n})\n\n// Navigation component\nconst Navigation = defineComponent({\n  name: \"Navigation\",\n  setup() {\n    const { paginationState, totalPages, nextPage, prevPage } = useCarousel()\n    const isFirst = computed(() => paginationState.value.current === 0)\n    const isLast = computed(() => paginationState.value.current === totalPages.value - 1)\n    return () => (\n      <div class=\"navigation\">\n        <motion.button\n          class={[\"nav-button\", { disabled: isFirst.value }]}\n          disabled={isFirst.value}\n          onClick={prevPage}\n          whileHover={{ scale: isFirst.value ? 1 : 1.1 }}\n          whilePress={{ scale: isFirst.value ? 1 : 0.9 }}\n        >\n          <ChevronUpIcon />\n        </motion.button>\n\n        <div class=\"dots\">\n          {Array.from({ length: totalPages.value }).map((_, idx) =>\n            <div\n              key={idx}\n              class={[\"dot\", { active: idx === paginationState.value.current }]}\n            />\n          )}\n        </div>\n\n        <motion.button\n          class={[\"nav-button\", { disabled: isLast.value }]}\n          disabled={isLast.value}\n          onClick={nextPage}\n          whileHover={{ scale: isLast.value ? 1 : 1.1 }}\n          whilePress={{ scale: isLast.value ? 1 : 0.9 }}\n          style={{ willChange: \"transform\" }}\n        >\n          <ChevronDownIcon />\n        </motion.button>\n      </div>\n    )\n  }\n})\n\n// Carousel images and aspect ratios\nconst images = [\n  { src: \"1\", aspectRatio: 4 / 3 },\n  { src: \"2\", aspectRatio: 4 / 3 },\n  { src: \"3\", aspectRatio: 4 / 3 },\n  { src: \"4\", aspectRatio: 4 / 3 },\n  { src: \"5\", aspectRatio: 4 / 3 },\n  { src: \"6\", aspectRatio: 4 / 3 },\n]\n\n</script>\n\n<template>\n  <article class=\"carousel-container\">\n    <Carousel\n      class=\"carousel\"\n      :gap=\"20\"\n      snap=\"page\"\n      :loop=\"false\"\n      :overflow=\"true\"\n      axis=\"y\"\n    >\n      <img\n        v-for=\"image in images\"\n        :key=\"image.src\"\n        class=\"photo\"\n        :src=\"`/photos/japan-bw/${image.src}.jpg`\"\n        :alt=\"`Photo ${image.src}`\"\n        :style=\"{ aspectRatio: image.aspectRatio }\"\n      />\n      <template #after>\n        <Navigation />\n      </template>\n    </Carousel>\n  </article>\n</template>\n\n\n<style scoped>\nbody {\n    overflow: hidden;\n}\n.carousel-container {\n  position: relative;\n}\n.carousel {\n  width: 400px;\n  height: 500px;\n  position: relative;\n}\n.photo {\n  width: 100%;\n  height: 450px;\n  border-radius: 12px;\n  object-fit: cover;\n}\n.navigation {\n  position: absolute;\n  right: -80px;\n  top: 50%;\n  transform: translateY(-50%);\n  display: flex;\n  flex-direction: column;\n  align-items: center;\n  gap: 12px;\n  padding: 12px 8px;\n  background: rgba(0, 0, 0, 0.6);\n  border-radius: 24px;\n  backdrop-filter: blur(10px);\n  z-index: 10;\n}\n.nav-button {\n  background: none;\n  border: none;\n  color: white;\n  cursor: pointer;\n  display: flex;\n  align-items: center;\n  justify-content: center;\n  padding: 4px;\n  transition: opacity 0.2s ease;\n}\n.nav-button.disabled {\n  opacity: 0.3;\n  cursor: not-allowed;\n}\n.nav-button:not(.disabled):hover {\n  opacity: 0.8;\n}\n.dots {\n  display: flex;\n  flex-direction: column;\n  gap: 8px;\n  align-items: center;\n}\n.dot {\n  width: 8px;\n  height: 8px;\n  border-radius: 50%;\n  background: rgba(255, 255, 255, 0.4);\n  transition: background-color 0.2s ease;\n}\n.dot.active {\n  background: white;\n}\n@media (max-width: 600px) {\n  .carousel {\n    width: 150px;\n    height: 150px;\n  }\n  .photo {\n    height: 150px;\n  }\n}\n</style>\n"
