            "code": "<script setup lang=\"ts\">\nimport {\n    animate,\n    clamp,\n    motion,\n    useDomRef,\n    useMotionValue,\n    useTransform,\n} from \"motion-v\"\nimport { computed, onMounted, ref } from \"vue\"\n\nconst props = defineProps({\n    src: {\n        type: String,\n        default: \"/photos/japan-day/7.jpg\",\n    },\n    step: {\n        type: Number,\n        default: 50,\n    },\n})\n\nconst containerRef = useDomRef()\nconst x = useMotionValue(0)\nconst boundary = ref(300)\n\n// Make the input range reactive with computed to update when boundary changes\nconst inputRange = computed(() => [-boundary.value, boundary.value])\n\nconst clipPath = useTransform(x, inputRange as any, [\n    \"inset(0% 0% 0% 0%)\",\n    \"inset(0% 0% 0% 100%)\",\n])\n\nconst lineBackgroundColor = useSliderColor(x, boundary)\n\nonMounted(() => {\n    if (containerRef.value) {\n        boundary.value = containerRef.value.clientWidth / 2\n    }\n})\n\nconst keyboard = useKeyboard(x, boundary, props.step)\n\n/**\n * ==============   Utils   ================\n */\n\nfunction useSliderColor(x, boundary) {\n    return useTransform(\n        x,\n        computed(() => [\n            -boundary.value + 20,\n            -boundary.value + 60,\n            boundary.value - 60,\n            boundary.value - 20,\n        ]) as any,\n        [\n            \"rgba(255, 255, 255, 0)\",\n            \"rgba(255, 255, 255, 1)\",\n            \"rgba(255, 255, 255, 1)\",\n            \"rgba(255, 255, 255, 0)\",\n        ]\n    )\n}\n\nfunction useKeyboard(x, boundary, step) {\n    const handleKeyDown = (event) => {\n        let moveBy = 0\n\n        if (event.key === \"ArrowLeft\") {\n            moveBy = -step\n        } else if (event.key === \"ArrowRight\") {\n            moveBy = step\n        } else {\n            return\n        }\n\n        animate(x, clamp(-boundary.value, boundary.value, x.get() + moveBy), {\n            type: \"spring\",\n            stiffness: 900,\n            damping: 40,\n            velocity: moveBy * 10,\n        })\n    }\n\n    const start = () => {\n        document.addEventListener(\"keydown\", handleKeyDown)\n    }\n\n    const stop = () => {\n        document.removeEventListener(\"keydown\", handleKeyDown)\n    }\n\n    return { start, stop }\n}\n</script>\n\n<template>\n    <div ref=\"containerRef\" class=\"container\">\n        <div class=\"image-container\">\n            <motion.img :src=\"src\" alt=\"Kirby photo\" class=\"image\" />\n            <motion.img\n                :style=\"{ clipPath }\"\n                :src=\"src\"\n                alt=\"Kirby photo in black and white\"\n                class=\"image overlay\"\n            />\n        </div>\n\n        <motion.div\n            class=\"slider\"\n            drag=\"x\"\n            :drag-elastic=\"0.05\"\n            :drag-constraints=\"containerRef\"\n            :style=\"{ x, backgroundColor: lineBackgroundColor }\"\n            tabindex=\"0\"\n            @focus=\"keyboard.start\"\n            @blur=\"keyboard.stop\"\n        >\n            <div class=\"handle\">\n                <svg\n                    xmlns=\"http://www.w3.org/2000/svg\"\n                    width=\"24\"\n                    height=\"24\"\n                    viewBox=\"0 0 24 24\"\n                    fill=\"none\"\n                    stroke=\"currentColor\"\n                    stroke-width=\"2\"\n                    stroke-linecap=\"round\"\n                    stroke-linejoin=\"round\"\n                >\n                    <path d=\"m18 8 4 4-4 4\" />\n                    <path d=\"m6 8-4 4 4 4\" />\n                    <path d=\"M8 12h.01\" />\n                    <path d=\"M12 12h.01\" />\n                    <path d=\"M16 12h.01\" />\n                </svg>\n            </div>\n        </motion.div>\n    </div>\n</template>\n\n<style>\nbody {\n    overflow: hidden;\n}\n\n.container {\n    position: relative;\n}\n\n.image-container {\n    position: relative;\n    background-color: #0b1011;\n    border: 1px solid #1d2628;\n    border-radius: 12px;\n    overflow: hidden;\n    aspect-ratio: 4/3;\n    height: 300px;\n}\n\n@media (max-width: 500px) {\n    .image-container {\n        height: 200px;\n    }\n}\n\n.image {\n    position: absolute;\n    width: 100%;\n    height: 100%;\n    object-fit: cover;\n}\n\n.overlay {\n    top: 0;\n    left: 0;\n    filter: grayscale(100%);\n}\n\n.slider {\n    position: absolute;\n    top: 0;\n    bottom: 0;\n    width: 2px;\n    background-color: white;\n    cursor: grab;\n    left: 50%;\n    transform: translateX(-50%);\n    filter: drop-shadow(2px 0 3px rgba(0, 0, 0, 0.4));\n}\n\n.handle {\n    position: absolute;\n    top: 50%;\n    left: 50%;\n    transform: translate(-50%, -50%);\n    width: 40px;\n    height: 40px;\n    background-color: white;\n    border-radius: 50%;\n    display: flex;\n    align-items: center;\n    justify-content: center;\n    color: #0f1115;\n    cursor: grab;\n    touch-action: none;\n}\n\n.slider:focus-visible {\n    outline: none;\n}\n\n.slider:focus-visible .handle {\n    outline: 4px solid #ff0088;\n    outline-offset: 2px;\n}\n\n.handle:active {\n    cursor: grabbing;\n}\n</style>\n"
