            "code": "<script setup lang=\"ts\">\nimport { ref } from 'vue'\nimport { animate, motion, clamp, mix, useMotionValue, useTransform, useDomRef } from 'motion-v'\n\nconst props = defineProps({\n  maxPull: {\n    type: Number,\n    default: 20\n  },\n  maxSquish: {\n    type: Number,\n    default: 0.92\n  },\n  maxStretch: {\n    type: Number,\n    default: 1.08\n  },\n  keyboardStep: {\n    type: Number,\n    default: 0.05\n  },\n  keyboardSpring: {\n    type: Object,\n    default: () => ({ stiffness: 200, damping: 60 })\n  }\n})\n\nconst sliderRef = useDomRef()\nconst initialDragY = ref(0)\nconst initialProgressY = ref(0)\nconst size = ref({ top: 0, bottom: 0 })\n\n/**\n * The progress of the slider, normalized between 0 and 1. During the gesture\n * this can exceed 1 or go below 0, and then we animate it back to the closest\n * edge when the gesture ends.\n */\nconst progress = useMotionValue(0.5)\n\n/**\n * The brightness of the slider, which is the progress clamped between 0 and 1.\n */\nconst brightness = useTransform(() => clamp(0, 1, progress.get()))\n\n/**\n * Use useTransform to map the progress to the y-offset of the slider, and\n * then map that y value to squish/squash scale values.\n */\nconst y = useTransform(progress, [-1, 0, 1, 2], [props.maxPull, 0, 0, -props.maxPull])\nconst scaleX = useTransform(\n  y,\n  [-props.maxPull, 0, 0, props.maxPull],\n  [props.maxSquish, 1, 1, props.maxSquish]\n)\nconst scaleY = useTransform(\n  y,\n  [-props.maxPull, 0, 0, props.maxPull],\n  [props.maxStretch, 1, 1, props.maxStretch]\n)\n\n/**\n * Invert the container scale values to insure the sun icon\n * maintains the correct aspect ratio.\n */\nconst invertScaleX = useTransform(() => invertScale(scaleX.get()))\nconst invertScaleY = useTransform(() => invertScale(scaleY.get()))\n\nconst updateProgress = (clientY) => {\n  const { top, bottom } = size.value\n  progress.set(calcProgress(bottom, top, clientY))\n}\n\nconst updateProgressWithKeyboard = (event) => {\n  if (event.key !== \"ArrowUp\" && event.key !== \"ArrowDown\") return\n\n  let current = clamp(0, 1, progress.get())\n\n  if (current <= 0.04) current = 0\n  if (current >= 0.96) current = 1\n\n  const newProgress =\n    current + (event.key === \"ArrowUp\" ? props.keyboardStep : -props.keyboardStep)\n\n  if (newProgress > 1) {\n    animate(progress, 1, {\n      velocity: 20,\n      type: \"spring\",\n      ...props.keyboardSpring,\n    })\n  } else if (newProgress < 0) {\n    animate(progress, 0, {\n      velocity: -20,\n      type: \"spring\",\n      ...props.keyboardSpring,\n    })\n  } else {\n    progress.set(clamp(0, 1, newProgress))\n  }\n}\n\nconst handleTapStart = (event) => {\n  if (!sliderRef.value) return\n\n  const { top, bottom } = sliderRef.value.getBoundingClientRect()\n  size.value = { top, bottom }\n\n  initialDragY.value = event.clientY\n  initialProgressY.value = mix(bottom, top, progress.get())\n}\n\nconst handlePan = (event) => {\n  const dragOffset = event.clientY - initialDragY.value\n  const newProgressY = initialProgressY.value + dragOffset\n  updateProgress(newProgressY)\n}\n\nconst handlePanEnd = () => {\n  /**\n   * If the slider ended outside of the normal range,\n   * animate it back to the closest edge.\n   */\n  const finalProgress = progress.get()\n  if (finalProgress < 0) {\n    animate(progress, 0)\n  } else if (finalProgress > 1) {\n    animate(progress, 1)\n  }\n}\n\nconst handleFocus = () => {\n  document.addEventListener(\"keydown\", updateProgressWithKeyboard)\n}\n\nconst handleBlur = () => {\n  document.removeEventListener(\"keydown\", updateProgressWithKeyboard)\n}\n\n/**\n * ==============   Utils   ================\n */\nfunction invertScale(scale) {\n  return 1 / scale\n}\n\nfunction calcProgress(min, max, value) {\n  return (value - min) / (max - min)\n}\n</script>\n\n<template>\n  <div class=\"container\">\n    <motion.div ref=\"sliderRef\" class=\"slider\" :style=\"{ y, scaleX, scaleY }\" @press-start=\"handleTapStart\"\n      @pan=\"handlePan\" @pan-end=\"handlePanEnd\" @focus=\"handleFocus\" @blur=\"handleBlur\" tabindex=\"0\"\n      :initial=\"{ boxShadow: '0px 0px 0px 4px #0d63f800' }\" :while-focus=\"{ boxShadow: '0px 0px 0px 4px #0d63f8ff' }\"\n      :transition=\"{ duration: 0.15 }\">\n      <motion.div class=\"indicator\" :style=\"{ scaleY: brightness }\" />\n      <motion.div :style=\"{ scaleX: invertScaleX, scaleY: invertScaleY }\">\n        <svg xmlns=\"http://www.w3.org/2000/svg\" width=\"40\" height=\"40\" viewBox=\"0 0 24 24\" fill=\"none\" stroke=\"#101417\"\n          stroke-width=\"2\" stroke-linecap=\"round\" stroke-linejoin=\"round\" class=\"icon\">\n          <circle cx=\"12\" cy=\"12\" r=\"4\" stroke=\"#101417\" fill=\"#101417\" />\n          <path d=\"M12 3v1\" />\n          <path d=\"M12 20v1\" />\n          <path d=\"M3 12h1\" />\n          <path d=\"M20 12h1\" />\n          <path d=\"m18.364 5.636-.707.707\" />\n          <path d=\"m6.343 17.657-.707.707\" />\n          <path d=\"m5.636 5.636.707.707\" />\n          <path d=\"m17.657 17.657.707.707\" />\n        </svg>\n      </motion.div>\n    </motion.div>\n  </div>\n</template>\n\n\n<style>\n.container {\n  width: 400px;\n  height: 300px;\n  overflow: hidden;\n  display: flex;\n  align-items: center;\n  justify-content: center;\n  backdrop-filter: blur(2px);\n}\n\n.slider {\n  width: 100px;\n  height: 225px;\n  background-color: rgba(255, 255, 255, 0.1);\n  border-radius: 40px;\n  overflow: hidden;\n  position: relative;\n  display: flex;\n  align-items: flex-end;\n  justify-content: center;\n  touch-action: none;\n  outline: none;\n}\n\n.indicator {\n  position: absolute;\n  top: 0;\n  left: 0;\n  bottom: 0;\n  right: 0;\n  background-color: #f5f5f5;\n  transform-origin: center bottom;\n  pointer-events: none;\n}\n\n.icon {\n  position: relative;\n  z-index: 1;\n  bottom: 20px;\n  pointer-events: none;\n}\n</style>\n"
