            "code": "<script setup lang=\"ts\">\nimport { ref } from 'vue'\nimport { motion } from 'motion-v'\nimport { AnimateNumber, Cursor } from 'motion-plus-vue'\n\nconst props = withDefaults(defineProps<{\n  data: number[]\n}>(), {\n  data: () => [30, 8, 36, 29, 50, 78]\n})\n\n// Calculate points for the SVG path\nconst points = props.data.map((value, index) => ({\n  x: 50 + index * 100,\n  y: 250 - value * 2,\n  value,\n}))\n\nconst pathD = points.reduce(buildPath, \"\")\n\nconst hoveredPoint = ref(null)\nconst percentageChange = ref<number | null>(null)\n\nconst startHover = (index) => {\n  hoveredPoint.value = index\n\n  if (index > 0) {\n    const currentValue = props.data[index]\n    const previousValue = props.data[index - 1]\n    const change = ((currentValue - previousValue) / previousValue) * 100\n    percentageChange.value = change\n  } else {\n    percentageChange.value = null\n  }\n}\n\nconst endHover = () => {\n  hoveredPoint.value = null\n  percentageChange.value = null\n}\n\n/**\n * ==============   Utils   ================\n */\n\nfunction buildPath(path, point, i) {\n  if (i === 0) return `M ${point.x},${point.y}`\n  return `${path} L ${point.x},${point.y}`\n}\n</script>\n\n<template>\n  <div class=\"container\" @pointerleave=\"endHover\">\n    <svg width=\"600\" height=\"300\" class=\"svg-style\">\n      <defs>\n        <linearGradient\n          id=\"areaGradient\"\n          x1=\"0\"\n          x2=\"0\"\n          y1=\"0\"\n          y2=\"1\"\n        >\n          <stop\n            offset=\"0%\"\n            stop-color=\"#4ff0b7\"\n            stop-opacity=\"0.2\"\n          />\n          <stop\n            offset=\"100%\"\n            stop-color=\"#4ff0b7\"\n            stop-opacity=\"0\"\n          />\n        </linearGradient>\n      </defs>\n\n      <!-- Background grid lines -->\n      <motion.line\n        v-for=\"y in [50, 100, 150, 200, 250]\"\n        :key=\"y\"\n        x1=\"50\"\n        :y1=\"y\"\n        x2=\"550\"\n        :y2=\"y\"\n        stroke=\"#1d2628\"\n        stroke-width=\"1\"\n        stroke-dasharray=\"5,5\"\n      />\n\n      <!-- Area fill under the line -->\n      <motion.path\n        :d=\"`${pathD} L 550,250 L 50,250 Z`\"\n        fill=\"url(#areaGradient)\"\n      />\n\n      <!-- Main graph line -->\n      <motion.path\n        :d=\"pathD\"\n        fill=\"none\"\n        stroke=\"#4ff0b7\"\n        stroke-width=\"2\"\n        :initial=\"{ pathLength: 0 }\"\n        :animate=\"{ pathLength: 1 }\"\n        :transition=\"{ duration: 1.5, ease: 'easeInOut' }\"\n      />\n\n      <!-- Points -->\n      <motion.g\n        initial=\"hidden\"\n        animate=\"visible\"\n        :variants=\"{\n          visible: {\n            transition: {\n              delayChildren: 0.1,\n              staggerChildren: 1.5 / 6,\n            },\n          },\n        }\"\n      >\n        <g v-for=\"(point, index) in points\" :key=\"index\">\n          <!-- Hit area -->\n          <motion.rect\n            :x=\"point.x - 50\"\n            y=\"0\"\n            width=\"100\"\n            height=\"300\"\n            fill=\"transparent\"\n            @hover-start=\"startHover(index)\"\n            :style=\"{ cursor: 'pointer' }\"\n          />\n          <!-- Dot -->\n          <motion.circle\n            :cx=\"point.x\"\n            :cy=\"point.y\"\n            r=\"6\"\n            fill=\"#0b1011\"\n            stroke=\"#4ff0b7\"\n            stroke-width=\"1\"\n            :animate=\"{ scale: hoveredPoint === index ? 1.5 : 1 }\"\n            :variants=\"{\n              hidden: { scale: 0.5, opacity: 0 },\n              visible: { scale: 1, opacity: 1 },\n            }\"\n          />\n        </g>\n      </motion.g>\n    </svg>\n    \n    <Cursor\n      v-if=\"percentageChange !== null\"\n      :follow=\"true\"\n      :initial=\"{ opacity: 0, scale: 0.5 }\"\n      :offset=\"{ x: 20, y: 20 }\"\n      key=\"cursor\"\n    >\n      <div class=\"number-container\">\n        <motion.div\n          :initial=\"false\"\n          :animate=\"{\n            rotate: percentageChange < 0 ? 180 : 0,\n            color: percentageChange < 0 ? '#ff0088' : '#4ff0b7',\n          }\"\n          :transition=\"{\n            type: 'spring',\n            visualDuration: 0.6,\n            bounce: 0.2,\n          }\"\n          class=\"arrow-style\"\n        >\n          \n        </motion.div>\n        <AnimateNumber\n          :initial=\"false\"\n          :animate=\"{\n            color: percentageChange < 0 ? '#ff0088' : '#4ff0b7',\n          }\"\n          :transition=\"{\n            type: 'spring',\n            visualDuration: 0.6,\n            bounce: 0.2,\n          }\"\n          class=\"number-style\"\n          :format=\"{\n            notation: 'compact',\n            compactDisplay: 'short',\n          }\"\n          suffix=\"%\"\n          :value=\"percentageChange\"\n        />\n      </div>\n    </Cursor>\n  </div>\n</template>\n\n<style>\n/**\n * ==============   Styles   ================\n */\n.container {\n  background-color: #0b1011;\n  padding: 20px;\n  border-radius: 12px;\n  border: 1px solid #1d2628;\n}\n\n.svg-style {\n  overflow: visible;\n}\n\n.number-container {\n  display: flex;\n  align-items: center;\n  gap: 8px;\n  background-color: #0b1011;\n  border: 1px solid rgba(255,255,255,0.1);\n  padding: 6px 12px;\n  border-radius: 40px;\n  will-change: transform;\n}\n\n.number-style {\n  font-size: 24px;\n}\n\n.arrow-style {\n  font-size: 24px;\n  font-weight: bold;\n}\n</style>\n"
