            "code": "<script setup lang=\"tsx\">\n/**@jsxImportSource vue */\nimport { ref, onMounted, onUnmounted, watch, nextTick, defineComponent } from \"vue\"\nimport { motion, AnimatePresence,useDomRef } from \"motion-v\"\n\n// ==============   State   ================\nconst isOpen = ref(false)\nconst dialogRef = useDomRef()\n\n// ==============   Animation States   ================\nconst dialogOpenState = {\n    opacity: 1,\n    filter: \"blur(0px)\",\n    rotateX: 0,\n    rotateY: 0,\n    z: 0,\n    transition: {\n        delay: 0.2,\n        duration: 0.5,\n        ease: [0.17, 0.67, 0.51, 1],\n        opacity: {\n            delay: 0.2,\n            duration: 0.5,\n            ease: \"easeOut\",\n        },\n    },\n}\n\nconst dialogInitialState = {\n    opacity: 0,\n    filter: \"blur(10px)\",\n    z: -100,\n    rotateY: 25,\n    rotateX: 5,\n    transformPerspective: 500,\n    transition: {\n        duration: 0.3,\n        ease: [0.67, 0.17, 0.62, 0.64],\n    },\n}\n\n// ==============   Utils   ================\n// Check if click is outside the dialog\nfunction checkClickOutside(event: MouseEvent, element: HTMLElement) {\n    const { top, left, width, height } = element.getBoundingClientRect()\n    if (\n        event.clientX < left ||\n        event.clientX > left + width ||\n        event.clientY < top ||\n        event.clientY > top + height\n    ) {\n        return true\n    }\n    return false\n}\n\n// Close dialog on outside click\nfunction useClickOutside(refEl: typeof dialogRef, close: () => void) {\n    const handler = (event: MouseEvent) => {\n        if (refEl.value && checkClickOutside(event, refEl.value)) {\n            close()\n        }\n    }\n    onMounted(() => {\n        document.addEventListener(\"click\", handler)\n    })\n    onUnmounted(() => {\n        document.removeEventListener(\"click\", handler)\n    })\n}\n\n// ==============   Dialog Open/Close   ================\nfunction openDialog() {\n    isOpen.value = true\n}\nfunction closeDialog() {\n    isOpen.value = false\n}\n\n// Open/close dialog imperatively for native <dialog> API\nwatch(isOpen, async (open) => {\n    await nextTick()\n    if (dialogRef.value) {\n        if (open) {\n            dialogRef.value.showModal()\n        } else {\n            dialogRef.value.close()\n        }\n    }\n})\n\nuseClickOutside(dialogRef, closeDialog)\n\n// ==============   Close Icon   ================\nconst CloseIcon = defineComponent({\n  name: \"CloseIcon\",\n  setup() {\n    return () => (\n      <svg\n        xmlns=\"http://www.w3.org/2000/svg\"\n        width=\"18\"\n        height=\"18\"\n        fill=\"none\"\n        aria-hidden=\"true\"\n        focusable=\"false\"\n        style=\"display: block\"\n      >\n        <path d=\"M 0 0 L 18 0 L 18 18 L 0 18 Z\" fill=\"transparent\"></path>\n        <path\n          d=\"M 14.063 3.938 L 3.938 14.063 M 14.063 14.063 L 3.938 3.938\"\n          fill=\"rgb(0,0,0)\"\n          stroke-width=\"1.3\"\n          stroke=\"rgb(255, 255, 255)\"\n          stroke-linecap=\"round\"\n          stroke-linejoin=\"round\"\n        ></path>\n      </svg>\n    )\n  }\n})\n\n</script>\n\n<template>\n  <div>\n    <motion.button\n      class=\"openButton\"\n      :whileHover=\"{ scale: 1.1 }\"\n      :whilePress=\"{ scale: 1.05 }\"\n      data-primary-action\n      @click=\"openDialog\"\n    >\n      Open Modal\n    </motion.button>\n    <AnimatePresence>\n      <template v-if=\"isOpen\">\n        <motion.div\n          class=\"overlay\"\n          :initial=\"{ opacity: 0 }\"\n          :animate=\"{ opacity: 1 }\"\n          :exit=\"{ opacity: 0 }\"\n        />\n        <motion.dialog\n          ref=\"dialogRef\"\n          class=\"modal\"\n          :initial=\"dialogInitialState\"\n          :animate=\"dialogOpenState\"\n          :exit=\"dialogInitialState\"\n          :open=\"false\"\n          :style=\"{ transformPerspective: '500px' }\"\n          @cancel.prevent=\"closeDialog\"\n          @close=\"closeDialog\"\n        >\n          <h2 class=\"title\">Confirm</h2>\n          <p>Are you sure you want to become a Motion expert?</p>\n          <div class=\"controls\">\n            <button @click=\"closeDialog\" class=\"cancel\">\n              Cancel\n            </button>\n            <button @click=\"closeDialog\" class=\"save\">\n              Expert me\n            </button>\n          </div>\n          <button\n            class=\"closeButton\"\n            aria-label=\"Close\"\n            @click=\"closeDialog\"\n          >\n            <CloseIcon />\n          </button>\n        </motion.dialog>\n      </template>\n    </AnimatePresence>\n  </div>\n</template>\n\n<style>\n.openButton, .controls button {\n    background-color: #ff0088;\n    color: #f5f5f5;\n    font-size: 16px;\n    padding: 10px 20px;\n    border-radius: 10px;\n}\n\n.controls {\n    border-top: 1px solid var(--divider);\n    padding-top: 20px;\n    margin-top: 20px;\n    display: flex;\n    justify-content: flex-end;\n    gap: 10px;\n}\n\n.controls button.cancel {\n    background-color: var(--divider);\n}\n\n.modal {\n    border-radius: 10px;\n    border: 1px solid #1d2628;\n    background-color: #0b1011;\n    position: relative;\n    z-index: 10000000;\n    padding: 20px;\n    min-width: 300px;\n}\n\n.modal p {\n    margin: 0;\n}\n\n.modal::backdrop {\n    display: none;\n}\n\n.title {\n    font-size: 24px;\n    margin: 0 0 20px;\n}\n\n.closeButton {\n    position: absolute;\n    top: 20px;\n    right: 20px;\n}\n\n.overlay {\n    background: rgba(0, 0, 0, 0.5);\n    position: fixed;\n    inset: 0;\n    z-index: 9999999;\n    backdrop-filter: blur(3px);\n}\n</style>\n"
