            "code": "<script setup lang=\"tsx\">\n/** @jsxImportSource vue */\nimport { motion } from 'motion-v'\nimport { CSSProperties, defineComponent } from 'vue'\n\n/**\n * ==============   Components   ================\n */\n\nconst Card = defineComponent({\n    props: {\n        emoji: { type: String, required: true },\n        hueA: { type: Number, required: true },\n        hueB: { type: Number, required: true },\n        i: { type: Number, required: true }\n    },\n    setup(props) {\n        const background = `linear-gradient(306deg, ${hue(props.hueA)}, ${hue(props.hueB)})`\n        \n        return () => (\n            <motion.div\n                style={cardContainer}\n                initial=\"offscreen\"\n                whileInView=\"onscreen\"\n                inViewOptions={{ amount: 0.8 }}\n            >\n                <div style={{ ...splash, background }} />\n                <motion.div style={card} variants={cardVariants} class=\"card\">\n                    {props.emoji}\n                </motion.div>\n            </motion.div>\n        )\n    }\n})\n\n/**\n * ==============   Animation   ================\n */\n\nconst cardVariants = {\n    offscreen: {\n        y: 300,\n    },\n    onscreen: {\n        y: 50,\n        rotate: -10,\n        transition: {\n            type: \"spring\",\n            bounce: 0.4,\n            duration: 0.8,\n        },\n    },\n}\n\nconst hue = (h: number) => `hsl(${h}, 100%, 50%)`\n\n/**\n * ==============   Data   ================\n */\n\nconst food: [string, number, number][] = [\n    [\"\", 340, 10],\n    [\"\", 20, 40],\n    [\"\", 60, 90],\n    [\"\", 80, 120],\n    [\"\", 100, 140],\n    [\"\", 205, 245],\n    [\"\", 260, 290],\n    [\"\", 290, 320],\n]\n\n/**\n * ==============   Styles   ================\n */\n\nconst container = {\n    margin: \"100px auto\",\n    maxWidth: '500px',\n    paddingBottom: '100px',\n    width: \"100%\",\n}\n\nconst cardContainer = {\n    overflow: \"hidden\",\n    display: \"flex\",\n    justifyContent: \"center\",\n    alignItems: \"center\",\n    position: \"relative\",\n    paddingTop: '20px',\n    marginBottom: '-120px',\n}\n\nconst splash: CSSProperties = {\n    position: \"absolute\",\n    top: 0,\n    left: 0,\n    right: 0,\n    bottom: 0,\n    clipPath: `path(\"M 0 303.5 C 0 292.454 8.995 285.101 20 283.5 L 460 219.5 C 470.085 218.033 480 228.454 480 239.5 L 500 430 C 500 441.046 491.046 450 480 450 L 20 450 C 8.954 450 0 441.046 0 430 Z\")`,\n}\n\nconst card = {\n    fontSize: '164px',\n    width: '300px',\n    height: '430px',\n    display: \"flex\",\n    justifyContent: \"center\",\n    alignItems: \"center\",\n    borderRadius: '20px',\n    background: \"#f5f5f5\",\n    boxShadow:\n        \"0 0 1px hsl(0deg 0% 0% / 0.075), 0 0 2px hsl(0deg 0% 0% / 0.075), 0 0 4px hsl(0deg 0% 0% / 0.075), 0 0 8px hsl(0deg 0% 0% / 0.075), 0 0 16px hsl(0deg 0% 0% / 0.075)\",\n    transformOrigin: \"10% 60%\",\n}\n</script>\n\n<template>\n    <div :style=\"container\">\n        <Card \n            v-for=\"([emoji, hueA, hueB], i) in food\" \n            :key=\"emoji\"\n            :emoji=\"emoji\"\n            :hueA=\"hueA\"\n            :hueB=\"hueB\"\n            :i=\"i\"\n        />\n    </div>\n</template>\n\n<style>\n  #sandbox > div{\n    width:100%;\n  }\n</style>\n"
