            "code": "<script setup>\nimport { motion, useDomRef, useSpring } from \"motion-v\"\n\nconst props = defineProps({\n    maxTilt: {\n        type: Number,\n        default: 15,\n    },\n})\n\nconst cardRef = useDomRef()\nconst z = useSpring(0)\nconst rotateX = useSpring(0)\nconst rotateY = useSpring(0)\n\nconst calculateTilt = (event) => {\n    if (!cardRef.value) return { rotateX: 0, rotateY: 0 }\n\n    const rect = cardRef.value.getBoundingClientRect()\n    const x = event.clientX - rect.left\n    const y = event.clientY - rect.top\n\n    // Convert coordinates to percentages\n    const xPercent = x / rect.width\n    const yPercent = y / rect.height\n    return {\n        rotateX: props.maxTilt * (0.5 - yPercent),\n        rotateY: props.maxTilt * (xPercent - 0.5),\n    }\n}\n\nconst handlePointerMove = (e) => {\n    const tilt = calculateTilt(e)\n    rotateX.set(tilt.rotateX)\n    rotateY.set(tilt.rotateY)\n}\n\nconst handlePointerLeave = () => {\n    rotateX.set(0)\n    rotateY.set(0)\n    z.set(0)\n}\n\nconst handlePointerEnter = () => {\n    z.set(-10)\n}\n</script>\n\n<template>\n    <motion.div\n        ref=\"cardRef\"\n        class=\"card\"\n        :style=\"{\n            transformPerspective: '500px',\n            z,\n            rotateX,\n            rotateY,\n        }\"\n        :transition=\"{ type: 'spring', stiffness: 200, damping: 20 }\"\n        @pointermove=\"handlePointerMove\"\n        @pointerleave=\"handlePointerLeave\"\n        @pointerenter=\"handlePointerEnter\"\n    >\n        <img\n            src=\"/photos/amsterdam-cityscape/1.jpg\"\n            alt=\"Amsterdam cityscape\"\n            class=\"image\"\n        />\n        <div class=\"gradient-overlay\" />\n        <div class=\"caption\">A high-rise in Amsterdam Zuid</div>\n    </motion.div>\n</template>\n\n<style>\n.card {\n    position: relative;\n    width: 300px;\n    height: 400px;\n    border-radius: 20px;\n    overflow: hidden;\n    background: #0b1011;\n    border: 1px solid #1d2628;\n    will-change: transform;\n}\n\n.image {\n    width: 100%;\n    height: 100%;\n    object-fit: cover;\n    -webkit-touch-callout: none;\n    -webkit-user-select: none;\n    user-select: none;\n    touch-action: none;\n}\n\n.gradient-overlay {\n    position: absolute;\n    bottom: 0;\n    left: 0;\n    right: 0;\n    height: 120px;\n    z-index: 1;\n    background: linear-gradient(to top, rgba(0, 0, 0, 1), rgba(0, 0, 0, 0));\n}\n\n.caption {\n    position: absolute;\n    z-index: 2;\n    bottom: 20px;\n    left: 20px;\n    right: 20px;\n    color: #f5f5f5;\n    font-size: 16px;\n    line-height: 1.5;\n    text-shadow: 0 1px 2px rgba(0, 0, 0, 0.5);\n}\n</style>\n"
