            "code": "<script setup lang=\"tsx\">\n/**@jsxImportSource vue */\nimport { ref, onMounted, CSSProperties, defineComponent } from \"vue\"\nimport { animate, AnimationSequence, mix, wrap } from \"motion-v\"\nimport { splitText } from \"motion-plus\"\nimport { Typewriter } from \"motion-plus-vue\"\n\n// Countdown text array\nconst text = [\n  \"Ten\",\n  \"Nine\",\n  \"Eight\",\n  \"Seven\",\n  \"Six\",\n  \"Five\",\n  \"Four\",\n  \"Three\",\n  \"Two\",\n  \"One\",\n]\n\nconst index = ref(0)\nconst state = ref<\"typing\" | \"exploding\">(\"typing\")\n\n// Style objects\nconst container: CSSProperties = {\n  display: \"flex\",\n  alignItems: \"flex-start\",\n  gap: 0,\n  flexDirection: \"column\",\n  width: \"400px\",\n  fontSize: \"48px\",\n  textTransform: \"uppercase\",\n  lineHeight: 1,\n  color: \"var(--accent)\",\n  height: \"48px\",\n}\n\nconst animatingText: CSSProperties = {\n  fontSize: \"48px\",\n  fontWeight: 700,\n  lineHeight: 1,\n  height: \"48px\",\n}\n\nconst cursor = {\n  background: \"#ff0088\",\n  width: \"3px\",\n  height: \"0.8em\",\n  top: \"0.05em\",\n}\n\n// Handler for when the typewriter animation completes\nfunction handleTypewriterComplete() {\n  state.value = \"exploding\"\n}\n\n// Handler for when the explosion animation completes\nfunction handleExplodeComplete() {\n  index.value = wrap(0, text.length, index.value + 1)\n  state.value = \"typing\"\n}\n\n/**\n * ExplodingText\n * Animates each character outward with random velocity and angle.\n */\nconst ExplodingText = defineComponent({\n  name: \"ExplodingText\",\n  props: {\n    text: { type: String, required: true },\n    minVelocity: { type: Number, default: 200 },\n    maxVelocity: { type: Number, default: 400 },\n    onComplete: { type: Function, required: true },\n  },\n  setup(props) {\n    const el = ref<HTMLElement>()\n\n    onMounted(() => {\n      if (!el.value) return\n      const { chars } = splitText(el.value)\n      const sequence:AnimationSequence = chars.map((char: HTMLElement) => {\n        const velocity = mix(props.minVelocity, props.maxVelocity, Math.random())\n        // Angle between -45deg and +45deg (in radians)\n        const angle = (Math.random() - 0.5) * (Math.PI / 2)\n        return [\n          char,\n          {\n            opacity: 0,\n            x: Math.cos(angle) * velocity,\n            y: Math.sin(angle) * velocity,\n          },\n          {\n            type: \"inertia\",\n            velocity,\n            at: \"<\",\n          },\n        ]\n      })\n      const animation = animate(sequence, { delay: 0.1 })\n      animation.finished.then(() => props.onComplete())\n      // Clean up\n      return () => animation.stop()\n    })\n\n    return () => {\n      return (\n        <div\n          class=\"animating-text\"\n          ref={el}\n          aria-label={props.text}\n          style={animatingText}\n        >\n          {props.text}\n        </div>\n      )\n    }\n  },\n})\n</script>\n\n<template>\n  <h2 :style=\"container\">\n    <Typewriter v-if=\"state === 'typing'\" as=\"div\" :cursor-style=\"cursor\" :text-style=\"animatingText\"\n      class=\"animating-text\" @complete=\"handleTypewriterComplete\">\n      {{ text[index] }}\n    </Typewriter>\n    <ExplodingText v-else :text=\"text[index]\"  @complete=\"handleExplodeComplete\" />\n  </h2>\n  <Stylesheet />\n</template>\n\n/**\n* ============== Styles ================\n*/\n<style>\n.container {\n  display: flex;\n  align-items: flex-start;\n  gap: 0;\n  flex-direction: column;\n  width: 400px;\n  font-size: 48px;\n  text-transform: uppercase;\n  line-height: 1;\n  color: var(--accent);\n  height: 48px;\n}\n\n.animating-text span,\n.animating-text div {\n  font-size: 48px;\n  font-weight: 700;\n  line-height: 1;\n  height: 48px;\n}\n\n.cursor {\n  background: #ff0088;\n  width: 3px;\n}\n</style>\n"
