            "category": "Dialog",
            "code": "\"use client\"\n\nimport { motion, MotionConfig } from \"motion/react\"\nimport { PropsWithChildren, useId, useState } from \"react\"\n\nfunction Item({ header, children }: PropsWithChildren<{ header: string }>) {\n    const [isOpen, setIsOpen] = useState(false)\n    const [hasFocus, setHasFocus] = useState(false)\n    const id = useId()\n\n    return (\n        <MotionConfig transition={{ duration: 0.3 }}>\n            <motion.section\n                initial={false}\n                animate={isOpen ? \"open\" : \"closed\"}\n            >\n                <h3>\n                    <motion.button\n                        id={id + \"-button\"}\n                        aria-expanded={isOpen}\n                        aria-controls={id}\n                        onClick={() => setIsOpen(!isOpen)}\n                        onFocus={onlyKeyboardFocus(() => setHasFocus(true))}\n                        onBlur={() => setHasFocus(false)}\n                    >\n                        <span>{header}</span> <ChevronDownIcon />\n                        {hasFocus && (\n                            <motion.div\n                                layoutId=\"focus-ring\"\n                                className=\"focus-ring\"\n                            />\n                        )}\n                    </motion.button>\n                </h3>\n                <motion.div\n                    variants={{\n                        open: {\n                            height: \"auto\",\n                            maskImage:\n                                \"linear-gradient(to bottom, black 100%, transparent 100%)\",\n                        },\n                        closed: {\n                            height: 0,\n                            maskImage:\n                                \"linear-gradient(to bottom, black 50%, transparent 100%)\",\n                        },\n                    }}\n                    id={id}\n                    aria-labelledby={id + \"-button\"}\n                    className=\"accordion-content\"\n                >\n                    <motion.div\n                        variants={{\n                            open: {\n                                filter: \"blur(0px)\",\n                                opacity: 1,\n                            },\n                            closed: {\n                                filter: \"blur(2px)\",\n                                opacity: 0,\n                            },\n                        }}\n                    >\n                        {children}\n                    </motion.div>\n                </motion.div>\n                <hr />\n            </motion.section>\n        </MotionConfig>\n    )\n}\n\nexport default function Accordion() {\n    return (\n        <>\n            <div className=\"accordion\">\n                <Item header=\"What is Motion+?\">\n                    <p>\n                        Motion+ is a one-time fee, lifetime access membership\n                        that unlocks the source code for all Motion examples,\n                        early access features, premium components, and an\n                        exclusive Discord community.\n                    </p>\n                </Item>\n                <Item header={`What does \"lifetime access\" mean?`}>\n                    <p>\n                        Just that! No one needs another subscription in their\n                        life.\n                    </p>\n                    <p>\n                        {`Lifetime access means you'll receive all updates to Motion+ as they're released.`}\n                    </p>\n                </Item>\n                <Item header=\"How does the team package work?\">\n                    <p>\n                        After purchase, you can nominate up to 10 team members\n                        to join Motion+.\n                    </p>\n                </Item>\n            </div>\n            <StyleSheet />\n        </>\n    )\n}\n\n/**\n * ==============   Icons   ================\n */\nfunction ChevronDownIcon() {\n    return (\n        <motion.svg\n            xmlns=\"http://www.w3.org/2000/svg\"\n            width=\"24\"\n            height=\"24\"\n            viewBox=\"0 0 24 24\"\n            fill=\"none\"\n            stroke=\"currentColor\"\n            strokeWidth=\"2\"\n            strokeLinecap=\"round\"\n            strokeLinejoin=\"round\"\n            variants={{\n                open: { rotate: 180 },\n                closed: { rotate: 0 },\n            }}\n        >\n            <path d=\"m6 9 6 6 6-6\" />\n        </motion.svg>\n    )\n}\n\n/**\n * ==============   Utils   ================\n */\nfunction onlyKeyboardFocus(callback: () => void) {\n    return (e: React.FocusEvent<HTMLButtonElement>) => {\n        if (e.type === \"focus\" && e.target.matches(\":focus-visible\")) {\n            callback()\n        }\n    }\n}\n\n/**\n * ==============   Styles   ================\n */\nfunction StyleSheet() {\n    return (\n        <style>{`\n        .accordion {\n            display: flex;\n            flex-direction: column;\n            margin: 20px;\n            background: #0b1011;\n            border: 1px solid #1d2628;\n            border-radius: 10px;\n            min-width: 300px;\n            max-width: 500px;\n        }\n\n        .accordion section {\n            padding: 20px;\n            position: relative;\n        }\n\n        .accordion h3 {\n            margin: 0;\n            display: flex;\n        }\n\n        .accordion hr {\n            margin: 0;\n            border: 0;\n            border-bottom: 1px solid #1d2628;\n            position: absolute;\n            bottom: 0;\n            left: 20px;\n            right: 20px;\n            z-index: 0;\n        }\n\n        .accordion section:last-child hr {\n            display: none;\n        }\n\n        .accordion h3 button {\n            display: flex;\n            justify-content: space-between;\n            align-items: center;\n            flex: 1;\n            position: relative;\n        }\n\n        .accordion h3 span,\n        .accordion h3 svg {\n            text-align: left;\n            z-index: 1;\n            position: relative;\n        }\n\n        .accordion .focus-ring {\n            position: absolute;\n            inset: -10px;\n            background: var(--hue-4-transparent);\n            border-radius: 5px;\n            z-index: 0;\n        }\n\n        .accordion-content {\n            overflow: hidden;\n        }\n\n        .accordion-content > div {\n            padding: 20px 0 0;\n        }\n\n        .accordion-content p {\n            line-height: 1.5;\n            padding: 0;\n            margin: 0;\n        }\n    `}</style>\n    )\n}\n"
