            "code": "\"use client\"\n\nimport { AnimateActivity } from \"motion-plus/animate-activity\"\nimport { motion } from \"motion/react\"\nimport { forwardRef, SVGProps, useState } from \"react\"\n\n/**\n * A simple utility to wrap a number within a range.\n */\nconst wrap = (min: number, max: number, v: number) => {\n    const rangeSize = max - min\n    return ((((v - min) % rangeSize) + rangeSize) % rangeSize) + min\n}\n\nexport default function AnimateActivitySlider() {\n    const items = Array.from(\n        { length: 10 }, //  { length: 10 },\n        (_, i) => `/photos/tokyo-night/image-${30 + i}.jpg`\n    )\n    const [activeIndex, setActiveIndex] = useState(0)\n    const [direction, setDirection] = useState<1 | -1 | 0>(0)\n\n    function setSlide(newDirection: 1 | -1) {\n        setDirection(newDirection)\n        setActiveIndex(wrap(0, items.length, activeIndex + newDirection))\n    }\n\n    const buttonColor = \"rgba(0, 0, 0, 0.4)\"\n    const focusOutlineColor = \"rgba(255, 255, 255, 0.6)\"\n\n    return (\n        <div style={container}>\n            <motion.button\n                aria-label=\"Previous\"\n                style={{ ...button, backgroundColor: buttonColor }}\n                onClick={() => setSlide(-1)}\n                whileFocus={{ outline: `2px solid ${focusOutlineColor}` }}\n                whileTap={{ scale: 0.9 }}\n            >\n                <ArrowLeft />\n            </motion.button>\n            <div style={slideContainer}>\n                {items.map((item, i) => (\n                    <AnimateActivity\n                        key={item}\n                        mode={i === activeIndex ? \"visible\" : \"hidden\"}\n                        layoutMode=\"pop\"\n                    >\n                        <Slide direction={direction} imageUrl={item} />\n                    </AnimateActivity>\n                ))}\n            </div>\n            <motion.button\n                aria-label=\"Next\"\n                style={{ ...button, backgroundColor: buttonColor }}\n                onClick={() => setSlide(1)}\n                whileFocus={{ outline: `2px solid ${focusOutlineColor}` }}\n                whileTap={{ scale: 0.9 }}\n            >\n                <ArrowRight />\n            </motion.button>\n        </div>\n    )\n}\n\nconst Slide = forwardRef(function Slide(\n    {\n        imageUrl,\n        direction,\n    }: {\n        imageUrl: string\n        direction: number\n    },\n    ref: React.Ref<HTMLDivElement>\n) {\n    return (\n        <motion.div\n            ref={ref}\n            initial={{ opacity: 0, x: direction * 50 }}\n            animate={{\n                opacity: [0, 1],\n                x: [direction * 50, 0],\n                transition: {\n                    delay: 0.2,\n                    type: \"spring\",\n                    visualDuration: 0.3,\n                    bounce: 0.4,\n                },\n            }}\n            exit={{ opacity: 0, x: -direction * 50 }}\n            style={box}\n        >\n            <img src={imageUrl} alt=\"Tokyo at night\" style={imageStyle} />\n        </motion.div>\n    )\n})\n\n/**\n * ==============   Icons   ================\n */\nconst iconsProps: SVGProps<SVGSVGElement> = {\n    xmlns: \"http://www.w3.org/2000/svg\",\n    width: \"24\",\n    height: \"24\",\n    viewBox: \"0 0 24 24\",\n    fill: \"none\",\n    stroke: \"currentColor\",\n    strokeWidth: \"2\",\n    strokeLinecap: \"round\",\n    strokeLinejoin: \"round\",\n}\n\nfunction ArrowLeft() {\n    return (\n        <svg {...iconsProps}>\n            <path d=\"m12 19-7-7 7-7\" />\n            <path d=\"M19 12H5\" />\n        </svg>\n    )\n}\n\nfunction ArrowRight() {\n    return (\n        <svg {...iconsProps}>\n            <path d=\"M5 12h14\" />\n            <path d=\"m12 5 7 7-7 7\" />\n        </svg>\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\n\nconst container: React.CSSProperties = {\n    display: \"flex\",\n    position: \"relative\",\n    justifyContent: \"center\",\n    alignItems: \"center\",\n    gap: 10,\n    width: \"100%\",\n    height: \"100%\",\n}\n\nconst slideContainer: React.CSSProperties = {\n    width: 400,\n    height: 300,\n    position: \"relative\",\n    display: \"flex\",\n    justifyContent: \"center\",\n    alignItems: \"center\",\n}\n\nconst box: React.CSSProperties = {\n    width: \"100%\",\n    height: \"100%\",\n    borderRadius: \"10px\",\n    position: \"absolute\",\n    top: 0,\n    left: 0,\n    overflow: \"hidden\",\n}\n\nconst imageStyle: React.CSSProperties = {\n    display: \"block\",\n    width: \"100%\",\n    height: \"100%\",\n    objectFit: \"cover\",\n}\n\nconst button: React.CSSProperties = {\n    width: 40,\n    height: 40,\n    borderRadius: \"50%\",\n    display: \"flex\",\n    justifyContent: \"center\",\n    alignItems: \"center\",\n    position: \"relative\",\n    zIndex: 1,\n    outlineOffset: 2,\n    border: \"none\",\n    cursor: \"pointer\",\n    color: \"white\",\n}\n"
