            "code": "\"use client\"\n\nimport { AnimatePresence, motion } from \"motion/react\"\nimport { useState } from \"react\"\n\nfunction ModeExample({\n    mode,\n    icon,\n    state,\n}: {\n    mode: \"sync\" | \"wait\" | \"popLayout\"\n    icon: React.ReactNode\n    state: boolean\n}) {\n    const defaultEase = [0.26, 0.02, 0.23, 0.94]\n    const motionProps = {\n        style: {\n            ...baseCircleStyle,\n            backgroundColor: state ? \"#f5f5f5\" : \"transparent\",\n            color: state ? \"#0f1115\" : \"#f5f5f5\",\n            border: state\n                ? \"2px solid #1d2628\"\n                : \"2px solid #f5f5f5\",\n        },\n        initial: { opacity: 0, scale: 0.6 },\n        animate: {\n            opacity: 1,\n            scale: 1,\n            ease: mode === \"wait\" ? [0.02, 0.35, 0.25, 0.99] : defaultEase,\n        },\n        exit: {\n            opacity: 0,\n            scale: 0.8,\n            ease: mode === \"wait\" ? [0.46, 0.04, 0.97, 0.44] : defaultEase,\n        },\n        transition: { duration: 0.3 },\n    }\n\n    return (\n        <div style={modeSection}>\n            <div style={iconContainer}>\n                <AnimatePresence mode={mode}>\n                    <motion.div key={String(state)} {...motionProps}>\n                        {icon}\n                    </motion.div>\n                </AnimatePresence>\n            </div>\n            <code style={modeTitle}>{mode}</code>\n        </div>\n    )\n}\n\nexport default function AnimatePresenceModes() {\n    const [state, setState] = useState(true)\n\n    const switchItems = () => {\n        setState((prev) => !prev)\n    }\n\n    return (\n        <div style={container}>\n            <div style={modesContainer}>\n                <ModeExample mode=\"sync\" icon={<SyncIcon />} state={state} />\n                <ModeExample mode=\"wait\" icon={<WaitIcon />} state={state} />\n                <ModeExample\n                    mode=\"popLayout\"\n                    icon={<PopLayoutIcon />}\n                    state={state}\n                />\n            </div>\n\n            <motion.button\n                style={button}\n                onClick={switchItems}\n                whileTap={{ scale: 0.95 }}\n            >\n                Switch\n            </motion.button>\n        </div>\n    )\n}\n\nfunction SyncIcon() {\n    return (\n        <svg\n            width=\"24\"\n            height=\"24\"\n            viewBox=\"0 0 24 24\"\n            fill=\"none\"\n            stroke=\"currentColor\"\n            strokeWidth=\"2\"\n            strokeLinecap=\"round\"\n            strokeLinejoin=\"round\"\n        >\n            <path d=\"M21 12a9 9 0 0 0-9-9 9.75 9.75 0 0 0-6.74 2.74L3 8\" />\n            <path d=\"M3 3v5h5\" />\n            <path d=\"M3 12a9 9 0 0 0 9 9 9.75 9.75 0 0 0 6.74-2.74L21 16\" />\n            <path d=\"M16 16h5v5\" />\n        </svg>\n    )\n}\n\nfunction WaitIcon() {\n    return (\n        <svg\n            width=\"24\"\n            height=\"24\"\n            viewBox=\"0 0 24 24\"\n            fill=\"none\"\n            stroke=\"currentColor\"\n            strokeWidth=\"2\"\n            strokeLinecap=\"round\"\n            strokeLinejoin=\"round\"\n        >\n            <path d=\"M12 2v4\" />\n            <path d=\"m16.2 7.8 2.9-2.9\" />\n            <path d=\"M18 12h4\" />\n            <path d=\"m16.2 16.2 2.9 2.9\" />\n            <path d=\"M12 18v4\" />\n            <path d=\"m4.9 19.1 2.9-2.9\" />\n            <path d=\"M2 12h4\" />\n            <path d=\"m4.9 4.9 2.9 2.9\" />\n        </svg>\n    )\n}\n\nfunction PopLayoutIcon() {\n    return (\n        <svg\n            width=\"24\"\n            height=\"24\"\n            viewBox=\"0 0 24 24\"\n            fill=\"none\"\n            stroke=\"currentColor\"\n            strokeWidth=\"2\"\n            strokeLinecap=\"round\"\n            strokeLinejoin=\"round\"\n        >\n            <path d=\"M21 13v6a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h6\" />\n            <path d=\"m21 3-9 9\" />\n            <path d=\"M15 3h6v6\" />\n        </svg>\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\n\nconst container: React.CSSProperties = {\n    display: \"flex\",\n    flexDirection: \"column\",\n    alignItems: \"center\",\n    gap: \"40px\",\n    color: \"#f5f5f5\",\n    borderRadius: \"12px\",\n}\n\nconst modesContainer: React.CSSProperties = {\n    display: \"flex\",\n    gap: \"60px\",\n    justifyContent: \"center\",\n    alignItems: \"center\",\n    width: \"100%\",\n}\n\nconst modeSection: React.CSSProperties = {\n    display: \"flex\",\n    flexDirection: \"column\",\n    alignItems: \"center\",\n    gap: \"16px\",\n}\n\nconst iconContainer: React.CSSProperties = {\n    width: \"80px\",\n    height: \"80px\",\n    position: \"relative\",\n    display: \"flex\",\n    alignItems: \"center\",\n    justifyContent: \"center\",\n    flexDirection: \"column\",\n}\n\nconst modeTitle: React.CSSProperties = {\n    fontSize: \"14px\",\n    fontWeight: \"500\",\n    color: \"#f5f5f5\",\n    opacity: \"0.9\",\n}\n\nconst button: React.CSSProperties = {\n    backgroundColor: \"#f5f5f5\",\n    color: \"#0f1115\",\n    border: \"none\",\n    borderRadius: \"8px\",\n    padding: \"12px 32px\",\n    fontSize: \"14px\",\n    fontWeight: \"500\",\n    cursor: \"pointer\",\n    outline: \"none\",\n}\n\nconst baseCircleStyle: React.CSSProperties = {\n    width: \"80px\",\n    height: \"80px\",\n    borderRadius: \"50%\",\n    display: \"flex\",\n    alignItems: \"center\",\n    justifyContent: \"center\",\n    willChange: \"transform\",\n    boxSizing: \"border-box\",\n    flexShrink: 0,\n}\n"
