            "category": "Experimental",
            "code": "\"use client\"\n\nimport { motion, MotionValue, useMotionValue, useTransform } from \"motion/react\"\n\nexport default function AppleWatchHomeScreen() {\n    /**\n     * Arbitrary starting position to center an icon.\n     * A more sophisticated implementation could use measurements\n     * and calculations to decide which is the center or most\n     * important icon.\n     */\n    const x = useMotionValue(-225)\n    const y = useMotionValue(-225)\n\n    return (\n        <div className=\"device\">\n            <motion.div\n                drag\n                dragConstraints={{\n                    left: -650,\n                    right: 50,\n                    top: -600,\n                    bottom: 50,\n                }}\n                style={{\n                    width: 1000,\n                    height: 1000,\n                    x,\n                    y,\n                    background: \"transparent\",\n                    willChange: \"transform\",\n                }}\n            >\n                {grid.map((rows, rowIndex) =>\n                    rows.map((colIndex: number) => (\n                        <Item\n                            key={`${rowIndex}-${colIndex}`}\n                            row={rowIndex}\n                            col={colIndex}\n                            planeX={x}\n                            planeY={y}\n                        />\n                    ))\n                )}\n            </motion.div>\n            <Stylesheet />\n        </div>\n    )\n}\n\nfunction Item({ row, col, planeX, planeY }: ItemProps) {\n    const xOffset =\n        col * (icon.size + icon.margin) +\n        (row % 2) * ((icon.size + icon.margin) / 2)\n    const yOffset = row * icon.size\n\n    const screenOffsetX = useTransform(() => planeX.get() + xOffset + 20)\n    const screenOffsetY = useTransform(() => planeY.get() + yOffset + 20)\n    const { x, xScale } = useTransform(screenOffsetX, xRange, {\n        x: translateRange,\n        xScale: scaleRange,\n    })\n    const { y, yScale } = useTransform(screenOffsetY, yRange, {\n        y: translateRange,\n        yScale: scaleRange,\n    })\n    const scale = useTransform(() => Math.min(xScale.get(), yScale.get()))\n\n    return (\n        <motion.div\n            style={{\n                position: \"absolute\",\n                left: xOffset,\n                top: yOffset,\n                x,\n                y,\n                scale,\n                width: icon.size,\n                height: icon.size,\n                borderRadius: \"50%\",\n                contain: \"strict\",\n                willChange: \"transform\",\n                background: \"white\",\n                opacity: 0.9,\n            }}\n        />\n    )\n}\n\n// Fill a grid of numbers to represent each app icon\nconst grid = new Array(10).fill([0, 1, 2, 3, 4, 5, 6, 7])\n\ninterface ItemProps {\n    row: number\n    col: number\n    planeX: MotionValue<number>\n    planeY: MotionValue<number>\n}\n\n/**\n * ==============   Settings   ================\n */\nconst icon = {\n    margin: 20,\n    size: 100,\n}\n\nconst device = {\n    width: 368,\n    height: 448,\n}\n\n/**\n * ==============   Utils   ================\n */\n\n// Transform mapping functions\nconst createScreenRange = (axis: keyof typeof device) => [\n    -60,\n    80,\n    device[axis] - (icon.size + icon.margin) / 2 - 80,\n    device[axis] - (icon.size + icon.margin) / 2 + 60,\n]\n\nconst scaleRange = [0, 1, 1, 0]\nconst translateRange = [50, 0, 0, -50]\nconst xRange = createScreenRange(\"width\")\nconst yRange = createScreenRange(\"height\")\n\n/**\n * ==============   Styles   ================\n */\nfunction Stylesheet() {\n    return (\n        <style>\n            {`\n        .device {\n          width: ${device.width}px;\n          height: ${device.height}px;\n          background-color: #0b1011;\n          border: 1px solid #1d2628;\n          border-radius: 80px;\n          overflow: hidden;\n          position: relative;\n          touch-action: none;\n          user-select: none;\n        }\n      `}\n        </style>\n    )\n}\n"
