            "category": "Basics",
            "code": "\"use client\"\n\nimport { delay } from \"motion\"\nimport * as motion from \"motion/react-client\"\nimport { useEffect, useState } from \"react\"\n\nexport default function Keyframes() {\n    const [aspectRatio, setAspectRatio] = useState(1)\n    const [width, setWidth] = useState(100)\n\n    const debouncedAspectRatio = useDebouncedState(aspectRatio)\n    const debouncedWidth = useDebouncedState(width)\n\n    return (\n        <div id=\"example\">\n            <div className=\"container\">\n                <motion.div\n                    layout\n                    className=\"box\"\n                    style={{\n                        aspectRatio: debouncedAspectRatio,\n                        width: debouncedWidth,\n                        borderRadius: 20,\n                    }}\n                ></motion.div>\n            </div>\n            <div className=\"inputContainer\">\n                <div>\n                    <Input\n                        value={aspectRatio}\n                        set={(newValue) => setAspectRatio(newValue)}\n                        min={0.1}\n                        max={5}\n                        step={0.1}\n                    >\n                        Aspect ratio\n                    </Input>\n                    <Input\n                        value={width}\n                        set={(newValue) => setWidth(newValue)}\n                        min={10}\n                        max={1000}\n                        step={5}\n                    >\n                        Width\n                    </Input>\n                </div>\n            </div>\n            <StyleSheet />\n        </div>\n    )\n}\n\ninterface InputProps {\n    children: string\n    value: number\n    set: (newValue: number) => void\n    min?: number\n    max?: number\n    step?: number\n}\n\nfunction Input({ value, children, set, min = 0, max = 100, step }: InputProps) {\n    return (\n        <label>\n            <code>{children}</code>\n            <input\n                value={value}\n                type=\"range\"\n                min={min}\n                max={max}\n                step={step}\n                onChange={(e) => set(parseFloat(e.target.value))}\n            />\n            <input\n                type=\"number\"\n                value={value}\n                min={min}\n                max={max}\n                onChange={(e) => set(parseFloat(e.target.value) || 0)}\n            />\n        </label>\n    )\n}\n\nfunction useDebouncedState<T>(value: T, duration: number = 0.2): T {\n    const [debouncedValue, setDebouncedValue] = useState<T>(value)\n    useEffect(() => {\n        return delay(() => setDebouncedValue(value), duration)\n    }, [value, duration])\n    return debouncedValue\n}\n\n/**\n * ==============   Styles   ================\n */\n\nfunction StyleSheet() {\n    return (\n        <style>{`\n            .container {\n                display: flex;\n                flex-direction: column;\n                justify-content: center;\n                align-items: center;\n                width: 300px;\n                height: 300px;\n                gap: 20px;\n            }\n\n            .box {\n                background-color: #8df0cc;\n                position: relative;\n                z-index: 1;\n            }\n\n            .inputContainer {\n                display: flex;\n                flex-direction: row;\n                gap: 20px;\n                background-color: #0b1011;\n                padding: 20px 40px;\n                border-radius: 10px;\n                position: relative;\n                z-index: 2;\n            }\n\n            #example {\n                display: flex;\n                align-items: center;\n                flex-direction: column;\n            }\n\n            #example input {\n                accent-color: #8df0cc;\n                font-family: \"Azeret Mono\", monospace;\n                font-size: 12px;\n            }\n\n            #example .inputs {\n                display: flex;\n                flex-direction: column;\n                padding-left: 50px;\n            }\n\n            #example label {\n                display: flex;\n                align-items: center;\n                margin: 10px 0;\n                font-size: 12px;\n            }\n\n            #example label code {\n                width: 100px;\n            }\n\n            #example input[type=\"number\"] {\n                border: 0;\n                border-bottom: 1px dotted #8df0cc;\n                color: #8df0cc;\n                margin-left: 10px;\n                background: transparent;\n            }\n\n            #example input[type=\"number\"]:focus {\n                outline: none;\n                border-bottom: 2px solid #8df0cc;\n            }\n\n            #example input[type=\"number\"]::-webkit-inner-spin-button {\n                -webkit-appearance: none;\n            }\n\n            input[type='range']::-webkit-slider-runnable-track {\n                height: 10px;\n                -webkit-appearance: none;\n                background: #0b1011;\n                border: 1px solid #1d2628;\n                border-radius: 10px;\n                margin-top: -1px;\n            }\n\n            input[type='range']::-webkit-slider-thumb {\n                -webkit-appearance: none;\n                height: 20px;\n                width: 20px;\n                border-radius: 50%;\n                background: #8df0cc;\n                top: -4px;\n                position: relative;\n            }\n        `}</style>\n    )\n}\n"
