            "code": "\"use client\"\n\nimport { Progress } from \"@base-ui-components/react/progress\"\nimport { animate, motion, useMotionValue, useTransform } from \"motion/react\"\nimport { useEffect } from \"react\"\n\nfunction BaseProgress() {\n    const progress = useMotionValue(0)\n    const roundedProgress = useTransform(() => Math.round(progress.get()))\n    const pathLength = useTransform(progress, [0, 100], [0, 1])\n    const strokeLinecap = useTransform(() =>\n        pathLength.get() === 0 ? \"none\" : \"round\"\n    )\n    const { filter, scale, opacity } = useTransform(progress, [0, 100], {\n        filter: [\"blur(10px)\", \"blur(0px)\"],\n        scale: [0.5, 1],\n        opacity: [0.5, 1],\n    })\n\n    useEffect(() => {\n        animate(progress, 100, { duration: 3, ease: [0.31, 0.05, 0.28, 0.85] })\n    }, [progress])\n\n    return (\n        <>\n            <div className=\"container\">\n                <Progress.Root className=\"root\" value={roundedProgress.get()}>\n                    <Progress.Indicator\n                        render={\n                            <div\n                                className=\"indicator-container\"\n                                style={{ width: \"100%\" }}\n                            >\n                                <svg\n                                    className=\"indicator\"\n                                    viewBox=\"0 0 100 100\"\n                                >\n                                    <motion.path\n                                        d=\"M50 10 A40 40 0 1 1 50 90 A40 40 0 1 1 50 10\"\n                                        fill=\"none\"\n                                        stroke=\"var(--divider)\"\n                                        strokeWidth=\"8\"\n                                    />\n                                    <motion.path\n                                        d=\"M50 10 A40 40 0 1 1 50 90 A40 40 0 1 1 50 10\"\n                                        fill=\"none\"\n                                        stroke=\"#8df0cc\"\n                                        strokeWidth=\"8\"\n                                        style={{\n                                            pathLength,\n                                            strokeLinecap,\n                                        }}\n                                    />\n                                </svg>\n                                <div className=\"input-container\">\n                                    <motion.div\n                                        className=\"input\"\n                                        style={{\n                                            filter,\n                                            scale,\n                                            opacity,\n                                            willChange:\n                                                \"filter, transform, opacity\",\n                                        }}\n                                    >\n                                        {roundedProgress}\n                                    </motion.div>\n                                </div>\n                            </div>\n                        }\n                    />\n                </Progress.Root>\n            </div>\n            <StyleSheet />\n        </>\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\nfunction StyleSheet() {\n    return (\n        <style>{`\n            .container {\n                display: flex;\n                align-items: center;\n                justify-content: center;\n                min-height: 200px;\n            }\n\n            .root {\n                position: relative;\n                width: 200px;\n                height: 200px;\n            }\n\n            .indicator-container {\n                position: relative;\n                width: 100%;\n                height: 100%;\n            }\n\n            .indicator {\n                width: 100%;\n                height: 100%;\n            }\n\n            .input-container {\n                position: absolute;\n                top: 0;\n                left: 0;\n                width: 100%;\n                height: 100%;\n                display: flex;\n                align-items: center;\n                justify-content: center;\n            }\n\n            .input {\n                font-size: 64px;\n                text-align: center;\n                color: #8df0cc;\n            }\n        `}</style>\n    )\n}\n\nexport default BaseProgress\n"
