            "code": "\"use client\"\n\nimport { Radio } from \"@base-ui-components/react/radio\"\nimport { RadioGroup } from \"@base-ui-components/react/radio-group\"\nimport {\n    AnimatePresence,\n    motion,\n    useMotionValue,\n    useTransform,\n} from \"motion/react\"\nimport { useState } from \"react\"\n\nfunction BaseRadio() {\n    const [value, setValue] = useState(\"default\")\n    const scale = useMotionValue(1)\n    const borderWidth = useTransform(scale, [0.95, 1.05], [3, 1.5])\n\n    return (\n        <>\n            <RadioGroup\n                className=\"root\"\n                value={value}\n                onValueChange={(newValue) => setValue(newValue as string)}\n            >\n                {[\"default\", \"comfortable\", \"compact\"].map((size) => (\n                    <div className=\"item-wrapper\" key={size}>\n                        <Radio.Root\n                            className=\"radio-item\"\n                            value={size}\n                            id={size}\n                            render={\n                                <motion.button\n                                    style={{ scale, willChange: \"transform\" }}\n                                    whileHover={{ scale: 1.05 }}\n                                    whileTap={{ scale: 0.95 }}\n                                >\n                                    <AnimatePresence>\n                                        {value === size && (\n                                            <Radio.Indicator className=\"indicator\">\n                                                <motion.div\n                                                    className=\"border\"\n                                                    style={{ borderWidth }}\n                                                    initial={false}\n                                                    animate={{\n                                                        borderColor: \"#ffff\",\n                                                        scale: 1,\n                                                    }}\n                                                    exit={{\n                                                        borderColor: \"#fff0\",\n                                                        scale: 0.95,\n                                                    }}\n                                                    transition={{\n                                                        duration: 0.2,\n                                                        ease: \"easeOut\",\n                                                    }}\n                                                />\n                                                <motion.div\n                                                    className=\"dot\"\n                                                    initial={{ scale: 0 }}\n                                                    animate={{\n                                                        scale: 1,\n                                                        opacity: 1,\n                                                    }}\n                                                    exit={{\n                                                        scale: 0,\n                                                        opacity: 0,\n                                                    }}\n                                                    transition={{\n                                                        type: \"spring\",\n                                                        stiffness: 400,\n                                                        damping: 25,\n                                                    }}\n                                                />\n                                            </Radio.Indicator>\n                                        )}\n                                    </AnimatePresence>\n                                </motion.button>\n                            }\n                        />\n                        <label htmlFor={size} className=\"label\">\n                            {size.charAt(0).toUpperCase() + size.slice(1)}\n                        </label>\n                    </div>\n                ))}\n            </RadioGroup>\n            <StyleSheet />\n        </>\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\nfunction StyleSheet() {\n    return (\n        <style>{`\n            .root {\n                display: flex;\n                flex-direction: column;\n                gap: 10px;\n            }\n            .item-wrapper {\n                display: flex;\n                align-items: center;\n            }\n            .radio-item {\n                background-color: #0b1011;\n                border: 1px solid #1d2628;\n                width: 25px;\n                height: 25px;\n                border-radius: 50%;\n                outline: none;\n                cursor: pointer;\n            }\n            .indicator {\n                display: flex;\n                align-items: center;\n                justify-content: center;\n                width: 100%;\n                height: 100%;\n                position: relative;\n            }\n            .border {\n                position: absolute;\n                inset: 0;\n                border-radius: 50%;\n                border-style: solid;\n            }\n            .dot {\n                width: 11px;\n                height: 11px;\n                border-radius: 50%;\n                background-color: #8df0cc;\n            }\n            .label {\n                color: #f5f5f5;\n                padding-left: 15px;\n                font-size: 18px;\n                line-height: 1;\n                cursor: pointer;\n            }\n        `}</style>\n    )\n}\n\nexport default BaseRadio\n"
