            "code": "\"use client\"\n\nimport { Select } from \"@base-ui-components/react/select\"\nimport { AnimatePresence, motion } from \"motion/react\"\nimport { useState } from \"react\"\n\nfunction BaseSelect() {\n    const [open, setOpen] = useState(false)\n    const [value, setValue] = useState<string | null>(null)\n\n    const fruits = [\n        { value: \"apple\", label: \"Apple\" },\n        { value: \"banana\", label: \"Banana\" },\n        { value: \"cherry\", label: \"Cherry\" },\n        { value: \"date\", label: \"Date\" },\n        { value: \"elderberry\", label: \"Elderberry\" },\n    ]\n\n    return (\n        <>\n            <Select.Root\n                open={open}\n                onOpenChange={setOpen}\n                value={value}\n                onValueChange={setValue}\n                items={fruits}\n            >\n                <Select.Trigger\n                    className=\"trigger\"\n                    render={\n                        <motion.button\n                            whileHover={{ scale: 1.02 }}\n                            whileTap={{ scale: 0.98 }}\n                            style={{ willChange: \"transform\" }}\n                        >\n                            <Select.Value>\n                                {(selectedValue) =>\n                                    selectedValue\n                                        ? fruits.find(\n                                              (f) => f.value === selectedValue\n                                          )?.label\n                                        : \"Select a fruit...\"\n                                }\n                            </Select.Value>\n                            <Select.Icon>\n                                <ChevronDownIcon />\n                            </Select.Icon>\n                        </motion.button>\n                    }\n                />\n\n                <AnimatePresence>\n                    {open && (\n                        <Select.Portal>\n                            <Select.Positioner>\n                                <Select.Popup\n                                    className=\"content\"\n                                    render={\n                                        <motion.div\n                                            initial={{\n                                                opacity: 0,\n                                                scale: 0.95,\n                                            }}\n                                            animate={{ opacity: 1, scale: 1 }}\n                                            exit={{ opacity: 0, scale: 0.95 }}\n                                            transition={{ duration: 0.15 }}\n                                            style={{\n                                                willChange:\n                                                    \"transform, opacity\",\n                                            }}\n                                        >\n                                            {fruits.map((fruit) => (\n                                                <Select.Item\n                                                    key={fruit.value}\n                                                    value={fruit.value}\n                                                    className=\"item\"\n                                                    render={\n                                                        <motion.div\n                                                            initial={{\n                                                                color: \"#f5f5f5\",\n                                                                backgroundColor:\n                                                                    \"#0b1011\",\n                                                            }}\n                                                            whileHover={{\n                                                                color: \"#0f1115\",\n                                                                backgroundColor:\n                                                                    \"#8df0cc\",\n                                                            }}\n                                                            transition={{\n                                                                duration: 0.1,\n                                                                ease: \"linear\",\n                                                            }}\n                                                            style={{\n                                                                willChange:\n                                                                    \"background-color\",\n                                                            }}\n                                                        />\n                                                    }\n                                                >\n                                                    <Select.ItemText>\n                                                        {fruit.label}\n                                                    </Select.ItemText>\n                                                </Select.Item>\n                                            ))}\n                                        </motion.div>\n                                    }\n                                />\n                            </Select.Positioner>\n                        </Select.Portal>\n                    )}\n                </AnimatePresence>\n            </Select.Root>\n            <StyleSheet />\n        </>\n    )\n}\n\nfunction ChevronDownIcon() {\n    return (\n        <svg\n            xmlns=\"http://www.w3.org/2000/svg\"\n            width=\"16\"\n            height=\"16\"\n            viewBox=\"0 0 24 24\"\n            fill=\"none\"\n            stroke=\"currentColor\"\n            strokeWidth=\"2\"\n            strokeLinecap=\"round\"\n            strokeLinejoin=\"round\"\n        >\n            <path d=\"m6 9 6 6 6-6\" />\n        </svg>\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\nfunction StyleSheet() {\n    return (\n        <style>{`\n            .container {\n                display: flex;\n                align-items: center;\n                justify-content: center;\n                min-height: 200px;\n            }\n\n            .trigger {\n                display: inline-flex;\n                align-items: center;\n                justify-content: space-between;\n                border-radius: 5px;\n                padding: 0 15px;\n                font-size: 16px;\n                line-height: 1;\n                height: 35px;\n                gap: 5px;\n                width: 200px;\n                background-color: #0b1011;\n                color: #f5f5f5;\n                border: 1px solid #1d2628;\n            }\n\n            .content {\n                overflow: hidden;\n                background-color: #0b1011;\n                color: #f5f5f5;\n                border: 1px solid #1d2628;\n                border-radius: 5px;\n            }\n\n            .viewport {\n                padding: 5px;\n            }\n\n            .item {\n                font-size: 16px;\n                line-height: 1;\n                border-radius: 3px;\n                display: flex;\n                align-items: center;\n                height: 25px;\n                padding-right: 35px;\n                padding-left: 25px;\n                position: relative;\n                user-select: none;\n                cursor: pointer;\n            }\n\n            .item[data-highlighted] {\n                outline: none;\n                background-color: #0f1115;\n                color: #f5f5f5;\n            }\n        `}</style>\n    )\n}\n\nexport default BaseSelect\n"
