            "code": "\"use client\"\n\nimport { motion, Transition } from \"motion/react\"\nimport { useState } from \"react\"\n\n/**\n * A toggle switch that demonstrates custom easing functions with both bounce and spring animations.\n */\nfunction App() {\n    const [isOn, setIsOn] = useState(true)\n\n    return (\n        <div className=\"container\">\n            <div\n                className=\"switch\"\n                data-is-on={isOn}\n                onClick={() => setIsOn(!isOn)}\n            >\n                <motion.div\n                    className=\"ball\"\n                    layout\n                    transition={isOn ? spring : bounce}\n                />\n            </div>\n        </div>\n    )\n}\n\n/**\n * ================  Constants  =========================\n */\n\nconst bounce: Transition = {\n    duration: 1.2,\n    ease: bounceEase,\n}\n\nconst spring: Transition = {\n    type: \"spring\",\n    stiffness: 700,\n    damping: 30,\n}\n\n/**\n * ================  Utils  =========================\n */\n\n// From https://easings.net/#easeOutBounce\nfunction bounceEase(x: number) {\n    const n1 = 7.5625\n    const d1 = 2.75\n\n    if (x < 1 / d1) {\n        return n1 * x * x\n    } else if (x < 2 / d1) {\n        return n1 * (x -= 1.5 / d1) * x + 0.75\n    } else if (x < 2.5 / d1) {\n        return n1 * (x -= 2.25 / d1) * x + 0.9375\n    } else {\n        return n1 * (x -= 2.625 / d1) * x + 0.984375\n    }\n}\n\n/**\n * ==============   Styles   ================\n */\n\nfunction Stylesheet() {\n    return (\n        <style>\n            {`\n                .container {\n                    display: flex;\n                    align-items: center;\n                    justify-content: center;\n                    flex-direction: column;\n                    min-height: 100vh;\n                }\n\n                .switch {\n                    width: 80px;\n                    height: 200px;\n                    background-color: #fff3;\n                    display: flex;\n                    align-items: flex-end;\n                    border-radius: 50px;\n                    padding: 10px;\n                    cursor: pointer;\n                }\n\n                .switch[data-is-on=\"true\"] {\n                    align-items: flex-start;\n                }\n\n                .ball {\n                    width: 80px;\n                    height: 80px;\n                    background-color: #f5f5f5;\n                    border-radius: 40px;\n                    will-change: transform;\n                }\n            `}\n        </style>\n    )\n}\n\nexport default function AppWithStyles() {\n    return (\n        <>\n            <App />\n            <Stylesheet />\n        </>\n    )\n}\n"
