            "category": "Lists",
            "code": "\"use client\"\n\nimport {\n    animate,\n    easeIn,\n    mix,\n    motion,\n    progress,\n    useMotionValue,\n    useTransform,\n    wrap,\n} from \"motion/react\"\nimport Image from \"next/image\"\nimport { useEffect, useRef, useState } from \"react\"\n\ninterface CardStackProps {\n    images?: { src: string; ratio: number }[]\n    maxRotate?: number\n}\n\nexport default function CardStack({\n    images = defaultImages,\n    maxRotate = 5,\n}: CardStackProps) {\n    const [currentIndex, setCurrentIndex] = useState(0)\n    const ref = useRef<HTMLUListElement>(null)\n    const [width, setWidth] = useState(400)\n\n    useEffect(() => {\n        if (!ref.current) return\n        setWidth(ref.current.offsetWidth)\n    }, [])\n\n    return (\n        <>\n            <ul className=\"stack\" ref={ref}>\n                {images.map((image, index) => {\n                    return (\n                        <StackImage\n                            {...image}\n                            minDistance={width * 0.5}\n                            maxRotate={maxRotate}\n                            key={image.src}\n                            index={index}\n                            currentIndex={currentIndex}\n                            totalImages={images.length}\n                            setNextImage={() => {\n                                setCurrentIndex(\n                                    wrap(0, images.length, currentIndex + 1)\n                                )\n                            }}\n                        />\n                    )\n                })}\n            </ul>\n\n            <Instructions />\n            <Stylesheet />\n        </>\n    )\n}\n\ninterface StackImageProps {\n    src: string\n    ratio: number\n    index: number\n    totalImages: number\n    currentIndex: number\n    maxRotate: number\n    minDistance?: number\n    minSpeed?: number\n    setNextImage: () => void\n}\n\nfunction StackImage({\n    src,\n    ratio,\n    index,\n    currentIndex,\n    totalImages,\n    maxRotate,\n    setNextImage,\n    minDistance = 400,\n    minSpeed = 50,\n}: StackImageProps) {\n    /**\n     * Math.sin(index) is a way of generating a value between -1 and 1 in a\n     * deterministic way that can provide a pleasing distribution throughout a range.\n     * For instance passing it to `mix(0, maxRotate)` will give us a nice\n     * distribution throughout -maxRotate and maxRotate.\n     */\n    const baseRotation = mix(0, maxRotate, Math.sin(index))\n    const x = useMotionValue(0)\n    const rotate = useTransform(\n        x,\n        [0, 400],\n        [baseRotation, baseRotation + 10],\n        { clamp: false }\n    )\n    const zIndex = totalImages - wrap(totalImages, 0, index - currentIndex + 1)\n\n    const onDragEnd = () => {\n        const distance = Math.abs(x.get())\n        const speed = Math.abs(x.getVelocity())\n\n        if (distance > minDistance || speed > minSpeed) {\n            setNextImage()\n\n            animate(x, 0, {\n                type: \"spring\",\n                stiffness: 600,\n                damping: 50,\n            })\n        } else {\n            animate(x, 0, {\n                type: \"spring\",\n                stiffness: 300,\n                damping: 50,\n            })\n        }\n    }\n\n    const opacity = progress(totalImages * 0.25, totalImages * 0.75, zIndex)\n\n    const progressInStack = progress(0, totalImages - 1, zIndex)\n    const scale = mix(0.5, 1, easeIn(progressInStack))\n\n    return (\n        <motion.li\n            className=\"item\"\n            style={{\n                width: ratio > 1 ? \"100%\" : \"auto\",\n                height: ratio <= 1 ? \"100%\" : \"auto\",\n                aspectRatio: ratio,\n                zIndex,\n                rotate,\n                x,\n            }}\n            initial={{ opacity: 0, scale: 0.3 }}\n            animate={{ opacity, scale }}\n            whileTap={index === currentIndex ? { scale: 0.98 } : {}}\n            transition={{\n                type: \"spring\",\n                stiffness: 600,\n                damping: 30,\n            }}\n            drag={index === currentIndex ? \"x\" : false}\n            onDragEnd={onDragEnd}\n        >\n            <Image\n                src={src}\n                fill\n                alt=\"\"\n                onPointerDown={(e) => e.preventDefault()}\n            />\n        </motion.li>\n    )\n}\n\nfunction Instructions() {\n    return (\n        <p className=\"instructions big\">\n            Swipe the top photo left or right.\n            <br />\n            Swiped photos move to the back of the stack.\n        </p>\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\nfunction Stylesheet() {\n    return (\n        <style>\n            {`\n          body {\n            overflow: hidden;\n          }\n\n          .stack {\n            position: relative;\n            width: 400px;\n            height: 400px;\n            max-width: 90vw;\n            list-style: none;\n            margin: 0;\n            padding: 0;\n          }\n\n          @media (max-width: 600px) {\n            .stack {\n              width: 200px;\n              height: 200px;\n            }\n          }\n            \n          .item {\n            border-radius: 10px;\n            overflow: hidden;\n            will-change: transform, opacity, filter;\n            top: 50%;\n            left: 50%;\n            translate: -50% -50%;\n            position: absolute;\n            background: #0001;\n            filter: drop-shadow(1px 3px 5px rgba(0,0,0,0.3));\n          }\n\n          .item img {\n            width: 100%;\n            height: 100%;\n            user-select: none;\n            -webkit-user-select: none;\n            touch-action: none;\n          }\n\n          .instructions {\n            font-size: 14px;\n            color: var(--feint-text);\n            text-align: center;\n            line-height: 1.4;\n            padding: 20px 8px;\n          }\n            `}\n        </style>\n    )\n}\n\n/**\n * ==============   Data   ================\n * Photos courtesy of https://citizenofnowhe.re\n */\nconst defaultImages = [\n    {\n        src: \"/photos/prague/image-03.jpg\",\n        ratio: 3 / 4,\n    },\n    {\n        src: \"/photos/prague/image-09.jpg\",\n        ratio: 3 / 4,\n    },\n    {\n        src: \"/photos/prague/image-01.jpg\",\n        ratio: 4 / 3,\n    },\n    {\n        src: \"/photos/prague/image-11.jpg\",\n        ratio: 4 / 3,\n    },\n    {\n        src: \"/photos/prague/image-07.jpg\",\n        ratio: 4 / 3,\n    },\n    {\n        src: \"/photos/prague/image-12.jpg\",\n        ratio: 4 / 3,\n    },\n    {\n        src: \"/photos/prague/image-05.jpg\",\n        ratio: 4 / 3,\n    },\n    {\n        src: \"/photos/prague/image-13.jpg\",\n        ratio: 3 / 4,\n    },\n    {\n        src: \"/photos/prague/image-08.jpg\",\n        ratio: 4 / 3,\n    },\n    {\n        src: \"/photos/prague/image-02.jpg\",\n        ratio: 4 / 3,\n    },\n    {\n        src: \"/photos/prague/image-06.jpg\",\n        ratio: 4 / 3,\n    },\n    {\n        src: \"/photos/prague/image-10.jpg\",\n        ratio: 3 / 4,\n    },\n    {\n        src: \"/photos/prague/image-04.jpg\",\n        ratio: 4 / 3,\n    },\n]\n"
