            "code": "\"use client\"\n\nimport { Carousel, useCarousel } from \"motion-plus/react\"\nimport { motion } from \"motion/react\"\n\nfunction Navigation() {\n    const { nextPage, prevPage, isNextActive, isPrevActive } = useCarousel()\n\n    return (\n        <nav className=\"navigation\">\n            <motion.button\n                className=\"nav-arrow\"\n                aria-label=\"Previous page\"\n                onClick={prevPage}\n                animate={{ opacity: isPrevActive ? 1 : 0.3 }}\n                transition={{ duration: 0.3 }}\n            >\n                <ChevronLeftIcon />\n            </motion.button>\n\n            <motion.button\n                className=\"nav-arrow\"\n                aria-label=\"Next page\"\n                onClick={nextPage}\n                animate={{ opacity: isNextActive ? 1 : 0.3 }}\n                transition={{ duration: 0.3 }}\n            >\n                <ChevronRightIcon />\n            </motion.button>\n        </nav>\n    )\n}\n\nexport default function CarouselPaginationArrowsExample() {\n    const images = [\n        { src: \"1\", aspectRatio: 4 / 3 },\n        { src: \"2\", aspectRatio: 4 / 3 },\n        { src: \"3\", aspectRatio: 3 / 4 },\n        { src: \"4\", aspectRatio: 4 / 3 },\n        { src: \"5\", aspectRatio: 4 / 3 },\n        { src: \"6\", aspectRatio: 2 / 3 },\n    ]\n\n    return (\n        <article>\n            <div className=\"carousel-container\">\n                <Carousel\n                    className=\"carousel\"\n                    items={images.map((image, index) => (\n                        <img\n                            key={index}\n                            draggable={false}\n                            className=\"photo\"\n                            src={`/photos/amsterdam-cyclists/${image.src}.jpg`}\n                            alt={`Photo ${index + 1}`}\n                            style={{ aspectRatio: image.aspectRatio }}\n                        />\n                    ))}\n                    overflow\n                    gap={20}\n                    snap=\"page\"\n                    loop={false}\n                >\n                    <Navigation />\n                </Carousel>\n            </div>\n            <Stylesheet />\n        </article>\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\nfunction Stylesheet() {\n    return (\n        <style>\n            {`\n        body {\n            overflow: hidden;\n        }\n\n        #sandbox {\n          align-items: center;\n        }\n\n        article {\n          width: 100%;\n          max-width: 600px;\n          display: flex;\n          flex-direction: column;\n          gap: 20px;\n          padding: 20px;\n        }\n\n        .carousel-container {\n          position: relative;\n          padding: 20px;\n          box-sizing: border-box;\n        }\n\n        .carousel {\n          position: relative;\n        }\n\n        .photo {\n          height: 300px;\n          max-width: 100%;\n          border-radius: 12px;\n          object-fit: cover;\n        }\n\n        @media (max-width: 600px) {\n          .photo {\n            height: 150px;\n          }\n        }\n\n        .navigation {\n          position: absolute;\n          left: 0;\n          right: 0;\n          display: flex;\n          align-items: center;\n          gap: 16px;\n          padding: 12px 16px;\n          border-radius: 24px;\n          z-index: 30;\n        }\n\n        .nav-arrow {\n          background: none;\n          border: none;\n          color: white;\n          cursor: pointer;\n          display: flex;\n          align-items: center;\n          justify-content: center;\n          padding: 4px;\n          transition: opacity 0.2s ease;\n          border-radius: 50%;\n        }\n\n        .nav-arrow:focus-visible {\n          outline: 2px solid white;\n        }\n\n        .nav-arrow:disabled {\n          opacity: 0.3;\n          cursor: not-allowed;\n        }\n\n        .nav-arrow:not(:disabled):hover {\n          opacity: 0.8;\n        }\n\n        .dots {\n          display: flex;\n          gap: 8px;\n        }\n\n        .dot {\n          width: 8px;\n          height: 8px;\n          border-radius: 50%;\n          background: rgba(255, 255, 255, 0.4);\n          border: none;\n          cursor: pointer;\n          padding: 0;\n          transition: background-color 0.2s ease;\n          will-change: transform;\n        }\n\n        .dot.active {\n          background: white;\n        }\n\n        .dot:hover {\n          background: rgba(255, 255, 255, 0.6);\n        }\n      `}\n        </style>\n    )\n}\n\nfunction ChevronLeftIcon() {\n    return (\n        <svg\n            width=\"20\"\n            height=\"20\"\n            viewBox=\"0 0 24 24\"\n            fill=\"none\"\n            stroke=\"currentColor\"\n            strokeWidth=\"2\"\n            strokeLinecap=\"round\"\n            strokeLinejoin=\"round\"\n        >\n            <path d=\"M15 18l-6-6 6-6\" />\n        </svg>\n    )\n}\n\nfunction ChevronRightIcon() {\n    return (\n        <svg\n            width=\"20\"\n            height=\"20\"\n            viewBox=\"0 0 24 24\"\n            fill=\"none\"\n            stroke=\"currentColor\"\n            strokeWidth=\"2\"\n            strokeLinecap=\"round\"\n            strokeLinejoin=\"round\"\n        >\n            <path d=\"M9 18l6-6-6-6\" />\n        </svg>\n    )\n}\n"
