            "code": "\"use client\"\n\nimport { Carousel, useCarousel } from \"motion-plus/react\"\nimport { animate, motion, useMotionValue } from \"motion/react\"\nimport { useEffect } from \"react\"\n\nfunction AutoplayProgress({ duration }: { duration: number }) {\n    const { currentPage, nextPage } = useCarousel()\n    const progress = useMotionValue(0)\n\n    useEffect(() => {\n        const animation = animate(progress, [0, 1], {\n            duration,\n            ease: \"linear\",\n            onComplete: nextPage,\n        })\n\n        return () => animation.stop()\n    }, [duration, nextPage, progress, currentPage])\n\n    return (\n        <div className=\"autoplay-progress\">\n            <motion.div\n                className=\"progress-bar\"\n                style={{ scaleX: progress, willChange: \"transform\" }}\n            />\n        </div>\n    )\n}\n\nexport default function CarouselAutoplayExample() {\n    return (\n        <article>\n            <Carousel\n                axis=\"x\"\n                className=\"carousel\"\n                items={images.map((image, index) => (\n                    <motion.img\n                        key={index}\n                        draggable={false}\n                        className=\"photo\"\n                        src={`/photos/amsterdam-cyclists/${image.src}.jpg`}\n                        alt={`Photo ${index + 1}`}\n                        style={{ aspectRatio: image.aspectRatio }}\n                    />\n                ))}\n                gap={20}\n                snap=\"page\"\n                loop={true}\n                fade={80}\n            >\n                <AutoplayProgress duration={3} />\n            </Carousel>\n            <Stylesheet />\n        </article>\n    )\n}\n\nconst images = [\n    { src: \"1\", aspectRatio: 4 / 3 },\n    { src: \"2\", aspectRatio: 4 / 3 },\n    { src: \"3\", aspectRatio: 3 / 4 },\n    { src: \"4\", aspectRatio: 4 / 3 },\n    { src: \"5\", aspectRatio: 4 / 3 },\n]\n\n/**\n * ==============   Styles   ================\n */\nfunction Stylesheet() {\n    return (\n        <style>\n            {`\n        #sandbox {\n          align-items: center;\n        }\n\n        article {\n          width: 100%;\n          max-width: 600px;\n          display: flex;\n          flex-direction: column;\n          gap: 20px;\n\n        .carousel {\n          position: relative;\n          display: flex;\n          flex-direction: column;\n          align-items: center;\n          justify-content: center;\n          padding: 20px 50px;\n        }\n\n        .photo {\n          height: 300px;\n          border-radius: 12px;\n          object-fit: cover;\n        }\n\n        @media (max-width: 600px) {\n          .photo {\n            height: 150px;\n          }\n        }\n\n        .autoplay-progress {\n          width: 120px;\n          height: 6px;\n          background: rgba(255, 255, 255, 0.3);\n          border-radius: 3px;\n          overflow: hidden;\n          backdrop-filter: blur(10px);\n          z-index: 10;\n          margin-left: 50px;\n        }\n\n        .progress-bar {\n          width: 100%;\n          height: 100%;\n          background: white;\n          transform-origin: left;\n          border-radius: 3px;\n        }\n      `}\n        </style>\n    )\n}\n"
