            "category": "Carousel",
            "code": "\"use client\"\n\nimport { Carousel, useTickerItem } from \"motion-plus/react\"\nimport { motion, useTransform } from \"motion/react\"\n\nfunction CoverflowItem({ src, index }: { src: string; index: number }) {\n    const { offset, props } = useTickerItem()\n\n    const { rotateY, scale } = useTransform(offset, [-200, 0, 200], {\n        rotateY: [20, 0, -20],\n        scale: [0.7, 1, 0.7],\n    })\n    const x = useTransform(\n        offset,\n        [-800, -200, 200, 800],\n        [\"100%\", \"0%\", \"0%\", \"-100%\"]\n    )\n    const zIndex = useTransform(offset, (value) =>\n        Math.max(0, Math.round(1000 - Math.abs(value)))\n    )\n\n    return (\n        <motion.li {...props} style={{ ...props.style, zIndex }}>\n            <motion.img\n                draggable={false}\n                src={`/photos/prague/${src}.jpg`}\n                alt={`Photo ${index + 1}`}\n                className=\"coverflow-item\"\n                style={{ transformPerspective: 500, x, rotateY, scale }}\n            />\n        </motion.li>\n    )\n}\n\nexport default function CarouselCoverflowExample() {\n    const images = [\n        \"image-01\",\n        \"image-02\",\n        \"image-03\",\n        \"image-04\",\n        \"image-05\",\n        \"image-06\",\n        \"image-07\",\n        \"image-08\",\n        \"image-09\",\n    ]\n\n    return (\n        <div className=\"mask\">\n            <Carousel\n                className=\"coverflow-carousel\"\n                items={images.map((src, index) => (\n                    <CoverflowItem key={src} src={src} index={index} />\n                ))}\n                overflow\n                gap={0}\n                itemSize=\"manual\"\n                safeMargin={200}\n            />\n            <Stylesheet />\n        </div>\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\nfunction Stylesheet() {\n    return (\n        <style>\n            {`\n        body {\n            overflow-x: hidden;\n        }\n\n        #sandbox {\n            align-items: stretch;\n            justify-content: stretch;\n        }\n              \n        .coverflow-carousel {\n          width: 350px;\n          height: 350px;\n          display: flex;\n          align-items: center;\n          justify-content: center;\n        }\n\n        .coverflow-item {\n          width: 350px;\n          height: 350px;\n          object-fit: cover;\n          border-radius: 12px;\n          box-shadow: 0 10px 40px rgba(0, 0, 0, 0.3);\n          will-change: transform, opacity;\n        }\n\n        @media (max-width: 600px) {\n          .coverflow-carousel {\n            width: 250px;\n            height: 250px;\n          }\n          .coverflow-item {\n            width: 250px;\n            height: 250px;\n          }\n        }\n\n        .mask {\n          mask-image: linear-gradient(to right, transparent 10%, black 25%, black 75%, transparent 90%);\n          webkit-mask-image: linear-gradient(to right, transparent 10%, black 25%, black 75%, transparent 90%);\n          display: flex;\n          align-items: center;\n          justify-content: center;\n          flex: 1;\n        }\n      `}\n        </style>\n    )\n}\n"
