            "code": "\"use client\"\n\nimport { Carousel, useTickerItem } from \"motion-plus/react\"\nimport { motion, useTransform } from \"motion/react\"\n\nfunction Image({\n    index,\n    src,\n    alt,\n    aspectRatio,\n}: {\n    index: number\n    src: string\n    alt: string\n    aspectRatio: number\n}) {\n    const { offset } = useTickerItem()\n    const rotate = useTransform(offset, [0, 20000], [0, 360], { clamp: false })\n    const x = useTransform(offset, [-1000, 0, 1000], [\"40%\", \"0%\", \"-40%\"])\n    const y = useTransform(offset, [0, 1000], [0, 400], { clamp: false })\n    const scale = useTransform(offset, [-1000, 0, 1000], [0.5, 1, 0.5])\n    const filter = useTransform(\n        offset,\n        [-1000, 0, 1000],\n        [\"blur(5px)\", \"blur(0px)\", \"blur(5px)\"]\n    )\n    const opacity = useTransform(offset, [-1000, 0, 1000], [0, 1, 0])\n\n    return (\n        <motion.img\n            key={index}\n            draggable={false}\n            className=\"photo\"\n            src={src}\n            alt={alt}\n            style={{\n                aspectRatio,\n                rotate,\n                scale,\n                x,\n                y,\n                filter,\n                opacity,\n            }}\n        />\n    )\n}\n\nexport default function CarouselItemOffsetExample() {\n    return (\n        <>\n            <Carousel\n                className=\"carousel\"\n                items={images.map((image, index) => (\n                    <Image\n                        index={index}\n                        key={index}\n                        src={`/photos/tokyo-shinjuku-2/${image.src}.jpg`}\n                        alt={`Photo ${index + 1}`}\n                        aspectRatio={image.aspectRatio}\n                    />\n                ))}\n                itemSize=\"fill\"\n                overflow\n                gap={20}\n                safeMargin={200}\n            />\n            <Stylesheet />\n        </>\n    )\n}\n\n/**\n * ==============   Dummy data   ================\n */\nconst images = [\n    { src: \"image-3\", aspectRatio: 4 / 3 },\n    { src: \"image-1\", aspectRatio: 3 / 4 },\n    { src: \"image-2\", aspectRatio: 4 / 3 },\n    { src: \"image-4\", aspectRatio: 3 / 4 },\n    { src: \"image-5\", aspectRatio: 4 / 3 },\n    { src: \"image-6\", aspectRatio: 3 / 4 },\n    { src: \"image-7\", aspectRatio: 4 / 3 },\n    { src: \"image-8\", aspectRatio: 3 / 4 },\n]\n\n/**\n * ==============   Styles   ================\n */\nfunction Stylesheet() {\n    return (\n        <style>\n            {`\n        body {\n          overflow: hidden;\n        }\n\n        .carousel {\n          width: 100%;\n          max-width: 400px;\n        }\n\n        .carousel li {\n          display: flex;\n          align-items: center;\n          justify-content: center;\n        }\n\n        .photo {\n          height: 300px;\n          border-radius: 12px;\n          object-fit: cover;\n          justify-self: center;\n        }\n\n        @media (max-width: 600px) {\n          .carousel {\n            max-width: 250px;\n          }\n\n          .photo {\n            height: 150px;\n          }\n        }\n      `}\n        </style>\n    )\n}\n"
