            "code": "\"use client\"\n\nimport { Carousel, useCarousel } from \"motion-plus/react\"\nimport { motion } from \"motion/react\"\n\nfunction Navigation() {\n    const { currentPage, totalPages, nextPage, prevPage } = useCarousel()\n\n    return (\n        <div className=\"navigation\">\n            <motion.button\n                className={`nav-button ${currentPage === 0 ? \"disabled\" : \"\"}`}\n                onClick={prevPage}\n                whileHover={{ scale: currentPage === 0 ? 1 : 1.1 }}\n                whileTap={{ scale: currentPage === 0 ? 1 : 0.9 }}\n            >\n                <ChevronUpIcon />\n            </motion.button>\n\n            <div className=\"dots\">\n                {Array.from({ length: totalPages }).map((_, index) => (\n                    <div\n                        key={index}\n                        className={`dot ${\n                            index === currentPage ? \"active\" : \"\"\n                        }`}\n                    />\n                ))}\n            </div>\n\n            <motion.button\n                className={`nav-button ${\n                    currentPage === totalPages - 1 ? \"disabled\" : \"\"\n                }`}\n                onClick={nextPage}\n                whileHover={{ scale: currentPage === totalPages - 1 ? 1 : 1.1 }}\n                whileTap={{ scale: currentPage === totalPages - 1 ? 1 : 0.9 }}\n                style={{ willChange: \"transform\" }}\n            >\n                <ChevronDownIcon />\n            </motion.button>\n        </div>\n    )\n}\n\nexport default function CarouselVerticalExample() {\n    const images = [\n        { src: \"1\", aspectRatio: 4 / 3 },\n        { src: \"2\", aspectRatio: 4 / 3 },\n        { src: \"3\", aspectRatio: 4 / 3 },\n        { src: \"4\", aspectRatio: 4 / 3 },\n        { src: \"5\", aspectRatio: 4 / 3 },\n        { src: \"6\", aspectRatio: 4 / 3 },\n    ]\n\n    return (\n        <>\n            <Carousel\n                className=\"carousel\"\n                items={images.map((image, index) => (\n                    <img\n                        key={index}\n                        draggable={false}\n                        className=\"photo\"\n                        src={`/photos/japan-bw/${image.src}.jpg`}\n                        alt={`Photo ${index + 1}`}\n                        style={{ aspectRatio: image.aspectRatio }}\n                    />\n                ))}\n                gap={20}\n                snap=\"page\"\n                loop={false}\n                axis=\"y\"\n                overflow\n            >\n                <Navigation />\n            </Carousel>\n            <Stylesheet />\n        </>\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\nfunction Stylesheet() {\n    return (\n        <style>\n            {`\n        body {\n            overflow: hidden;\n        }\n\n        .carousel {\n          width: 400px;\n          height: 500px;\n          position: relative;\n        }\n\n        .photo {\n          width: 100%;\n          height: 450px;\n          border-radius: 12px;\n          object-fit: cover;\n        }\n\n        .navigation {\n          position: absolute;\n          right: -80px;\n          top: 50%;\n          transform: translateY(-50%);\n          display: flex;\n          flex-direction: column;\n          align-items: center;\n          gap: 12px;\n          padding: 12px 8px;\n          background: rgba(0, 0, 0, 0.6);\n          border-radius: 24px;\n          backdrop-filter: blur(10px);\n          z-index: 10;\n        }\n\n        .nav-button {\n          background: none;\n          border: none;\n          color: white;\n          cursor: pointer;\n          display: flex;\n          align-items: center;\n          justify-content: center;\n          padding: 4px;\n          transition: opacity 0.2s ease;\n        }\n\n        .nav-button.disabled {\n          opacity: 0.3;\n          cursor: not-allowed;\n        }\n\n        .nav-button:not(.disabled):hover {\n          opacity: 0.8;\n        }\n\n        .dots {\n          display: flex;\n          flex-direction: column;\n          gap: 8px;\n          align-items: center;\n        }\n\n        .dot {\n          width: 8px;\n          height: 8px;\n          border-radius: 50%;\n          background: rgba(255, 255, 255, 0.4);\n          transition: background-color 0.2s ease;\n        }\n\n        .dot.active {\n          background: white;\n        }\n\n        @media (max-width: 600px) {\n          .carousel {\n            width: 150px;\n            height: 150px;\n          }\n\n          .photo {\n            height: 150px;\n          }\n        }\n      `}\n        </style>\n    )\n}\n\nfunction ChevronUpIcon() {\n    return (\n        <svg\n            width=\"20\"\n            height=\"20\"\n            viewBox=\"0 0 24 24\"\n            fill=\"none\"\n            stroke=\"currentColor\"\n            strokeWidth=\"2\"\n            strokeLinecap=\"round\"\n            strokeLinejoin=\"round\"\n        >\n            <path d=\"M18 15l-6-6-6 6\" />\n        </svg>\n    )\n}\n\nfunction ChevronDownIcon() {\n    return (\n        <svg\n            width=\"20\"\n            height=\"20\"\n            viewBox=\"0 0 24 24\"\n            fill=\"none\"\n            stroke=\"currentColor\"\n            strokeWidth=\"2\"\n            strokeLinecap=\"round\"\n            strokeLinejoin=\"round\"\n        >\n            <path d=\"M6 9l6 6 6-6\" />\n        </svg>\n    )\n}\n"
