            "code": "\"use client\"\n\nimport { motion, transform, useAnimate } from \"motion/react\"\nimport { useEffect, useState } from \"react\"\n\nfunction CharactersRemaining() {\n    const [value, setValue] = useState(\"\")\n    const maxLength = 12\n    const charactersRemaining = maxLength - value.length\n    const [counterRef, animate] = useAnimate()\n\n    const mapRemainingToColor = transform([2, 6], [\"#ff008c\", \"#ccc\"])\n\n    useEffect(() => {\n        if (charactersRemaining > 6) return\n\n        const mapRemainingToSpringVelocity = transform([0, 5], [50, 0])\n\n        animate(\n            counterRef.current,\n            { scale: 1 },\n            {\n                type: \"spring\",\n                velocity: mapRemainingToSpringVelocity(charactersRemaining),\n                stiffness: 700,\n                damping: 80,\n            }\n        )\n    }, [animate, charactersRemaining])\n\n    return (\n        <div className=\"container\">\n            <input value={value} onChange={(e) => setValue(e.target.value)} />\n            <div>\n                <motion.span\n                    ref={counterRef}\n                    style={{\n                        color: mapRemainingToColor(charactersRemaining),\n                        willChange: \"transform\",\n                    }}\n                >\n                    {charactersRemaining}\n                </motion.span>\n            </div>\n            <Stylesheet />\n        </div>\n    )\n}\n\nfunction Stylesheet() {\n    return (\n        <style>\n            {`\n        .container, input {\n          position: relative;\n          font-size: 32px;\n          line-height: 1;\n        }\n\n        input {\n          background-color: #0b1011;\n          color: #f5f5f5;\n          border: 2px solid #1d2628;\n          border-radius: 10px;\n          padding: 20px;\n          padding-right: 70px;\n          width: 300px;\n        }\n\n        input:focus {\n          border-color: var(--hue-blue);\n        }\n\n        .container div {\n          color: #ccc;\n          background: linear-gradient(\n            to right,\n            rgba(255, 255, 255, 0) 0%,\n            #0b1011 20%\n          );\n          position: absolute;\n          top: 50%;\n          right: 2px;\n          transform: translateY(-50%);\n          padding: 10px;\n          padding-right: 20px;\n          padding-left: 50px;\n        }\n\n        .container div span {\n          display: block;\n        }\n      `}\n        </style>\n    )\n}\n\nexport default CharactersRemaining\n"
