            "code": "\"use client\"\n\nimport { motion, useMotionValue, useTransform } from \"motion/react\"\nimport { useEffect, useRef, useState } from \"react\"\n\nexport default function App() {\n    const ref = useRef<HTMLDivElement>(null)\n    const [{ width, height, top, left }, measure] = useElementDimensions(ref)\n    const gradientX = useMotionValue(0.5)\n    const gradientY = useMotionValue(0.5)\n    const background = useTransform(\n        () =>\n            `conic-gradient(from 0deg at calc(${\n                gradientX.get() * 100\n            }% - ${left}px) calc(${\n                gradientY.get() * 100\n            }% - ${top}px), #0cdcf7, #ff0088, #fff312, #0cdcf7)`\n    )\n\n    return (\n        <div\n            style={{\n                position: \"absolute\",\n                inset: 0,\n                display: \"flex\",\n                alignItems: \"center\",\n                justifyContent: \"center\",\n            }}\n            onPointerMove={(e) => {\n                gradientX.set(e.clientX / width)\n                gradientY.set(e.clientY / height)\n            }}\n        >\n            <motion.div\n                ref={ref}\n                style={{\n                    background,\n                    width: 400,\n                    height: 400,\n                    borderRadius: 50,\n                    cursor: \"none\",\n                }}\n                onPointerEnter={() => measure()}\n            />\n        </div>\n    )\n}\n\n/**\n * ================  Utils  =========================\n */\n\nfunction useElementDimensions(\n    ref: React.RefObject<HTMLDivElement | null>\n): [\n    { width: number; height: number; top: number; left: number },\n    VoidFunction\n] {\n    const [size, setSize] = useState({ width: 0, height: 0, top: 0, left: 0 })\n\n    function measure() {\n        if (!ref.current) return\n\n        setSize(ref.current.getBoundingClientRect())\n    }\n\n    // Note: This won't accurately reflect viewport size changes\n    useEffect(() => {\n        measure()\n    }, [])\n\n    return [size, measure]\n}\n"
