            "code": "\"use client\"\n\nimport { Cursor, usePointerPosition } from \"motion-plus/react\"\nimport {\n    motion,\n    transform,\n    useAnimate,\n    useSpring,\n    useTransform,\n} from \"motion/react\"\nimport { useEffect, useState } from \"react\"\n\nfunction usePointerReaction(\n    axis: \"x\" | \"y\",\n    measurement: \"innerWidth\" | \"innerHeight\"\n) {\n    const pointer = usePointerPosition()\n    const [size, setSize] = useState(0)\n    const [hasMoved, setHasMoved] = useState(false)\n\n    useEffect(() => {\n        setSize(window[measurement])\n\n        const onMove = () => {\n            setHasMoved(true)\n            removeOnMove()\n        }\n\n        window.addEventListener(\"mousemove\", onMove)\n\n        const removeOnMove = () => {\n            window.removeEventListener(\"mousemove\", onMove)\n        }\n\n        return removeOnMove\n    }, [])\n\n    const value = useTransform(() => {\n        const pointerPosition = pointer[axis].get()\n\n        if (!size || !hasMoved) return 0\n\n        return transform([0, size], [100, -100])(pointerPosition)\n    })\n\n    return useSpring(value, { damping: 80, stiffness: 200 })\n}\n\nexport default function CursorFloatingTarget() {\n    const [textRef, animate] = useAnimate()\n\n    const x = usePointerReaction(\"x\", \"innerWidth\")\n    const y = usePointerReaction(\"y\", \"innerHeight\")\n    const originX = useTransform(x, [20, -20], [0, 1])\n    const originY = useTransform(y, [20, -20], [0, 1])\n\n    /**\n     * Continuous rotation animation. Uses transform\n     * for hardware accelerated animation.\n     */\n    useEffect(() => {\n        animate(\n            textRef.current,\n            { transform: [\"rotate(0deg)\", \"rotate(360deg)\"] },\n            {\n                duration: 8,\n                ease: \"linear\",\n                repeat: Infinity,\n            }\n        )\n    }, [])\n\n    return (\n        <>\n            <div className=\"decorative-title\">Amsterdam</div>\n\n            <motion.button\n                className=\"target\"\n                initial={false}\n                animate=\"idle\"\n                whileTap=\"pressed\"\n                whileHover=\"hover\"\n                style={{ x, y }}\n            >\n                <motion.div\n                    className=\"target-circle\"\n                    variants={{ pressed: { scale: 0.9 } }}\n                >\n                    <motion.div\n                        className=\"fill-circle\"\n                        variants={{\n                            hover: { scale: 1.1 },\n                            idle: { scale: 0 },\n                        }}\n                        style={{ originX, originY }}\n                    />\n                </motion.div>\n                <motion.div\n                    ref={textRef}\n                    className=\"text\"\n                    variants={{\n                        hover: { letterSpacing: \"1px\" },\n                        idle: { letterSpacing: \"7px\" },\n                    }}\n                >\n                    {\"See more  See more  See more  See more  \"\n                        .split(\"\")\n                        .map((char, index, array) => (\n                            <span\n                                key={index}\n                                className=\"char\"\n                                style={{\n                                    transform: `rotate(${\n                                        (index * 360) / array.length\n                                    }deg) translateY(-90px)`,\n                                }}\n                            >\n                                {char}\n                            </span>\n                        ))}\n                </motion.div>\n            </motion.button>\n\n            <Cursor style={{ width: 5, height: 5 }} className=\"cursor\" />\n            <Cursor\n                follow\n                center={{ x: 0.5, y: 0.5 }}\n                spring={{ stiffness: 1000, damping: 50 }}\n                magnetic={{ snap: 0.9, padding: 0 }}\n                style={{ width: 40, height: 40, borderRadius: 200 }}\n                variants={{\n                    magnetic: { opacity: 0 },\n                }}\n                className=\"reticule\"\n            />\n\n            <Stylesheet />\n        </>\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\n\nfunction Stylesheet() {\n    return (\n        <style>\n            {`\n\n                body {\n                    overflow: hidden;\n                }\n\n                .decorative-title {\n                    position: absolute;\n                    top: 50%;\n                    left: 50%;\n                    transform: translate(-50%, -50%);\n                    font-size: 6rem;\n                    font-weight: 900;\n                    letter-spacing: -0.05em;\n                    color: white;\n                    z-index: 1;\n                    pointer-events: none;\n                    text-transform: uppercase;\n                }\n\n                @media (max-width: 768px) {\n                    .decorative-title {\n                        font-size: 3rem;\n                    }\n                }\n\n                .target {\n                    position: relative;\n                    width: 150px;\n                    height: 150px;\n                    display: flex;\n                    align-items: center;\n                    justify-content: center;\n                    border-radius: 50%;\n                    z-index: 10;\n                }\n\n                .target-circle {\n                    position: absolute;\n                    inset: 0;\n                    border: 2px solid white;\n                    border-radius: 50%;\n                    display: flex;\n                    align-items: center;\n                    justify-content: center;\n                    cursor: pointer;\n                    background: transparent;\n                    overflow: hidden;\n                }\n\n                .fill-circle {\n                    position: absolute;\n                    inset: 0;\n                    background: white;\n                    border-radius: 50%;\n                    transform: scale(0);\n                    transform-origin: center;\n                    overflow: hidden;\n                    will-change: transform;\n                    background-image: url('/photos/amsterdam-cyclists/1.jpg');\n                    background-size: cover;\n                    background-position: center;\n                }\n\n                .text {\n                    position: relative;\n                    z-index: 2;\n                    color: white;\n                    font-size: 14px;\n                    font-weight: 900;\n                    text-transform: uppercase;\n                    letter-spacing: 0;\n                    white-space: nowrap;\n                    user-select: none;\n                    width: 150px;\n                    height: 150px;\n                    display: flex;\n                    align-items: center;\n                    justify-content: center;\n                }\n\n                .char {\n                    position: absolute;\n                    transform-origin: center;\n                    display: inline-block;\n                }\n\n                .cursor {\n                    background-color: white;\n                }\n\n                .reticule {\n                    background-color: transparent;\n                    border: 1px solid white;\n                    border-radius: 50%;\n                }\n            `}\n        </style>\n    )\n}\n"
