            "code": "\"use client\"\n\nimport { Cursor, usePointerPosition } from \"motion-plus/react\"\nimport {\n    AnimatePresence,\n    clamp,\n    motion,\n    MotionValue,\n    Transition,\n    useSpring,\n    useTransform,\n    useVelocity,\n} from \"motion/react\"\nimport Image from \"next/image\"\nimport { useState } from \"react\"\n\nfunction usePointerToSkew(axisMotionValue: MotionValue<number>) {\n    const velocity = useVelocity(axisMotionValue)\n    const maxVelocity = useTransform(() => clamp(-1000, 1000, velocity.get()))\n    const smoothVelocity = useSpring(maxVelocity, {\n        damping: 10,\n        stiffness: 200,\n    })\n    return useTransform(smoothVelocity, [0, 100], [0, -1], {\n        clamp: false,\n    })\n}\n\nconst enterTransition: Transition = {\n    duration: 0.5,\n    ease: [0, 0.54, 0.37, 0.97],\n}\n\nconst exitTransition: Transition = {\n    duration: 0.2,\n    ease: \"easeIn\",\n}\n\nfunction Item({\n    label,\n    number,\n    image,\n}: {\n    label: string\n    number: number\n    image: string\n}) {\n    const [isHovered, setIsHovered] = useState(false)\n    const position = usePointerPosition()\n    const skewX = usePointerToSkew(position.x)\n    const skewY = usePointerToSkew(position.y)\n\n    const content = (\n        <>\n            <span style={numberLabel}>0{number}</span>\n            {label}\n        </>\n    )\n\n    return (\n        <motion.li\n            style={{\n                ...item,\n                justifyContent: isHovered ? \"flex-end\" : \"flex-start\",\n            }}\n            onHoverStart={() => setIsHovered(true)}\n            onHoverEnd={() => setIsHovered(false)}\n        >\n            <motion.span layout style={text}>\n                {content}\n            </motion.span>\n            <motion.span layout style={text} aria-hidden>\n                {content}\n            </motion.span>\n            <AnimatePresence>\n                {isHovered && (\n                    <Cursor\n                        follow\n                        offset={{ x: 30, y: 30 }}\n                        variants={{\n                            default: {\n                                clipPath: \"inset(0% 0% 0% 0%)\",\n                                transition: enterTransition,\n                            },\n                            exit: {\n                                clipPath: \"inset(50% 50% 50% 50%)\",\n                                transition: exitTransition,\n                            },\n                        }}\n                        style={{ skewX, skewY, originX: 0, originY: 0 }}\n                        key=\"cursor\"\n                    >\n                        <motion.div\n                            variants={{\n                                default: {\n                                    scale: 1,\n                                    transition: enterTransition,\n                                },\n                                exit: {\n                                    scale: 1.5,\n                                    transition: exitTransition,\n                                },\n                            }}\n                            style={{ width: 300, height: 400 }}\n                        >\n                            <Image\n                                src={image}\n                                width={300}\n                                height={400}\n                                alt={\"Photo of \" + label}\n                            />\n                        </motion.div>\n                    </Cursor>\n                )}\n            </AnimatePresence>\n        </motion.li>\n    )\n}\n\nexport default function TextReveal() {\n    return (\n        <ul style={list}>\n            <Item label=\"Tokyo\" number={1} image=\"/photos/japan-bw/2.jpg\" />\n            <Item\n                label=\"Amsterdam\"\n                number={2}\n                image=\"/photos/amsterdam-cityscape/1.jpg\"\n            />\n            <Item label=\"London\" number={3} image=\"/photos/cityscape/2.jpg\" />\n        </ul>\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\n\nconst list = {\n    display: \"flex\",\n    gap: 20,\n    listStyle: \"none\",\n    padding: 0,\n    margin: 0,\n    flexDirection: \"column\",\n} as const\n\nconst item = {\n    position: \"relative\",\n    cursor: \"pointer\",\n    overflow: \"hidden\",\n    display: \"flex\",\n    flexDirection: \"column\",\n    alignItems: \"flex-start\",\n    gap: 10,\n    height: 48,\n} as const\n\nconst text = {\n    color: \"#f5f5f5\",\n    fontSize: 48,\n    fontWeight: \"bold\",\n    textTransform: \"uppercase\",\n    display: \"flex\",\n    alignItems: \"center\",\n    gap: 10,\n    lineHeight: \"48px\",\n} as const\n\nconst numberLabel = {\n    fontSize: 48,\n    fontWeight: \"bold\",\n    textTransform: \"uppercase\",\n    opacity: 0.5,\n    fontVariantNumeric: \"tabular-nums\",\n} as const\n"
