            "category": "Interactions",
            "code": "\"use client\"\n\nimport {\n    animate,\n    clamp,\n    motion,\n    MotionValue,\n    useMotionValue,\n    useTransform,\n} from \"motion/react\"\nimport { useLayoutEffect, useRef, useState } from \"react\"\n\nexport default function ImageRevealSlider({\n    src = \"/photos/japan-day/7.jpg\",\n    step = 50,\n}: Props) {\n    const containerRef = useRef<HTMLDivElement>(null)\n    const x = useMotionValue(0)\n    const [boundary, setBoundary] = useState(300)\n    const clipPath = useTransform(\n        x,\n        [-boundary, boundary],\n        [\"inset(0% 0% 0% 0%)\", \"inset(0% 0% 0% 100%)\"]\n    )\n\n    const lineBackgroundColor = useSliderColor(x, boundary)\n\n    useLayoutEffect(() => {\n        if (containerRef.current) {\n            setBoundary(containerRef.current.clientWidth / 2)\n        }\n    }, [containerRef])\n\n    const keyboard = useKeyboard(x, boundary, step)\n\n    return (\n        <div ref={containerRef} className=\"container\">\n            <div className=\"image-container\">\n                <motion.img src={src} alt=\"Kirby photo\" className=\"image\" />\n                <motion.img\n                    style={{ clipPath }}\n                    src={src}\n                    alt=\"Kirby photo in black and white\"\n                    className=\"image overlay\"\n                />\n            </div>\n\n            <motion.div\n                className=\"slider\"\n                drag=\"x\"\n                dragElastic={0.05}\n                dragConstraints={containerRef}\n                style={{ x, backgroundColor: lineBackgroundColor }}\n                tabIndex={0}\n                onFocus={keyboard.start}\n                onBlur={keyboard.stop}\n            >\n                <div className=\"handle\">\n                    <LeftRightIcon />\n                </div>\n            </motion.div>\n\n            <Stylesheet />\n        </div>\n    )\n}\n\ninterface Props {\n    src?: string\n    step?: number\n}\n\n/**\n * ==============   Utils   ================\n */\n\nfunction useSliderColor(x: MotionValue<number>, boundary: number) {\n    return useTransform(\n        x,\n        [-boundary + 20, -boundary + 60, boundary - 60, boundary - 20],\n        [\n            \"rgba(255, 255, 255, 0)\",\n            \"rgba(255, 255, 255, 1)\",\n            \"rgba(255, 255, 255, 1)\",\n            \"rgba(255, 255, 255, 0)\",\n        ]\n    )\n}\n\nfunction useKeyboard(x: MotionValue<number>, boundary: number, step: number) {\n    const handleKeyDown = (event: KeyboardEvent) => {\n        let moveBy = 0\n\n        if (event.key === \"ArrowLeft\") {\n            moveBy = -step\n        } else if (event.key === \"ArrowRight\") {\n            moveBy = step\n        } else {\n            return\n        }\n\n        animate(x, clamp(-boundary, boundary, x.get() + moveBy), {\n            type: \"spring\",\n            stiffness: 900,\n            damping: 40,\n            velocity: moveBy * 10,\n        })\n    }\n\n    const start = () => {\n        document.addEventListener(\"keydown\", handleKeyDown)\n    }\n\n    const stop = () => {\n        document.removeEventListener(\"keydown\", handleKeyDown)\n    }\n\n    return { start, stop }\n}\n\n/**\n * ==============   Styles   ================\n */\n\nfunction Stylesheet() {\n    return (\n        <style>\n            {`\n                body {\n                    overflow: hidden;\n                }\n\n                .container {\n                    position: relative;\n                }\n\n                .image-container {\n                    position: relative;\n                    background-color: #0b1011;\n                    border: 1px solid #1d2628;\n                    border-radius: 12px;\n                    overflow: hidden;\n                    aspect-ratio: 4/3;\n                    height: 300px;\n                }\n\n                @media (max-width: 500px) {\n                    .image-container {\n                        height: 200px;\n                    }\n                }\n\n                .image {\n                    position: absolute;\n                    width: 100%;\n                    height: 100%;\n                    object-fit: cover;\n                }\n\n                .overlay {\n                    top: 0;\n                    left: 0;\n                    filter: grayscale(100%);\n                }\n\n                .slider {\n                    position: absolute;\n                    top: 0;\n                    bottom: 0;\n                    width: 2px;\n                    background-color: white;\n                    cursor: grab;\n                    left: 50%;\n                    transform: translateX(-50%);\n                    filter: drop-shadow(2px 0 3px rgba(0, 0, 0, .4))\n                }\n\n                .handle {\n                    position: absolute;\n                    top: 50%;\n                    left: 50%;\n                    transform: translate(-50%, -50%);\n                    width: 40px;\n                    height: 40px;\n                    background-color: white;\n                    border-radius: 50%;\n                    display: flex;\n                    align-items: center;\n                    justify-content: center;\n                    color: #0f1115;\n                    cursor: grab;\n                    touch-action: none;\n                }\n\n                .slider:focus-visible{\n                    outline: none; \n                }\n                    \n                .slider:focus-visible .handle {\n                    outline: 4px solid #ff0088; \n                    outline-offset: 2px;\n                }\n\n                .handle:active {\n                    cursor: grabbing;\n                }\n            `}\n        </style>\n    )\n}\n\nfunction LeftRightIcon() {\n    return (\n        <svg\n            xmlns=\"http://www.w3.org/2000/svg\"\n            width=\"24\"\n            height=\"24\"\n            viewBox=\"0 0 24 24\"\n            fill=\"none\"\n            stroke=\"currentColor\"\n            strokeWidth=\"2\"\n            strokeLinecap=\"round\"\n            strokeLinejoin=\"round\"\n        >\n            <path d=\"m18 8 4 4-4 4\" />\n            <path d=\"m6 8-4 4 4 4\" />\n            <path d=\"M8 12h.01\" />\n            <path d=\"M12 12h.01\" />\n            <path d=\"M16 12h.01\" />\n        </svg>\n    )\n}\n"
