            "code": "\"use client\"\n\nimport { Cursor, useCursorState, useMagneticPull } from \"motion-plus/react\"\nimport { motion } from \"motion/react\"\nimport { useRef } from \"react\"\n\nexport default function IOSPointer() {\n    const ref = useRef<HTMLButtonElement>(null)\n    const pull = useMagneticPull(ref, 0.1)\n\n    // Handle the color change when hovering over the Examples site popups\n    const { zone } = useCursorState()\n\n    return (\n        <div className=\"container\">\n            <motion.button ref={ref} className=\"button\" whileTap=\"pressed\">\n                <motion.span\n                    variants={{ pressed: { scale: 0.95 } }}\n                    style={pull}\n                >\n                    <Chevron />\n                    Appearance\n                </motion.span>\n            </motion.button>\n\n            <Cursor\n                magnetic\n                className=\"cursor\"\n                variants={{\n                    default: {\n                        backgroundColor:\n                            zone === \"overlay\" ? \"#eee\" : \"#7e7e7e\",\n                    },\n                    pointer: {\n                        backgroundColor: zone === \"overlay\" ? \"#fff\" : \"#ddd\",\n                    },\n                }}\n                style={{\n                    borderRadius: 10,\n                    mixBlendMode:\n                        zone === \"overlay\" ? \"difference\" : \"multiply\",\n                }}\n            />\n\n            <Stylesheet />\n        </div>\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\n\nfunction Stylesheet() {\n    return (\n        <style>\n            {`\n                .button {\n                    background: none;\n                    padding: 8px;\n                    color: #0A84FF;\n                    display: flex;\n                    align-items: center;\n                    justify-content: center;\n                    border: 1px dashed #fff4;\n                    border-radius: 0;\n                    user-select: none;\n                    font-family: -apple-system, BlinkMacSystemFont, \"Segoe UI\", Roboto, Helvetica, Arial, sans-serif;\n                    font-weight: 500;\n                    font-size: 24px;\n                }\n\n                .button > span {\n                    display: flex;\n                    gap: 8px;\n                    align-items: center;\n                }\n            `}\n        </style>\n    )\n}\n\nfunction Chevron() {\n    return (\n        <svg\n            width=\"12\"\n            height=\"20\"\n            viewBox=\"0 0 12 20\"\n            fill=\"none\"\n            xmlns=\"http://www.w3.org/2000/svg\"\n        >\n            <path\n                d=\"M10 2L2 10L10 18\"\n                stroke=\"currentColor\"\n                strokeWidth=\"2\"\n                strokeLinecap=\"round\"\n                strokeLinejoin=\"round\"\n            />\n        </svg>\n    )\n}\n"
