            "code": "\"use client\"\n\nimport { motion, useSpring, useTransform } from \"motion/react\"\nimport { useEffect } from \"react\"\n\nfunction LoadingFillText() {\n    const progress = useMockLoading()\n    const clipPath = useTransform(\n        progress,\n        [0, 1],\n        [\"inset(0 100% 0 0)\", \"inset(0 0% 0 0)\"]\n    )\n\n    return (\n        <div className=\"container\">\n            <div className=\"text-container\">\n                <div className=\"text text-bg\" aria-hidden>\n                    Loading\n                </div>\n                <motion.div className=\"text text-fill\" style={{ clipPath }}>\n                    Loading\n                </motion.div>\n            </div>\n            <StyleSheet />\n        </div>\n    )\n}\n\n/**\n * ==============   Utils   ================\n */\nfunction useMockLoading() {\n    const progress = useSpring(0)\n\n    useEffect(() => {\n        const interval = setInterval(() => {\n            const newProgress = progress.get() + Math.random() * 0.2\n\n            if (newProgress >= 1) {\n                clearInterval(interval)\n            }\n\n            progress.set(newProgress)\n        }, 500)\n\n        return () => clearInterval(interval)\n    }, [progress])\n\n    return progress\n}\n\n/**\n * ==============   Styles   ================\n */\nfunction StyleSheet() {\n    return (\n        <style>\n            {`\n            @import url('https://fonts.googleapis.com/css2?family=Unbounded:wght@200..900&display=swap');\n\n            .container {\n                display: flex;\n                justify-content: center;\n                align-items: center;\n            }\n\n            .text-container {\n                position: relative;\n                font-size: 32px;\n                font-weight: bold;\n            }\n\n            .text {\n                position: relative;\n                color: #ff0088;\n                font-family: \"Unbounded\", sans-serif;\n                font-optical-sizing: auto;\n                font-weight: 900;\n                font-style: normal;\n                text-transform: uppercase;\n                font-size: 64px;\n                letter-spacing: -0.06em;\n            }\n\n            .text-bg {\n                position: absolute;\n                top: 0;\n                left: 0;\n                opacity: 0.2;\n            }\n\n            .text-fill {\n                position: relative;\n                clip-path: inset(0 100% 0 0);\n                will-change: clip-path;\n                z-index: 1;\n            }\n            `}\n        </style>\n    )\n}\n\nexport default LoadingFillText\n"
