            "code": "\"use client\"\n\nimport {\n    animate,\n    motion,\n    Transition,\n    useMotionTemplate,\n    useMotionValue,\n    useMotionValueEvent,\n    useSpring,\n    useTransform,\n} from \"motion/react\"\nimport { useEffect, useState } from \"react\"\n\nfunction LoadingLineReveal() {\n    const progress = useMockLoading()\n    const [isLoaded, setIsLoaded] = useState(false)\n\n    const leftEdge = useMotionValue(\"calc(50% - 2px)\")\n    const rightEdge = useMotionValue(\"calc(50% + 2px)\")\n    const { topEdge, bottomEdge } = useTransform(progress, [0, 1], {\n        topEdge: [\"50%\", \"0%\"],\n        bottomEdge: [\"50%\", \"100%\"],\n    })\n\n    /**\n     * Ideally this would be as simple as something like inset() but this would leave\n     * the bit in the middle visible, whereas we want to punch out the bit in the\n     * middle and leave the rest. So to fix this we create a polygon that cuts into\n     * the middle with a thin line and then opens that thin line once progress is 1\n     */\n    const clipPath = useMotionTemplate`polygon(\n      0% 0%, ${leftEdge} 0%, ${leftEdge} ${topEdge}, ${leftEdge} ${bottomEdge}, ${rightEdge} ${bottomEdge}, ${rightEdge} ${topEdge}, \n      ${leftEdge} ${topEdge}, ${leftEdge} 0%, 100% 0%, 100% 100%, 0% 100%, 0% 0%\n    )`\n\n    useMotionValueEvent(progress, \"change\", (latest) => {\n        if (latest >= 1 && !isLoaded) {\n            setIsLoaded(true)\n        }\n    })\n\n    useEffect(() => {\n        if (!isLoaded) return\n\n        const transition: Transition = {\n            type: \"spring\",\n            visualDuration: 0.5,\n            bounce: 0,\n        }\n\n        animate(leftEdge, \"calc(0% - 0px)\", transition)\n        animate(rightEdge, \"calc(100% + 0px)\", transition)\n    }, [isLoaded])\n\n    return (\n        <div className=\"container\">\n            <Gallery />\n            <motion.div\n                className=\"black-overlay\"\n                animate={{ opacity: isLoaded ? 0 : 1 }}\n            />\n            <motion.div className=\"pink-overlay\" style={{ clipPath }} />\n            <StyleSheet />\n        </div>\n    )\n}\n\n/**\n * ==============   Utils   ================\n */\nfunction useMockLoading() {\n    const progress = useSpring(0, { stiffness: 500, damping: 40 })\n\n    useEffect(() => {\n        const interval = setInterval(() => {\n            const newProgress = progress.get() + Math.random() * 0.3\n\n            if (newProgress >= 1) {\n                progress.set(1)\n                clearInterval(interval)\n            } else {\n                progress.set(newProgress)\n            }\n        }, 300)\n\n        return () => clearInterval(interval)\n    }, [progress])\n\n    return progress\n}\n\nfunction Gallery() {\n    return (\n        <ul className=\"gallery-container\">\n            {images.map((image, i) => (\n                <li key={image.url} className=\"gallery-item\">\n                    <img\n                        src={image.url}\n                        alt={`Gallery item ${i + 1}`}\n                        style={{\n                            aspectRatio: image.aspectRatio,\n                            height:\n                                image.aspectRatio === \"4 / 3\" ? \"100%\" : \"auto\",\n                            width:\n                                image.aspectRatio === \"3 / 4\" ? \"100%\" : \"auto\",\n                        }}\n                    />\n                </li>\n            ))}\n        </ul>\n    )\n}\n\nconst images = [\n    {\n        url: \"/photos/japan-day/1.jpg\",\n        aspectRatio: \"4 / 3\",\n    },\n    {\n        url: \"/photos/japan-day/2.jpg\",\n        aspectRatio: \"3 / 4\",\n    },\n    {\n        url: \"/photos/japan-day/3.jpg\",\n        aspectRatio: \"3 / 4\",\n    },\n    {\n        url: \"/photos/japan-day/4.jpg\",\n        aspectRatio: \"3 / 4\",\n    },\n    {\n        url: \"/photos/japan-day/5.jpg\",\n        aspectRatio: \"4 / 3\",\n    },\n    {\n        url: \"/photos/japan-day/6.jpg\",\n        aspectRatio: \"3 / 4\",\n    },\n]\n\n/**\n * ==============   Styles   ================\n */\nfunction StyleSheet() {\n    return (\n        <style>\n            {`\n            .container {\n                position: relative;\n                width: 500px;\n                height: 400px;\n                overflow: hidden;\n            }\n\n            .gallery-container {\n                background-color: #0b1011;\n                border: 1px solid #1d2628;\n                border-radius: 25px;\n                width: 100%;\n                height: 100%;\n                margin: 0;\n                padding: 20px;\n                display: grid;\n                grid-template-columns: repeat(3, 1fr);\n                grid-template-rows: repeat(2, 1fr);\n                gap: 20px;\n                list-style: none;\n                box-sizing: border-box;\n            }\n\n            .gallery-item {\n                width: 100%;\n                height: 100%;\n                overflow: hidden;\n                position: relative;\n                background-color: #0f1115;\n                display: flex;\n                align-items: center;\n                justify-content: center;\n                border-radius: 10px;\n            }\n\n            .gallery-item img {\n                object-fit: cover;\n            }\n\n            .black-overlay {\n                position: fixed;\n                top: 0;\n                left: 0;\n                width: 100%;\n                height: 100%;\n                background-color: #0f1115;\n                z-index: 10000000000;\n                pointer-events: none;\n            }\n\n            .pink-overlay {\n                position: fixed;\n                top: 0;\n                left: 0;\n                width: 100%;\n                height: 100%;\n                background-color: #ff0088;\n                z-index: 10000000001;\n                pointer-events: none;\n                will-change: clip-path;\n            }\n            `}\n        </style>\n    )\n}\n\nexport default LoadingLineReveal\n"
