            "category": "Cursor",
            "code": "\"use client\"\n\nimport { usePointerPosition } from \"motion-plus/react\"\nimport { motion, useTransform } from \"motion/react\"\nimport { useEffect, useRef, useState } from \"react\"\n\nfunction Filing() {\n    const pointer = usePointerPosition()\n    const filingRef = useRef<HTMLDivElement>(null)\n    const [centerX, setCenterX] = useState<number | null>(null)\n    const [centerY, setCenterY] = useState<number | null>(null)\n\n    // Measure the filing's position on mount\n    useEffect(() => {\n        if (filingRef.current) {\n            const rect = filingRef.current.getBoundingClientRect()\n            setCenterX(rect.left + rect.width / 2)\n            setCenterY(rect.top + rect.height / 2)\n        }\n    }, [])\n\n    // Calculate angle from filing position to cursor position\n    const rotate = useTransform(() => {\n        if (centerX === null || centerY === null) return 0\n\n        const pointerX = pointer.x.get()\n        const pointerY = pointer.y.get()\n\n        const deltaX = pointerX - centerX\n        const deltaY = pointerY - centerY\n\n        return Math.atan2(deltaY, deltaX) * (180 / Math.PI)\n    })\n\n    return <motion.div ref={filingRef} className=\"filing\" style={{ rotate }} />\n}\n\nexport default function MagneticGrid({ size = 16 }: { size?: number }) {\n    return (\n        <div className=\"container\">\n            <div\n                className=\"square\"\n                style={\n                    {\n                        \"--grid-size\": size,\n                        \"--grid-cols\": size,\n                        \"--grid-rows\": size,\n                    } as React.CSSProperties\n                }\n            >\n                {Array.from({ length: size * size }).map((_, index) => (\n                    <Filing key={index} />\n                ))}\n            </div>\n\n            <StyleSheet />\n        </div>\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\nfunction StyleSheet() {\n    return (\n        <style>\n            {`\n        body {\n            overflow: hidden;\n        }\n\n        .container {\n          display: flex;\n          align-items: center;\n          justify-content: center;\n          min-height: 100vh;\n          box-sizing: border-box;\n          padding: 2rem;\n        }\n\n        .square {\n          position: relative;\n          width: 500px;\n          height: 500px;\n          background-color: #0b1011;\n          border-radius: 8px;\n          padding: 20px;\n          display: grid;\n          grid-template-columns: repeat(var(--grid-cols), 1fr);\n          grid-template-rows: repeat(var(--grid-rows), 1fr);\n          gap: 1px;\n        }\n\n        @media (max-width: 499px) {\n          .square {\n            width: 300px;\n            height: 300px;\n            padding: 15px;\n          }\n        }\n\n        .filing {\n          width: 70%;\n          height: 2px;\n          background: #f5f5f5;\n          border-radius: 1px;\n          transform-origin: center 50%;\n          justify-self: center;\n          align-self: center;\n          will-change: transform;\n        }\n\n        @media (max-width: 499px) {\n          .filing {\n            border-radius: 0.5px;\n          }\n        }\n      `}\n        </style>\n    )\n}\n"
