            "code": "\"use client\"\n\nimport { AnimatePresence, motion } from \"motion/react\"\nimport { useCallback, useRef, useState } from \"react\"\n\ntype Ripple = {\n    id: number\n    x: number\n    y: number\n    size: number\n}\n\nexport default function MaterialDesignRipple() {\n    const [ripples, setRipples] = useState<Ripple[]>([])\n    const idRef = useRef(0)\n    const buttonRef = useRef<HTMLButtonElement | null>(null)\n\n    const createRipple = useCallback((originX: number, originY: number) => {\n        const button = buttonRef.current\n        if (!button) return\n\n        const rect = button.getBoundingClientRect()\n        const localX = originX - rect.left\n        const localY = originY - rect.top\n        const dx = Math.max(localX, rect.width - localX)\n        const dy = Math.max(localY, rect.height - localY)\n        const radius = Math.sqrt(dx * dx + dy * dy)\n        const size = radius * 2\n\n        const id = ++idRef.current\n        setRipples((prev) => [...prev, { id, x: localX, y: localY, size }])\n\n        return id\n    }, [])\n\n    const removeLastRipple = useCallback(() => {\n        setRipples((prev) =>\n            prev.length ? prev.slice(0, prev.length - 1) : prev\n        )\n    }, [])\n\n    const onPointerDown = useCallback(\n        (event: React.PointerEvent<HTMLButtonElement>) => {\n            if ((event as any).isPrimary) {\n                createRipple(event.clientX, event.clientY)\n            }\n        },\n        [createRipple]\n    )\n\n    const onKeyDown = useCallback(\n        (event: React.KeyboardEvent<HTMLButtonElement>) => {\n            if (event.repeat) return\n            if (event.key === \" \" || event.key === \"Enter\") {\n                const button = buttonRef.current\n                if (!button) return\n                const rect = button.getBoundingClientRect()\n                createRipple(\n                    rect.left + rect.width / 2,\n                    rect.top + rect.height / 2\n                )\n            }\n        },\n        [createRipple]\n    )\n\n    const onKeyUp = useCallback(\n        (event: React.KeyboardEvent<HTMLButtonElement>) => {\n            if (event.key === \" \" || event.key === \"Enter\") {\n                removeLastRipple()\n            }\n        },\n        [removeLastRipple]\n    )\n\n    return (\n        <>\n            <motion.button\n                ref={buttonRef}\n                className=\"md-button\"\n                onPointerDown={onPointerDown}\n                onPointerUp={removeLastRipple}\n                onPointerCancel={removeLastRipple}\n                onPointerLeave={removeLastRipple}\n                onBlur={removeLastRipple}\n                onKeyDown={onKeyDown}\n                onKeyUp={onKeyUp}\n                whileHover={{\n                    borderColor: \"#8df0cc\",\n                    backgroundColor: \"#8df0cc30\",\n                }}\n                initial={{\n                    borderColor: \"#8df0ccaa\",\n                    backgroundColor: \"#8df0cc08\",\n                }}\n                transition={{\n                    duration: 0.2,\n                    ease: \"linear\",\n                }}\n            >\n                Click me\n                <span className=\"ripple-container\" aria-hidden>\n                    <AnimatePresence>\n                        {ripples.map((ripple) => (\n                            <motion.span\n                                key={ripple.id}\n                                className=\"ripple\"\n                                style={{\n                                    width: ripple.size,\n                                    height: ripple.size,\n                                    left: ripple.x - ripple.size / 2,\n                                    top: ripple.y - ripple.size / 2,\n                                }}\n                                initial={{ opacity: 0, transform: \"scale(0)\" }}\n                                animate={{\n                                    opacity: 0.4,\n                                    transform: \"scale(1)\",\n                                    transition: { duration: 0.3 },\n                                }}\n                                exit={{ opacity: 0 }}\n                                transition={{\n                                    duration: 0.55,\n                                    ease: \"easeOut\",\n                                }}\n                            />\n                        ))}\n                    </AnimatePresence>\n                </span>\n            </motion.button>\n\n            <Stylesheet />\n        </>\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\n\nfunction Stylesheet() {\n    return (\n        <style>{`\n      .md-button {\n        position: relative;\n        display: inline-flex;\n        align-items: center;\n        justify-content: center;\n        padding: 10px 20px;\n        border-radius: 4px;\n        text-transform: uppercase;\n        background-color: transparent;\n        color: var(--accent);\n        border: 1px solid var(--accent);\n        user-select: none;\n        cursor: pointer;\n        overflow: hidden;\n        letter-spacing: 0.2px;\n        -webkit-tap-highlight-color: transparent;\n      }\n\n      .ripple-container {\n        position: absolute;\n        inset: 0;\n        overflow: hidden;\n        border-radius: inherit;\n        pointer-events: none;\n      }\n\n      .ripple {\n        position: absolute;\n        border-radius: 50%;\n        background-color: currentColor;\n        will-change: opacity, transform;\n      }\n    `}</style>\n    )\n}\n"
