            "category": "Dialog",
            "code": "\"use client\"\n\nimport { CloseIcon } from \"@/ui/icons/CloseIcon\"\nimport { AnimatePresence, motion, TargetAndTransition } from \"motion/react\"\nimport { useEffect, useRef, useState } from \"react\"\n\n/**\n * This example shows how to use the `motion.dialog`\n * component.\n */\n\nexport default function Modal() {\n    const [isOpen, setIsOpen] = useState(false)\n\n    return (\n        <>\n            <motion.button\n                whileHover={{ scale: 1.1 }}\n                whileTap={{ scale: 1.05 }}\n                className=\"openButton\"\n                onClick={() => setIsOpen(true)}\n                data-primary-action\n            >\n                Open modal\n            </motion.button>\n            <AnimatePresence>\n                {isOpen ? <Dialog close={() => setIsOpen(false)} /> : null}\n            </AnimatePresence>\n            <StyleSheet />\n        </>\n    )\n}\n\nfunction Dialog({ close }: { close: () => void }) {\n    const ref = useRef<HTMLDialogElement>(null)\n\n    /**\n     * Use the dialog element's imperative API to open and close the dialog\n     * when the component mounts and unmounts. This enables exit animations\n     * and maintains the dialog's natural accessibility behaviour.\n     */\n    useEffect(() => {\n        if (!ref.current) return\n\n        ref.current.showModal()\n\n        return () => ref.current?.close()\n    }, [ref])\n\n    useClickOutside(ref, close)\n\n    return (\n        <>\n            <motion.div\n                className=\"overlay\"\n                initial={{ opacity: 0 }}\n                animate={{ opacity: 1 }}\n                exit={{ opacity: 0 }}\n            ></motion.div>\n            <motion.dialog\n                initial={dialogInitialState}\n                animate={dialogOpenState}\n                exit={dialogInitialState}\n                ref={ref}\n                className=\"modal\"\n                open={false}\n                /**\n                 * The onCancel event is triggered when the user\n                 * presses the Esc key. We prevent the default and\n                 * close the dialog via the provided callback that\n                 * first sets React state to false.\n                 *\n                 * AnimatePresence will take care of our exit animation\n                 * before actually closing the dialog.\n                 */\n                onCancel={(event) => {\n                    event.preventDefault()\n                    close()\n                }}\n                /**\n                 * However, if the Esc key is pressed twice, the\n                 * close method will always fire, and it isn't cancellable.\n                 * So we listen for this and make sure the React\n                 * state is updated to false.\n                 */\n                onClose={close}\n                style={{ transformPerspective: 500 }}\n            >\n                <h2 className=\"title\">Confirm</h2>\n                <p>Are you sure you want to become a Motion expert?</p>\n                <div className=\"controls\">\n                    <button onClick={close} className=\"cancel\">\n                        Cancel\n                    </button>\n                    <button onClick={close} className=\"save\">\n                        Expert me\n                    </button>\n                </div>\n                <button\n                    className=\"closeButton\"\n                    aria-label=\"Close\"\n                    onClick={close}\n                >\n                    <CloseIcon />\n                </button>\n            </motion.dialog>\n        </>\n    )\n}\n\nconst dialogOpenState: TargetAndTransition = {\n    opacity: 1,\n    filter: \"blur(0px)\",\n    rotateX: 0,\n    rotateY: 0,\n    z: 0,\n    transition: {\n        delay: 0.2,\n        duration: 0.5,\n        ease: [0.17, 0.67, 0.51, 1],\n        opacity: {\n            delay: 0.2,\n            duration: 0.5,\n            ease: \"easeOut\",\n        },\n    },\n}\n\nconst dialogInitialState: TargetAndTransition = {\n    opacity: 0,\n    filter: \"blur(10px)\",\n    z: -100,\n    rotateY: 25,\n    rotateX: 5,\n    transformPerspective: 500,\n    transition: {\n        duration: 0.3,\n        ease: [0.67, 0.17, 0.62, 0.64],\n    },\n}\n\n/**\n * ==============   Utils   ================\n */\n\nfunction useClickOutside(\n    ref: React.RefObject<HTMLDialogElement | null>,\n    close: VoidFunction\n) {\n    useEffect(() => {\n        const handleClickOutside = (event: React.MouseEvent<Element>) => {\n            if (ref.current && checkClickOutside(event, ref.current)) {\n                close()\n            }\n        }\n\n        document.addEventListener(\"click\", handleClickOutside as any)\n\n        return () => {\n            document.removeEventListener(\"click\", handleClickOutside as any)\n        }\n    }, [ref])\n}\n\nfunction checkClickOutside(\n    event: React.MouseEvent<Element>,\n    element: HTMLDialogElement\n) {\n    const { top, left, width, height } = element.getBoundingClientRect()\n\n    if (\n        event.clientX < left ||\n        event.clientX > left + width ||\n        event.clientY < top ||\n        event.clientY > top + height\n    ) {\n        return true\n    }\n}\n\n/**\n * ==============   Types   ================\n */\ninterface Dialog {\n    isOpen: boolean\n    open: () => void\n    close: () => void\n    ref: React.RefObject<HTMLDialogElement | null>\n}\n\n/**\n * ==============   Styles   ================\n */\nfunction StyleSheet() {\n    return (\n        <style>{`\n        .openButton, .controls button {\n            background-color: #ff0088;\n            color: #f5f5f5;\n            font-size: 16px;\n            padding: 10px 20px;\n            border-radius: 10px;\n        }\n\n        .controls {\n            border-top: 1px solid var(--divider);\n            padding-top: 20px;\n            margin-top: 20px;\n            display: flex;\n            justify-content: flex-end;\n            gap: 10px;\n        }\n\n        .controls button.cancel {\n            background-color: var(--divider);\n        }\n\n        .modal {\n            border-radius: 10px;\n            border: 1px solid #1d2628;\n            background-color: #0b1011;\n            position: relative;\n            z-index: 10000000;\n            padding: 20px;\n            min-width: 300px;\n        }\n\n        .modal p {\n            margin: 0;\n        }\n\n        .modal::backdrop {\n            display: none;\n        }\n\n        .title {\n            font-size: 24px;\n            margin: 0 0 20px;\n        }\n\n        .closeButton {\n            position: absolute;\n            top: 20px;\n            right: 20px;\n        }\n\n        .overlay {\n            background: rgba(0, 0, 0, 0.5);\n            position: fixed;\n            inset: 0;\n            z-index: 9999999;\n            backdrop-filter: blur(3px);\n        }\n    `}</style>\n    )\n}\n"
