            "category": "Interactions",
            "code": "\"use client\"\n\nimport type { Variants } from \"motion/react\"\nimport * as motion from \"motion/react-client\"\nimport { CSSProperties, useState } from \"react\"\n\nconst N_NOTIFICATIONS = 3\n\nconst NOTIFICATION_HEIGHT = 60\nconst NOTIFICATION_WIDTH = 280\nconst NOTIFICATION_GAP = 8\n\nexport default function NotificationsStack() {\n    const [isOpen, setIsOpen] = useState(false)\n\n    const stackVariants: Variants = {\n        open: {\n            y: 20,\n            scale: 0.9,\n            cursor: \"pointer\",\n        },\n        closed: {\n            y: 0,\n            scale: 1,\n            cursor: \"default\",\n        },\n    }\n\n    return (\n        <motion.div\n            style={stackStyle}\n            variants={stackVariants}\n            initial={false}\n            animate={isOpen ? \"open\" : \"closed\"}\n            transition={{\n                type: \"spring\",\n                mass: 0.7,\n            }}\n        >\n            <Header isOpen={isOpen} onClose={() => setIsOpen(false)} />\n\n            {Array.from({ length: N_NOTIFICATIONS }).map((_, i) => (\n                <Notification\n                    key={i}\n                    index={i}\n                    onClick={() => {\n                        setIsOpen((open) => !open)\n                    }}\n                />\n            ))}\n        </motion.div>\n    )\n}\n\nconst Header = ({\n    isOpen,\n    onClose,\n}: {\n    isOpen: boolean\n    onClose: () => void\n}) => {\n    const variants: Variants = {\n        open: {\n            y: 0,\n            scale: 1,\n            opacity: 1,\n        },\n        closed: {\n            y: 60,\n            scale: 0.8,\n            opacity: 0,\n        },\n    }\n\n    return (\n        <motion.div\n            style={headerStyle}\n            variants={variants}\n            transition={{\n                type: \"spring\",\n                stiffness: 600,\n                damping: 50,\n                delay: isOpen ? 0.2 : 0,\n            }}\n        >\n            <motion.h2 style={headerTitleStyle}>Notifications</motion.h2>\n            <motion.button\n                style={headerCloseStyle}\n                whileHover={{\n                    backgroundColor: \"#f5f5f5\",\n                    color: \"#0f1115\",\n                }}\n                onClick={onClose}\n            >\n                Collapse\n            </motion.button>\n        </motion.div>\n    )\n}\n\nconst Notification = ({\n    index,\n    onClick,\n}: {\n    index: number\n    onClick: () => void\n}) => {\n    const variants: Variants = {\n        open: {\n            y: 0,\n            scale: 1,\n            opacity: 1,\n            pointerEvents: \"auto\",\n            cursor: \"pointer\",\n        },\n        closed: {\n            y:\n                -index * (NOTIFICATION_HEIGHT + NOTIFICATION_GAP) -\n                NOTIFICATION_GAP * index,\n            scale: 1 - index * 0.1,\n            opacity: 1 - index * 0.4,\n            pointerEvents: index === 0 ? \"auto\" : \"none\",\n            cursor: index === 0 ? \"pointer\" : \"default\",\n        },\n    }\n\n    const notificationStyle = getNotificationStyle(index)\n\n    return (\n        <motion.div\n            style={notificationStyle}\n            variants={variants}\n            transition={{\n                type: \"spring\",\n                stiffness: 600,\n                damping: 50,\n                delay: index * 0.04,\n            }}\n            onClick={onClick}\n        />\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\nconst stackStyle: CSSProperties = {\n    position: \"relative\",\n    display: \"flex\",\n    flexDirection: \"column\",\n    gap: NOTIFICATION_GAP,\n}\n\nconst headerStyle: CSSProperties = {\n    position: \"absolute\",\n    top: -40,\n    left: 0,\n    height: 28,\n    width: \"100%\",\n    display: \"flex\",\n    alignItems: \"center\",\n    justifyContent: \"space-between\",\n    transformOrigin: \"bottom center\",\n    pointerEvents: \"none\",\n}\n\nconst headerTitleStyle: CSSProperties = {\n    fontSize: 18,\n    lineHeight: 1,\n    marginLeft: 8,\n}\n\nconst headerCloseStyle: CSSProperties = {\n    fontSize: 14,\n    lineHeight: 1,\n    marginRight: 8,\n    backgroundColor: \"#1f1f1f\",\n    padding: \"4px 12px\",\n    height: 20,\n    display: \"flex\",\n    alignItems: \"center\",\n    justifyContent: \"center\",\n    borderRadius: 16,\n    cursor: \"pointer\",\n    pointerEvents: \"auto\",\n    userSelect: \"none\",\n}\n\n/**\n * ==============   Utils   ================\n */\nfunction getNotificationStyle(index: number): CSSProperties {\n    return {\n        height: NOTIFICATION_HEIGHT,\n        width: NOTIFICATION_WIDTH,\n        backgroundColor: `#f5f5f5`,\n        borderRadius: 16,\n        display: \"flex\",\n        justifyContent: \"center\",\n        alignItems: \"center\",\n        zIndex: N_NOTIFICATIONS - index,\n        userSelect: \"none\",\n    }\n}\n"
