            "category": "Radix",
            "code": "\"use client\"\n\nimport { AnimateNumber } from \"motion-plus/react\"\nimport { useMotionValue, useSpring, useVelocity } from \"motion/react\"\nimport { Slider } from \"radix-ui\"\nimport { useEffect, useState } from \"react\"\n\nexport default function NumberSlider({\n    min = 0,\n    max = 100,\n    initialValue = 50,\n}) {\n    const [value, setValue] = useState([initialValue])\n\n    const scaled = useMotionValue(scale(value[0]))\n    const velocity = useVelocity(scaled)\n    const rotate = useSpring(velocity)\n\n    useEffect(() => {\n        scaled.set(scale(value[0]))\n    }, [value, scaled])\n\n    return (\n        <form>\n            <Slider.Root\n                className=\"slider\"\n                onValueChange={setValue}\n                defaultValue={[50]}\n                min={min}\n                max={max}\n                step={1}\n            >\n                <Slider.Track className=\"track\">\n                    <Slider.Range className=\"range\" />\n                </Slider.Track>\n                <Slider.Thumb className=\"thumb\" aria-label=\"Volume\">\n                    <div className=\"thumb-text-container\">\n                        <AnimateNumber\n                            transition={{ duration: 0.2, ease: \"easeOut\" }}\n                            locales=\"en-US\"\n                            className=\"thumb-text\"\n                            style={{ originX: 0.5, originY: 1.5, rotate }}\n                        >\n                            {value as any}\n                        </AnimateNumber>\n                    </div>\n                </Slider.Thumb>\n            </Slider.Root>\n            <StyleSheet />\n        </form>\n    )\n}\n\n/**\n * ==============   Utils   ================\n */\n\nfunction scale(value: number, scaleBy: number = -0.1) {\n    return value * scaleBy\n}\n\n/**\n * ==============   Styles   ================\n */\n\nfunction StyleSheet() {\n    return (\n        <style>{`\n        .slider {\n            position: relative;\n            display: flex;\n            align-items: center;\n            user-select: none;\n            touch-action: none;\n            width: 200px;\n            height: 20px;\n        }\n\n        .track {\n            background-color: #1d2628;\n            position: relative;\n            flex-grow: 1;\n            border-radius: 9999px;\n            height: 3px;\n        }\n\n        .range {\n            position: absolute;\n            background-color: #f5f5f5;\n            border-radius: 9999px;\n            height: 100%;\n        }\n\n        .thumb {\n            display: block;\n            width: 20px;\n            height: 20px;\n            background-color: #f5f5f5;\n            border-radius: 10px;\n        }\n            \n        .thumb:focus {\n            outline: none;\n            box-shadow: 0 0 0 2px #9911ff;\n        }\n\n        .thumb-text-container {\n            position: absolute;\n            top: calc(-100% - 12px);\n            left: 50%;\n            width: 1px;\n            display: flex;\n            align-items: center;\n            justify-content: center;\n\n        }\n\n        .thumb-text {\n            background-color: #9911ff;\n            color: #f5f5f5;\n            padding: 2px 4px;\n            border-radius: 4px;\n        }\n    `}</style>\n    )\n}\n"
