            "code": "\"use client\"\n\nimport { ChevronDownIcon } from \"@radix-ui/react-icons\"\nimport * as Select from \"@radix-ui/react-select\"\nimport { motion } from \"motion/react\"\nimport { useState } from \"react\"\n\nfunction RadixSelect() {\n    const [open, setOpen] = useState(false)\n    const [value, setValue] = useState(\"\")\n\n    return (\n        <>\n            <Select.Root\n                open={open}\n                onOpenChange={setOpen}\n                value={value}\n                onValueChange={setValue}\n            >\n                <Select.Trigger className=\"trigger\" asChild>\n                    <motion.button\n                        whileHover={{ scale: 1.02 }}\n                        whileTap={{ scale: 0.98 }}\n                        style={{ willChange: \"transform\" }}\n                    >\n                        <Select.Value placeholder=\"Select a fruit...\" />\n                        <Select.Icon>\n                            <ChevronDownIcon />\n                        </Select.Icon>\n                    </motion.button>\n                </Select.Trigger>\n\n                <Select.Portal>\n                    <Select.Content asChild>\n                        <motion.div\n                            className=\"content\"\n                            initial={{ opacity: 0, scale: 0.5 }}\n                            animate={{ opacity: 1, scale: 1 }}\n                            transition={{\n                                duration: 0.2,\n                                scale: {\n                                    type: \"spring\",\n                                    visualDuration: 0.3,\n                                    bounce: 0.5,\n                                },\n                            }}\n                            style={{ willChange: \"transform, opacity\" }}\n                        >\n                            <Select.Viewport className=\"viewport\">\n                                {[\"Apple\", \"Banana\", \"Orange\", \"Grape\"].map(\n                                    (fruit) => (\n                                        <Select.Item\n                                            key={fruit}\n                                            value={fruit.toLowerCase()}\n                                            className=\"item\"\n                                            asChild\n                                        >\n                                            <motion.div\n                                                initial={{\n                                                    backgroundColor:\n                                                        \"#0b1011\",\n                                                }}\n                                                whileHover={{\n                                                    backgroundColor:\n                                                        \"#9911ff\",\n                                                }}\n                                                transition={{\n                                                    duration: 0.1,\n                                                    ease: \"linear\",\n                                                }}\n                                                style={{\n                                                    willChange:\n                                                        \"background-color\",\n                                                }}\n                                            >\n                                                <Select.ItemText>\n                                                    {fruit}\n                                                </Select.ItemText>\n                                            </motion.div>\n                                        </Select.Item>\n                                    )\n                                )}\n                            </Select.Viewport>\n                        </motion.div>\n                    </Select.Content>\n                </Select.Portal>\n            </Select.Root>\n            <StyleSheet />\n        </>\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\nfunction StyleSheet() {\n    return (\n        <style>{`\n            .trigger {\n                display: inline-flex;\n                align-items: center;\n                justify-content: space-between;\n                border-radius: 5px;\n                padding: 0 15px;\n                font-size: 16px;\n                line-height: 1;\n                height: 35px;\n                gap: 5px;\n                width: 200px;\n                background-color: #0b1011;\n                color: #f5f5f5;\n                border: 1px solid #1d2628;\n            }\n\n            .trigger:focus {\n                box-shadow: 0 0 0 2px #0f1115;\n            }\n\n            .content {\n                overflow: hidden;\n                background-color: #0b1011;\n                color: #f5f5f5;\n                border: 1px solid #1d2628;\n                border-radius: 5px;\n            }\n\n            .viewport {\n                padding: 5px;\n            }\n\n            .item {\n                font-size: 16px;\n                line-height: 1;\n                border-radius: 3px;\n                display: flex;\n                align-items: center;\n                height: 25px;\n                padding-right: 35px;\n                padding-left: 25px;\n                position: relative;\n                user-select: none;\n                cursor: pointer;\n            }\n\n            .item[data-disabled] {\n                color: var(--gray-500);\n                pointer-events: none;\n            }\n\n            .item[data-highlighted] {\n                outline: none;\n                background-color: #0f1115;\n                color: #f5f5f5;\n            }\n        `}</style>\n    )\n}\n\nexport default RadixSelect\n"
