            "code": "\"use client\"\n\nimport * as Switch from \"@radix-ui/react-switch\"\nimport { motion } from \"motion/react\"\nimport { useState } from \"react\"\n\nfunction RadixSwitch() {\n    const [checked, setChecked] = useState(false)\n\n    return (\n        <div style={styles.container}>\n            <Switch.Root checked={checked} onCheckedChange={setChecked} asChild>\n                <motion.button\n                    style={{\n                        ...styles.switch,\n                        justifyContent: checked ? \"flex-end\" : \"flex-start\",\n                    }}\n                    initial={false}\n                    animate={{\n                        backgroundColor: checked\n                            ? \"#8df0cc\"\n                            : \"var(--hue-6-transparent)\",\n                    }}\n                    whileFocus={{\n                        boxShadow:\n                            \"0 0 0 5px #0f1115, 0 0 0 10px var(--hue-6-transparent)\",\n                        transition: { duration: 0.2 },\n                    }}\n                >\n                    <Switch.Thumb asChild>\n                        <motion.div\n                            style={styles.thumb}\n                            layout\n                            transition={{\n                                type: \"spring\",\n                                stiffness: 500,\n                                damping: 30,\n                            }}\n                        />\n                    </Switch.Thumb>\n                </motion.button>\n            </Switch.Root>\n        </div>\n    )\n}\n\n// Styles\nconst styles = {\n    container: {\n        display: \"flex\",\n        alignItems: \"center\",\n    },\n    switch: {\n        width: 100,\n        padding: 10,\n        backgroundColor: \"#666666\",\n        borderRadius: 9999,\n        position: \"relative\",\n        outline: \"none\",\n        cursor: \"pointer\",\n        display: \"flex\",\n    },\n    thumb: {\n        display: \"block\",\n        width: 50,\n        height: 50,\n        backgroundColor: \"#f5f5f5\",\n        borderRadius: \"50%\",\n    },\n} as const\n\nexport default RadixSwitch\n"
