            "code": "\"use client\"\n\nimport { AnimatePresence, motion } from \"motion/react\"\nimport { Toast } from \"radix-ui\"\nimport { useState } from \"react\"\n\nexport default function RadixToast() {\n    const [date, setDate] = useState<string | null>(null)\n\n    return (\n        <Toast.Provider>\n            <motion.button\n                whileTap={{ scale: 0.9 }}\n                className=\"button primary-action large\"\n                onClick={() => {\n                    setDate(prettyDate(randomDate()))\n                }}\n            >\n                Add to calendar\n            </motion.button>\n            <AnimatePresence mode=\"wait\">\n                {date ? (\n                    <Toast.Root\n                        key={date}\n                        duration={3000}\n                        open\n                        onOpenChange={() => setDate(null)}\n                        asChild\n                        forceMount\n                    >\n                        <motion.div\n                            className=\"toast-root\"\n                            initial={{ opacity: 0, x: 100 }}\n                            animate={{ opacity: 1, x: 0 }}\n                            exit={{ opacity: 0, scale: 0.9 }}\n                            drag=\"x\"\n                            dragElastic={0.1}\n                            dragConstraints={{ left: 0 }}\n                            onUpdate={(latest) => {\n                                if ((latest.x as number) > 100) {\n                                    setDate(null)\n                                }\n                            }}\n                        >\n                            <Toast.Title className=\"toast-title\">\n                                Scheduled: Catch up\n                            </Toast.Title>\n                            <Toast.Description asChild>\n                                <time\n                                    className=\"toast-description\"\n                                    dateTime={date}\n                                >\n                                    {date}\n                                </time>\n                            </Toast.Description>\n                            <Toast.Action\n                                className=\"toast-action\"\n                                asChild\n                                altText=\"Goto schedule to undo\"\n                            >\n                                <button className=\"button small\">Undo</button>\n                            </Toast.Action>\n                        </motion.div>\n                    </Toast.Root>\n                ) : null}\n            </AnimatePresence>\n            <Toast.Viewport className=\"toast-viewport\" />\n            <StyleSheet />\n        </Toast.Provider>\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\n\nfunction StyleSheet() {\n    return (\n        <style>{`\n        button {\n            cursor: pointer;\n        }\n\n        .toast-viewport {\n            --viewport-padding: 25px;\n            position: fixed;\n            bottom: 0;\n            right: 0;\n            display: flex;\n            flex-direction: column;\n            padding: var(--viewport-padding);\n            gap: 10px;\n            width: 390px;\n            max-width: 100vw;\n            margin: 0;\n            list-style: none;\n            z-index: 2147483647;\n            outline: none;\n        }\n\n        .toast-root {\n            background-color: #0b1011;\n            border: 1px solid #1d2628;\n            border-radius: 10px;\n            box-shadow:\n                hsl(206 22% 7% / 35%) 0px 10px 38px -10px,\n                hsl(206 22% 7% / 20%) 0px 10px 20px -15px;\n            padding: 15px;\n            display: grid;\n            grid-template-areas: \"title action\" \"description action\";\n            grid-template-columns: auto max-content;\n            column-gap: 15px;\n            align-items: center;\n        }\n\n        .toast-title {\n            grid-area: title;\n            margin-bottom: 5px;\n            font-weight: 500;\n            color: var(--text);\n            font-size: 15px;\n        }\n\n        .toast-description {\n            grid-area: description;\n            margin: 0;\n            color: var(--feint-text);\n            font-size: 13px;\n            line-height: 1.3;\n        }\n\n        .toast-action {\n            grid-area: action;\n        }\n\n        .button {\n            display: inline-flex;\n            align-items: center;\n            justify-content: center;\n            border-radius: 10px;\n            font-weight: 500;\n            user-select: none;\n        }\n        .button.small {\n            font-size: 14px;\n            padding: 0 10px;\n            line-height: 25px;\n            height: 25px;\n\n            background: #0d63f8;\n        }\n        .button.large {\n            font-size: 16px;\n            padding: 0 10px;\n            line-height: 35px;\n            height: 35px;\n        }\n    `}</style>\n    )\n}\n\n/**\n * ==============   Utils   ================\n */\n\nfunction randomDate() {\n    const now = new Date()\n    const inOneWeek = now.setDate(\n        now.getDate() + Math.round(Math.random() * 31)\n    )\n    return new Date(inOneWeek)\n}\n\nfunction prettyDate(date: Date) {\n    return new Intl.DateTimeFormat(\"en-US\", {\n        dateStyle: \"full\",\n        timeStyle: \"short\",\n    }).format(date)\n}\n"
