            "category": "Radix",
            "code": "\"use client\"\n\nimport {\n    TextAlignCenterIcon,\n    TextAlignLeftIcon,\n    TextAlignRightIcon,\n} from \"@radix-ui/react-icons\"\nimport { AnimatePresence, motion } from \"motion/react\"\nimport { ToggleGroup } from \"radix-ui\"\nimport { useState } from \"react\"\n\nfunction ToggleItem({\n    children,\n    value,\n    currentValue,\n    ...rest\n}: {\n    currentValue: string | string[]\n    value: string\n    children: React.ReactNode\n    \"aria-label\": string\n}) {\n    const isMultiple = Array.isArray(currentValue)\n    const isSelected = isMultiple\n        ? currentValue.includes(value)\n        : currentValue === value\n\n    return (\n        <ToggleGroup.Item value={value} asChild>\n            <motion.button\n                initial={{ scale: 1, backgroundColor: \"#2b323d00\" }}\n                animate={\n                    isSelected && isMultiple\n                        ? { backgroundColor: \"#2b323dff\" }\n                        : {}\n                }\n                whileTap={{ scale: 0.9 }}\n                whileFocus={{ boxShadow: \"0 0 0 2px #ff0088\" }}\n                className=\"toggle-item\"\n                {...rest}\n            >\n                <span>{children}</span>\n                <AnimatePresence initial={false}>\n                    {isSelected && !isMultiple && (\n                        <motion.div\n                            initial={{ opacity: 0 }}\n                            animate={{ opacity: 1 }}\n                            exit={{ opacity: 0 }}\n                            className=\"selected-indicator\"\n                            layoutId=\"selected-indicator\"\n                        />\n                    )}\n                </AnimatePresence>\n            </motion.button>\n        </ToggleGroup.Item>\n    )\n}\n\nexport default function RadixToggleGroup() {\n    const [alignment, setAlignment] = useState(\"left\")\n\n    return (\n        <ToggleGroup.Root\n            type=\"single\"\n            value={alignment}\n            onValueChange={setAlignment}\n            aria-label=\"Text alignment\"\n            className=\"container\"\n        >\n            <ToggleItem\n                currentValue={alignment}\n                value=\"left\"\n                aria-label=\"Left aligned\"\n            >\n                <TextAlignLeftIcon />\n            </ToggleItem>\n            <ToggleItem\n                currentValue={alignment}\n                value=\"center\"\n                aria-label=\"Center aligned\"\n            >\n                <TextAlignCenterIcon />\n            </ToggleItem>\n            <ToggleItem\n                currentValue={alignment}\n                value=\"right\"\n                aria-label=\"Right aligned\"\n            >\n                <TextAlignRightIcon />\n            </ToggleItem>\n            <StyleSheet />\n        </ToggleGroup.Root>\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\n\nfunction StyleSheet() {\n    return (\n        <style>{`\n        .container {\n            display: flex;\n            padding: 10px;\n            min-width: max-content;\n            border-radius: 6px;\n            background-color: #0b1011;\n            border-radius: 10px;\n            gap: 5px;\n        }\n\n        .container svg {\n            width: 24px;\n            height: 24px;\n        }\n\n        .toggle-item {\n            flex: 0 0 auto;\n            color: #f5f5f5;\n            padding: 8px;\n            border-radius: 4px;\n            display: inline-flex;\n            font-size: 14px;\n            line-height: 1;\n            align-items: center;\n            justify-content: center;\n            position: relative;\n        }\n\n        .toggle-item > span {\n            position: relative;\n            z-index: 1;\n        }\n\n        @media (max-width: 520px) {\n            .toggle-item {\n                padding: 5px;\n            }\n\n            .container svg {\n                width: 16px;\n                height: 16px;\n            }\n        }\n\n        .selected-indicator {\n            position: absolute;\n            top: 0;\n            left: 0;\n            bottom: 0;\n            right: 0;\n            background-color: #2b323dff;\n            border-radius: 5px;\n            mix-blend-mode: lighten;\n        }\n    `}</style>\n    )\n}\n"
