            "category": "Radix",
            "code": "\"use client\"\n\nimport { AnimatePresence, motion, Transition } from \"motion/react\"\nimport { Tooltip } from \"radix-ui\"\nimport React from \"react\"\n\nconst spring: Transition = {\n    visualDuration: 0.3,\n    type: \"spring\",\n    bounce: 0.6,\n}\n\nexport default function RadixTooltip() {\n    const [open, setOpen] = React.useState(false)\n\n    return (\n        <Tooltip.Provider>\n            <Tooltip.Root open={open} onOpenChange={setOpen}>\n                <Tooltip.Trigger asChild>\n                    <button className=\"tooltip-trigger\">\n                        Long hover or focus\n                    </button>\n                </Tooltip.Trigger>\n                <AnimatePresence>\n                    {open && (\n                        <Tooltip.Portal forceMount>\n                            <Tooltip.Content asChild sideOffset={10}>\n                                <motion.div\n                                    className=\"tooltip-content\"\n                                    initial={{ opacity: 0, y: 20, scale: 0.8 }}\n                                    animate={{ opacity: 1, y: 0, scale: 1 }}\n                                    exit={{\n                                        opacity: 0,\n                                        y: 20,\n                                        scale: 0.8,\n                                        transition: { duration: 0.1 },\n                                    }}\n                                    transition={{\n                                        ...spring,\n                                        opacity: { ...spring, bounce: 0 },\n                                    }}\n                                >\n                                    Add to library\n                                    <Tooltip.Arrow className=\"tooltip-arrow\" />\n                                </motion.div>\n                            </Tooltip.Content>\n                        </Tooltip.Portal>\n                    )}\n                </AnimatePresence>\n            </Tooltip.Root>\n            <StyleSheet />\n        </Tooltip.Provider>\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\n\nfunction StyleSheet() {\n    return (\n        <style>{`\n        .tooltip-content {\n            border-radius: 4px;\n            padding: 10px 15px;\n            font-size: 18px;\n            line-height: 1;\n            color: #f5f5f5;\n            background-color: #0cdcf7;\n            user-select: none;\n            will-change: transform, opacity;\n            color: #0f1115;\n        }\n\n        .tooltip-arrow {\n            fill: #0cdcf7;\n        }\n\n        .tooltip-trigger {\n            font-family: inherit;\n            border-radius: 10px;\n            display: inline-flex;\n            align-items: center;\n            justify-content: center;\n            color: #0f1115;\n            background-color: #0b1011;\n            border: 1px solid #1d2628;\n            color: #f5f5f5;\n            user-select: none;\n            display: flex;\n            align-items: center;\n            gap: 10px;\n            padding: 10px 15px;\n        }\n    `}</style>\n    )\n}\n"
