            "code": "\"use client\"\n\nimport {\n    animate,\n    motion,\n    MotionValue,\n    useMotionValue,\n    useMotionValueEvent,\n    useScroll,\n} from \"motion/react\"\nimport { useRef } from \"react\"\n\nexport default function ScrollLinked() {\n    const ref = useRef(null)\n    const { scrollXProgress } = useScroll({ container: ref })\n    const maskImage = useScrollOverflowMask(scrollXProgress)\n\n    return (\n        <div id=\"example\">\n            <svg id=\"progress\" width=\"80\" height=\"80\" viewBox=\"0 0 100 100\">\n                <circle cx=\"50\" cy=\"50\" r=\"30\" pathLength=\"1\" className=\"bg\" />\n                <motion.circle\n                    cx=\"50\"\n                    cy=\"50\"\n                    r=\"30\"\n                    className=\"indicator\"\n                    style={{ pathLength: scrollXProgress }}\n                />\n            </svg>\n            <motion.ul ref={ref} style={{ maskImage }}>\n                <li style={{ background: \"#ff0088\" }}></li>\n                <li style={{ background: \"#dd00ee\" }}></li>\n                <li style={{ background: \"#9911ff\" }}></li>\n                <li style={{ background: \"#0d63f8\" }}></li>\n                <li style={{ background: \"#0cdcf7\" }}></li>\n                <li style={{ background: \"#8df0cc\" }}></li>\n            </motion.ul>\n            <StyleSheet />\n        </div>\n    )\n}\n\nconst left = `0%`\nconst right = `100%`\nconst leftInset = `20%`\nconst rightInset = `80%`\nconst transparent = `#0000`\nconst opaque = `#000`\nfunction useScrollOverflowMask(scrollXProgress: MotionValue<number>) {\n    const maskImage = useMotionValue(\n        `linear-gradient(90deg, ${opaque}, ${opaque} ${left}, ${opaque} ${rightInset}, ${transparent})`\n    )\n\n    useMotionValueEvent(scrollXProgress, \"change\", (value) => {\n        if (value === 0) {\n            animate(\n                maskImage,\n                `linear-gradient(90deg, ${opaque}, ${opaque} ${left}, ${opaque} ${rightInset}, ${transparent})`\n            )\n        } else if (value === 1) {\n            animate(\n                maskImage,\n                `linear-gradient(90deg, ${transparent}, ${opaque} ${leftInset}, ${opaque} ${right}, ${opaque})`\n            )\n        } else if (\n            scrollXProgress.getPrevious() === 0 ||\n            scrollXProgress.getPrevious() === 1\n        ) {\n            animate(\n                maskImage,\n                `linear-gradient(90deg, ${transparent}, ${opaque} ${leftInset}, ${opaque} ${rightInset}, ${transparent})`\n            )\n        }\n    })\n\n    return maskImage\n}\n\n/**\n * ==============   Styles   ================\n */\n\nfunction StyleSheet() {\n    return (\n        <style>{`\n            #example {\n              width: 100vw;\n              max-width: 400px;\n              position: relative;\n            }\n\n            #example #progress {\n                position: absolute;\n                top: -65px;\n                left: -15px;\n                transform: rotate(-90deg);\n            }\n\n            #example .bg {\n                stroke: #0b1011;\n            }\n\n            #example #progress circle {\n                stroke-dashoffset: 0;\n                stroke-width: 10%;\n                fill: none;\n            }\n\n            #progress .indicator {\n                stroke: var(--accent);\n            }\n\n            #example ul {\n                display: flex;\n                list-style: none;\n                height: 220px;\n                overflow-x: scroll;\n                padding: 20px 0;\n                flex: 0 0 600px;\n                margin: 0 auto;\n                gap: 20px;\n            }\n\n            #example ::-webkit-scrollbar {\n                height: 5px;\n                width: 5px;\n                background: #fff3;\n                -webkit-border-radius: 1ex;\n            }\n\n            #example ::-webkit-scrollbar-thumb {\n                background: var(--accent);\n                -webkit-border-radius: 1ex;\n            }\n\n            #example ::-webkit-scrollbar-corner {\n                background: #fff3;\n            }\n\n            #example li {\n                flex: 0 0 200px;\n                background: var(--accent);\n            }\n\n    `}</style>\n    )\n}\n"
