            "code": "\"use client\"\n\nimport { motion } from \"motion/react\"\nimport { useState } from \"react\"\n\nfunction ScrollHighlightItem({\n    skill,\n    index,\n    isHighlighted,\n    onHighlight,\n}: {\n    skill: Skill\n    index: number\n    isHighlighted: boolean\n    onHighlight: (index: number) => void\n}) {\n    return (\n        <motion.li\n            className=\"skill-item\"\n            initial={false}\n            animate={{\n                opacity: isHighlighted ? 1 : 0.3,\n                scale: isHighlighted ? 1.02 : 1,\n            }}\n            transition={{\n                duration: 0.1,\n                ease: \"linear\",\n            }}\n            onViewportEnter={() => onHighlight(index)}\n            viewport={{\n                /**\n                 * Create an intersection area that's a little way down the screen -\n                 * -50% 0px -49% 0px would work for the middle of the screen\n                 */\n                margin: \"-28% 0px -68% 0px\",\n                amount: \"some\",\n            }}\n        >\n            {skill.name}\n        </motion.li>\n    )\n}\n\nexport default function ScrollHighlight() {\n    const [activeSkill, setActiveSkill] = useState<number | null>(null)\n\n    return (\n        <div className=\"container\">\n            <h1>Skills</h1>\n            <ul className=\"skills-list\">\n                {skills.map((skill, index) => (\n                    <ScrollHighlightItem\n                        key={skill.name}\n                        skill={skill}\n                        index={index}\n                        isHighlighted={activeSkill === index}\n                        onHighlight={() => setActiveSkill(index)}\n                    />\n                ))}\n            </ul>\n\n            <Stylesheet />\n        </div>\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\n\nfunction Stylesheet() {\n    return (\n        <style>\n            {`\n        .container {\n          padding: 300px 20px 100vh;\n          display: flex;\n        }\n\n        .container h1 {\n          font-size: 48px;\n          line-height: 1;\n          font-weight: 700;\n          margin: 0;\n          padding: 0;\n          text-align: center;\n          position: sticky;\n          top: 100px;\n          transform: rotate(-90deg);\n          text-transform: uppercase;\n          flex: 1 1 fit-content;\n          transform: rotate(-90deg) translate(-36%, 50%);\n          height: fit-content;\n        }\n\n        .skills-list {\n          padding: 50vh 0;\n          list-style: none;\n          padding: 0;\n          margin: 0;\n          display: flex;\n          flex-direction: column;\n          gap: 20px;\n        }\n\n        .skill-item {\n          padding: 4rem 0;\n          will-change: opacity;\n          font-size: clamp(2rem, 8vw, 6rem);\n          font-weight: 700;\n          margin: 0;\n          padding: 0;\n          line-height: 0.9;\n          text-transform: uppercase;\n          text-wrap: nowrap;\n        }\n\n        @media (max-width: 768px) {\n          .container h1 {\n            font-size: 24px;\n          }\n        }\n      `}\n        </style>\n    )\n}\n\n/**\n * Mock data\n */\n\ninterface Skill {\n    name: string\n}\n\nconst skills: Skill[] = [\n    { name: \"Branding\" },\n    { name: \"Web Design\" },\n    { name: \"Marketing\" },\n    { name: \"UI/UX Design\" },\n    { name: \"Development\" },\n    { name: \"Motion Design\" },\n]\n"
