            "category": "Basics",
            "code": "\"use client\"\n\nimport { motion, useScroll } from \"motion/react\"\nimport { useRef } from \"react\"\n\nfunction Item() {\n    const ref = useRef(null)\n    const { scrollYProgress } = useScroll({\n        target: ref,\n        offset: [\"end end\", \"start start\"],\n    })\n\n    return (\n        <section style={itemContainer}>\n            <div ref={ref} style={item}>\n                <figure style={progressIconContainer}>\n                    <svg\n                        style={progressIcon}\n                        width=\"75\"\n                        height=\"75\"\n                        viewBox=\"0 0 100 100\"\n                    >\n                        <circle\n                            style={progressIconBg}\n                            cx=\"50\"\n                            cy=\"50\"\n                            r=\"30\"\n                            pathLength=\"1\"\n                            className=\"bg\"\n                        />\n                        <motion.circle\n                            cx=\"50\"\n                            cy=\"50\"\n                            r=\"30\"\n                            pathLength=\"1\"\n                            style={{\n                                ...progressIconIndicator,\n                                pathLength: scrollYProgress,\n                            }}\n                        />\n                    </svg>\n                </figure>\n            </div>\n        </section>\n    )\n}\n\nexport default function TrackElementWithinViewport() {\n    return (\n        <>\n            <Item />\n            <Item />\n            <Item />\n            <Item />\n            <Item />\n            <Item />\n            <Item />\n            <Item />\n            <Item />\n            <Item />\n            <Item />\n            <Item />\n        </>\n    )\n}\n\n/**\n * ==============   Styles   ================\n */\n\nconst itemContainer: React.CSSProperties = {\n    height: \"100vh\",\n    maxHeight: \"400px\",\n    display: \"flex\",\n    justifyContent: \"center\",\n    alignItems: \"center\",\n}\n\nconst progressIconContainer: React.CSSProperties = {\n    position: \"sticky\",\n    top: 0,\n    width: 80,\n    height: 80,\n    margin: 0,\n    padding: 0,\n}\n\nconst processCircle: React.CSSProperties = {\n    strokeDashoffset: 0,\n    strokeWidth: 5,\n    fill: \"none\",\n}\n\nconst progressIcon: React.CSSProperties = {\n    ...processCircle,\n    transform: \"translateX(-100px) rotate(-90deg)\",\n    stroke: \"#ff0088\",\n}\n\nconst progressIconIndicator: React.CSSProperties = {\n    ...processCircle,\n    strokeDashoffset: 0,\n    strokeWidth: 5,\n    fill: \"none\",\n}\n\nconst progressIconBg: React.CSSProperties = {\n    opacity: 0.2,\n}\n\nconst item: React.CSSProperties = {\n    width: 200,\n    height: 250,\n    border: \"2px dotted #ff0088\",\n    position: \"relative\",\n}\n"
